insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'ABOUT-LIFERAY', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Liferay', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<strong>Liferay </strong>es la principal aplicaci&oacute;n basada en software libre para la creaci&oacute;n de entornos colaborativos on-line. Actualmente, en su sexto a&ntilde;o de desarrollo, Liferay Portal est&aacute; respaldado por una extensa red de servicios profesionales que ofrece desarrollos a medida, formaci&oacute;n y soporte en todo el mundo.]]></dynamic-content>\n    <dynamic-content language-id=\"en_US\"><![CDATA[<strong>Liferay </strong>is the world\'s leading open source portal&nbsp;platform. Now in its sixth year of development, Liferay Portal is backed by a comprehensive professional services network <a href=\"@friendly_url@/guest/services\">offering</a> custom development, training, and support across <a href=\"@friendly_url@/guest/team\">four continents</a>.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060607193700, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'ABOUTUS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<I><B>ENTERPRISE.</B> Open Source. For Life.</I><BR><BR>Liferay is the industry leader for open source solutions.&nbsp; Companies of all sizes and industries are turning to open source solutions for reliable and cost-effective measures to increase performance, reliability, and flexibility.&nbsp; Liferay stands at the forefront of OSS technology and is a trusted source for companies worldwide.&nbsp; Our unique and cost-effective solutions have garnered us the attention of the industry as being a cutting-edge company that enterprises can depend on. We are committed to providing innovative, user-friendly products and highly responsive support services. Liferay Portal has been carefully designed with high scalability and robust performance to handle the most demanding applications for companies across all industries. Adherence to J2EE standards means Liferay Portal can be deployed in many contexts without restricting its usage to any specific operating system, application server, or database.<BR><BR>To maximize usage of Liferay products, we offer a variety of technical support, custom development, and training services. Our professional service team is backed by comprehensive Liferay product knowledge and years of consulting experience with Fortune 500 companies.&nbsp; Companies turn to Liferay for unparalleled support.<BR><BR><I>Enterprise. <B>OPEN SOURCE.</B> For Life.</I><BR><BR>Liferay is committed to innovation, reliability, and community. We are a safe technology investment for our clients because our open source products lead to wider adoption by the technology community, the development of an expert user base, and acceptance as a standard portal platform. Our MIT license is business friendly and allows users to use, modify, or distribute Liferay with no licensing fees.<BR><BR>Liferay\'s open source products are extremely reliable as users in mission critical applications report enhancements and upgrades directly back to us.<BR><BR>Liferay\'s commitment to open source solutions reflects our philosophy of helping improve the world community. The wide use of Liferay as a free portal solution by various not-for-profit organizations is a testament to our success in this endeavor.<BR><BR><I>Enterprise. Open Source. <B>FOR LIFE.</B></I><BR><BR>Liferay is committed to making a difference in the world. Each month Liferay donates a percentage of its profits to various nonprofit organizations. In the past we have made financial contributions toward AIDS relief and the Sudan refugee crisis through well-respected organizations such as Samaritan\'s Purse and World Vision. The desire to help those in need is the heart of our company, and ultimately the reason why we exist.]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<i><b>ENTERPRISE.</b> Open Source. For Life.</i><br><br>Liferay es el líder de la industria en soluciones de portales open source. Compañías de todos los tamaños y negocios están cambiándose a soluciones open source como medidas de eficiencia y control de costes para ganar en rendimiento, eficiencia y flexibilidad. Liferay está al frente de la tecnología OSS y es una fuente de confianza para compañías de todo el mundo. Nuestras soluciones únicas y rentables nos han hecho ganarnos la atención de la industria como compañía lider tecnológica de la que las empresas pueden depender. Es nuestro compromiso ofrecer siempre productos innovadores y de fácil uso así como servicios de soporte con excelentes tiempos de respuesta. Liferay Portal ha sido diseñado cuidadosamente en base a una alta escalabilidad y un rendimiento robusto para implantar las más exigentes aplicaciones en compañías de todos los sectores de la industria. El empleo de estándares J2EE garantiza que Liferay Portal pueda ser desplegado en múltiples contextos sin restricciones de sistema operativo, servidor de aplicaciones o base de datos.<br>Para maximizar el uso de los productos de Liferay, ofrecemos soporte técnico de diferentes niveles, desarrollos a medida y servicios de formación. Nuestro equipo de servicios profesionales posee un amplio conocimiento de los productos de Liferay y años de experiencia en servicios de consultoría con compañías del Fortune 500. Las compañías acuden a Liferay por su excelente servicio de soporte.<br><br><i>Enterprise. <b>OPEN SOURCE.</b> For Life.</i><br><br>Liferay está comprometido con la innovación, la fiabilidad y la comunidad. Ofrecemos una inversión en tecnología segura para nuestros clientes porque nuestros productos open source disponen de una adopción más amplia en la comunidad tecnológica, el desarrollo de una base de usuarios expertos y la aceptación como una plataforma de portal estándar. La licencia MIT empleada es adecuada a entornos empresariales y permite usar, modificar o redistribuir Liferay sin ningún coste en licencias.&nbsp; <br>Los productos open source de Liferay son extremadamente fiables, ya que los usuarios de aplicaciones de misión crítica nos reportan directamente a nosotros mejoras y nuevas versiones.<br>El compromiso de Liferay con soluciones open source refleja nuestra filosofía de ayudar a mejorar la comunidad mundial. El amplio uso de Liferay como solución de portal gratuita por parte de varias organizaciones sin ánimo de lucro es prueba de nuestro éxito en este propósito.<br><br><i>Enterprise. Open Source. <b>FOR LIFE.</b></i><br><br>Liferay está comprometido con colaborar a crear un mundo mejor. Cada mes Liferay dona un porcentaje de sus beneficios a varias organizaciones sin ánimo de lucro. En el pasado hemos realizado contribuciones en proyectos de reducción del SIDA y a la crisis de refugiados en Sudan a través de organizaciones de prestigio como Samaritan\'s Purse y World Vision. El deseo de ayudar a los necesitados está en el corazón de nuestra compañía y es en último término la razón por la que existimos.<br>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606171900, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'ABOUTUS-CONTACTUS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us - Contact Us', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<b>Email</b><ul><li>Sales Inquiries: <a href=\"mailto:sales@liferay.com\" _fcksavedurl=\"mailto:sales@liferay.com\">sales@liferay.com</a></li><li>General Inquiries: <a href=\"mailto:staff@liferay.com\" _fcksavedurl=\"mailto:staff@liferay.com\">staff@liferay.com</a></li><li>Support: <a href=\"mailto:support@liferay.com\" _fcksavedurl=\"mailto:support@liferay.com\">support@liferay.com</a></li></ul><br>Liferay is also supported by a large community. To join our community:<ul><li>Visit <a href=\"http://forums.liferay.com/\" _fcksavedurl=\"http://forums.liferay.com/\">forums.liferay.com</a></li><li><a href=\"http://lists.sourceforge.net/lists/listinfo/lportal-development\" _fcksavedurl=\"http://lists.sourceforge.net/lists/listinfo/lportal-development\">Subscribe</a> or<a href=\"http://sourceforge.net/mailarchive/forum.php?forum=lportal-development\" _fcksavedurl=\"http://sourceforge.net/mailarchive/forum.php?forum=lportal-development\"> browse&nbsp;</a>our mailing list</li></ul>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<b>Correo electrónico:</b><ul><li>Equipo de ventas: <a href=\"mailto:sales@liferay.com\" _fcksavedurl=\"mailto:sales@liferay.com\">sales@liferay.com</a></li><li>Consultas generales: <a href=\"mailto:staff@liferay.com\" _fcksavedurl=\"mailto:staff@liferay.com\">staff@liferay.com</a></li><li>Servicios de soporte: <a href=\"mailto:support@liferay.com\" _fcksavedurl=\"mailto:support@liferay.com\">support@liferay.com</a></li></ul><br>Liferay también ofrece soporte a través de su amplia comunidad. Para un formar parte de ella:<ul><li>Visite los foros en <a href=\"http://forums.liferay.com/\" _fcksavedurl=\"http://forums.liferay.com/\">forums.liferay.com</a></li><li><a href=\"http://lists.sourceforge.net/lists/listinfo/lportal-development\" _fcksavedurl=\"http://lists.sourceforge.net/lists/listinfo/lportal-development\">Subscribase</a> o <a href=\"http://sourceforge.net/mailarchive/forum.php?forum=lportal-development\" _fcksavedurl=\"http://sourceforge.net/mailarchive/forum.php?forum=lportal-development\">consulte</a> nuestra lista de correo.</li></ul>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606174000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'ABOUTUS-LEADERSHIP', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us - Leadership', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>Liferay was formed by four partners from diverse technological and\nprofessional backgrounds. Our seasoned team endeavors to pioneer\ninnovative enterprise solutions for companies worldwide.<br><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\"><b>Brian Chan</b></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\">Brian\nChan, CEO and company founder, initially formed Liferay in 2000 to\nprovide nonprofit organizations with an open source solution to help\nthem collaborate on the Internet.&nbsp; He has since guided Liferay to\nbecome a leading industry-wide provider of open source enterprise\nsolutions. With a strong foundation in economics and software\narchitecture, Brian has solidified open source as a low-cost, high\nperformance solution for companies. Brian holds a B.S. in Economics and\nComputer Science from University of Chicago.</td></tr></tbody></table><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\"><b>Bryan Cheung</b></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\">Bryan\nCheung comes to Liferay with extensive consulting experience. His\nexperience in portal software includes the advertising campaign\nmanagement portal, one of the first commercial uses of Liferay. He has\nalso worked on custom Epicentric portlet development for Genentech, and\nhas done general consulting for Universal Music Group, Sony Consumer\nElectronics, Accenture Consulting, and Gateway Computers. Bryan holds a\nB.S. from University of California, Berkeley.</td></tr></tbody></table><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\"><b>Brian Kim</b></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\">Brian\nKim is an experienced technology consultant. At HubbardOne, Brian\nworked alongside upper management, including the CEO and CFO, to lead\nseveral core internal projects optimizing the company\'s infrastructure.\nPreviously, he served as a technical team lead at Accenture where he\nheaded projects for many Fortune 500 companies including Bank of\nAmerica and Wachovia Securities.&nbsp; Brian holds a B.S. and a M.S. in\nComputer Science from University of Chicago.</td></tr></tbody></table><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\"><b>Michael Young</b></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\">Michael\nYoung brings over 10 years of enterprise computing experience to\nLiferay. Michael has held positions at Sony Electronics, Syntricity,\nand the United States Navy. He led the overhaul of Syntricity\'s\ndataConductor EP data management and reporting software platform,\npaving Syntricity\'s way to profitability. As a contractor for the US\nNavy, Michael developed solutions that improved collaboration between\ntargeting and image systems. Michael holds a B.S. in Computer Science\nfrom University of California, San Diego.</td></tr></tbody></table></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>Liferay fue fundada por cuatro socios con experiencia tecnológica y profesional que cubre un amplio espectro. Nuestro equipo es capaz de ser pionero en la aplicación de soluciones innovadoras a compañías de todo el mundo.<br><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\"><b>Brian Chan</b></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\">Brian Chan, CEO y fundador de la compañía, creó Liferay en 2000 para ofrecer a organizaciones sin ánimo de lucro una solución open source para colaborar a través de Internet. Desde entonces ha guiado Liferay hasta que se ha convertido en un proveedor de soluciones open source líder. Con una fuerte base en economía y arquitectura de software, Brian ha solidificado Liferay Portal como una solución de bajo coste y alto rendimiento para las empresas. Brian es titulado en Economía y&nbsp; Computer Science por la Universidad de Chicago.<br></td></tr></tbody></table><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\"><b>Bryan Cheung</b></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\">Bryan Cheung aporta a Liferay una amplia experiencia en el mundo de la consultoría. Su experiencia previa con portales incluye el portal de advertising campaign management, uno de los primeros usos comerciales de Liferay. También ha trabajado en el desarrollo de portlets a medida Epicentric para Genetech y como consultor en Universal Music Group, Sony Consumer Electronics, Accenture Consulting, y Gateway Computers. Bryan es titulado por la Universidad de California, Berkeley.<br></td></tr></tbody></table><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\"><b>Brian Kim</b></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\">Brian Kim es un consultor tecnológico con amplia experiencia. En HubbardOne, Brian trabajó con su equipo de dirección, incluyendo su CEO y CFO, para liderar varios proyectos internos optimizando las infraestructuras de la compañía. Previamente trabajó como líder de equipos en Accenture donde encabezó proyectos para numerosas empresas Fortune 500 incluyendo&nbsp; Bank of America y Wachovia Securities. Brian es titulado en Computer Science por la Universidad de Chicago, y también tiene un master en Computer Science por la misma universidad.<br></td></tr></tbody></table><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\"><b>Michael Young</b></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\">Michael Young aporta más de diez años de experiencia en tecnologías de la información en la empresa. Michael ha trabajado en Sony Electronics, Syntricity y United States Navy. Lideró el proyecto de revisión y mejora de la plataforma software de gestión de datos e informes de&nbsp; Syntricity\'s dataConductor EP facilitando con ello su camino hacia la rentabilidad. En la US Navy, Michael desarrolló soluciones que mejoraron la colaboración entre sistemas de imágenes y de reconocimiento de objetivos. Michael es titulado en Computer Science por la Universidad de California, San Diego.<br><br><br></td></tr></tbody></table></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606174100, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'ABOUTUS-NEWS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us - News', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"title\" type=\"text\">\n    <dynamic-content><![CDATA[News Highlights]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'ABOUTUS-NEWS', 'ABOUTUS-NEWS', SPECIFIC_TIMESTAMP_20060501180234, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
