insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'DEVZONE-FAQS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Developer Zone - FAQs', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><a href=\"#q1\"> What is the difference between Liferay Portal Enterprise and Liferay Portal Professional?</a><br /> <br /> <a href=\"#q2\">What do I do if I am getting Out of Memory Errors?</a><br /> <br /> <a href=\"#q3\">How do I customize the General Guest pages?</a><br /> <br /> <a href=\"#q4\">What version of Java/Jikes should I use with Liferay 4.0.0?</a><br /> <br /> <br /> <p style=\"color: rgb(0, 0, 0);\" class=\"MsoNormal\"><span class=\"MsoHyperlink\"><strong style=\"\"><a name=\"q1\">What is the difference between Liferay Portal<st1:city w:st=\"on\"><st1:place w:st=\"on\">Enterprise</st1:place></st1:city> and Liferay Portal Professional?</a> <o:p></o:p></strong></span><br /> The <st1:city w:st=\"on\"><st1:place w:st=\"on\">Enterprise</st1:place></st1:city> version of Liferay portal uses Session EJBs to provide heavy scaling and transaction support required by large sites. This version requires an application server such as JBoss or Orion.&nbsp; The professional release of the portal calls java objects directly to provide a light weight facade. This version can be used with a servlet container like Tomcat. However, the source between the two is the essentially&nbsp; the same. A property, &quot;portal.release&quot;, within a properties file distinguishes between which is being used.<br /> <br /> In most instances, you should use Liferay Portal professional unless you require distributed transactions. <o:p></o:p></p> <p style=\"color: rgb(0, 0, 0);\" class=\"MsoNormal\"><strong style=\"\"><a name=\"q2\">What do I do if I am getting Out of Memory Errors?</a><o:p></o:p></strong><br /> Out of memory errors can occur for a variety of reasons.&nbsp; One suggestion is to increase the amount of memory in your startup options for your application server/container.<o:p></o:p></p> <p style=\"color: rgb(0, 0, 0);\" class=\"MsoNormal\"><strong style=\"\"><a name=\"q3\">How do I customize the General Guest pages?</a><o:p></o:p></strong><br /> Log into the portal with as a privileged user to change General Guest layouts.&nbsp; Switch to the General Guest comunity.&nbsp; Configure the page.<o:p></o:p></p> <p style=\"color: rgb(0, 0, 0);\" class=\"MsoNormal\"><strong style=\"\"><a name=\"q4\">What version of Java/Jikes should I use with Liferay 4.0.0?</a><o:p></o:p></strong><br /> It is recommended that Liferay 4.0.0 is run on JVMs 1.4.2 or 1.5.&nbsp; If you use 1.4.2, you will need Jikes 1.2.1; if 1.5, use Jikes 1.2.2.<o:p></o:p></p> </div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><a href=\"fckblank.html#1\" _fcksavedurl=\"fckblank.html#1\">¿Cual es la diferencia entre Liferay Portal Enterprise y Liferay Portal Professional?</a><br><a href=\"fckblank.html#2\" _fcksavedurl=\"fckblank.html#2\"><br>¿Qué puedo hacer si aparecen errores de falta de memoria (Out of Memory)?</a><br><br><a href=\"fckblank.html#3\" _fcksavedurl=\"fckblank.html#3\">¿Cómo puedo personalizar las páginas de General Guest?</a><br><br><a href=\"fckblank.html#4\" _fcksavedurl=\"fckblank.html#4\">¿Qué versión de Java/Jikes debo usar con Liferay 4.0.0?</a><br><br><br><span style=\"font-weight: bold;\">¿Cual es la diferencia entre Liferay Portal Enterprise y Liferay Portal Professional?</span><br>La version Enterprise de Liferay usa EJBs de sesión para proporcionar la escalabilidad y soporte de transacciones requerida en sitios web grandes. Esta versión requiere un servidor de aplicaciones como JBoss o Orion. La versión Professional del portal invoca a objetos java directamente para proporcionar un punto de acceso ligero. Esta versión puede desplegarse en un contenedor de servlets como Tomcat. Sin embargo, el código fuente de las dos versiones es prácticamente el mismo. Una propiedad, \"portal.release\", en un fichero de propiedades determina cual se usa.<br><br>En la mayoría de casos debe usarse Liferay Portal professional a no ser que se necesiten transacciones distribuidas.<br><br><span style=\"font-weight: bold;\">¿Qué puedo hacer si aparecen errores de falta de memoria (Out of Memory)?</span><br>Este tipo de errores aparecen por una gran variedad de causas. Una opción que puede probarse es incrementar la cantidad de memoria en las opciones de arranque del servidor de aplicaciones o contenedor de servlets.<br><br><span style=\"font-weight: bold;\">¿Cómo puedo personalizar las páginas de General Guest?</span><br>Auntenticándose en el portal con un usuario administrador, yendo a la comunidad denominada \'General Guest\' y configurando sus páginas.<br><br><span style=\"font-weight: bold;\">¿Qué versión de Java/Jikes debo usar con Liferay 4.0.0?</span><br>Se recomienda ejecutar Liferay 4.0.0 en JVMs 1.4.2 o 1.5. En caso de usar 1.4.2, se necesitará Jikes 1.2.1; en el caso de 1.5, debe usarse Jikes 1.2.2.<br><br></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606175100, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'DEVZONE-FEATURES-ROADMAP', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Devzone Features and Roadmap', '', '<div class=\"font-x-large\"><strong>4.0.0 Final</strong></div>\n<ul>\n    <li><strong>Security Model</strong>\n    <ul>\n        <li>Fine-grained Permissions\n        <ul>\n            <li>Metadata-based Permission Schemes at all Levels (Portal and Portlet)</li>\n            <li>Page-level Permissions</li>\n            <li>Custom Permission Grants\n            <ul>\n                <li>Role-based Permission Aggregation</li>\n                <li>Grant Individual Permissions to Users</li>\n            </ul>\n            </li>\n            <li>Permissions assigned to all Levels of the Enterprise (see below)</li>\n        </ul>\n        </li>\n    </ul>\n    <ul>\n        <li>Enterprise Taxonomy </li>\n    </ul>\n    <ul>\n        <ul>\n            <li>Enterprise</li>\n            <li>Organization</li>\n            <li>Location</li>\n            <li>Community</li>\n            <li>User</li>\n        </ul>\n    </ul>\n    </li>\n    <li><strong>Page Layout</strong>\n    <ul>\n        <li>Separate Public/Private Page Hierarchies\n        <ul>\n            <li>Communities</li>\n            <li>Users</li>\n        </ul>\n        </li>\n        <li>Improve Drag and Drop</li>\n        <li>WSYIWYG Add Content (powered by AJAX)</li>\n        <li>Layout Templates (1-1-1, 2-1-2, etc) refactored from Portlet Aggregator</li>\n        <li>Liferay Archive (LAR)\n        <ul>\n            <li>Import/Export Layouts and Preferences for a community</li>\n        </ul>\n        </li>\n    </ul>\n    </li>\n</ul>\n<ul>\n    <li><strong>Themes</strong>\n    <ul>\n        <li>Completely Velocity-based (while still maintaining JSP compatibility)</li>\n        <li>Stretchable Themes\n        <ul>\n            <li>Page is as wide as the browser</li>\n        </ul>\n        </li>\n        <li>Dynamic CSS\n        <ul>\n            <li>Modify Theme CSS at runtime (through Portlet Configuration Mode)</li>\n        </ul>\n        </li>\n        <li>Page-level Themes\n        <ul>\n            <li>Every page has its own Theme (otherwise inherit from the parent page)</li>\n        </ul>\n        </li>\n    </ul>\n    </li>\n</ul>\n<ul>\n    <li><strong>Performance and Scalability</strong>\n    <ul>\n        <li>Full-page Caching</li>\n        <li>Smaller Memory Footprint\n        <ul>\n            <li>Refactored Model Caching</li>\n        </ul>\n        </li>\n        <li>Faster Request/Response Times\n        <ul>\n            <li>Reduced Round-trip Javascript Loads</li>\n        </ul>\n        </li>\n        <li>Classloader Segregation\n        <ul>\n            <li>Portal Classes isolated from Portlet and Theme WARs</li>\n            <li>Allow for different versions of JARs to exist at runtime</li>\n            <li>Third-Party Portlets integrate seamlessly</li>\n        </ul>\n        </li>\n    </ul>\n    </li>\n    <li><strong>Integration</strong>\n    <ul>\n        <li>Alfresco\n        <ul>\n            <li>Web Client Portlet</li>\n            <li>Publish Alfresco Content in Liferay</li>\n        </ul>\n        </li>\n        <li>Struts Bridges</li>\n        <li>JSF Portlet/Servlet Transparency\n        <ul>\n            <li>Run a JSF Application in Portlet and Servlet contexts simultaneously</li>\n        </ul>\n        </li>\n        <li>FCKEditor Access to Liferay Journal (CMS)\n        <ul>\n            <li>Document Library</li>\n            <li>Image Gallery</li>\n            <li>Layouts</li>\n        </ul>\n        </li>\n        <li>Java Content Repository (JCR) - JSR-170\n        <ul>\n            <li>Document Library refactored to JCR</li>\n        </ul>\n        </li>\n        <li>Portal Client Package\n        <ul>\n            <li>Access the Portal SOA from Third Party Applications</li>\n        </ul>\n        </li>\n    </ul>\n    </li>\n    <li><strong>Application Servers</strong>\n    <ul>\n        <li>Geronimo Support</li>\n        <li>Websphere 6 Support </li>\n        <li>Weblogic 8.1SP5 Support</li>\n    </ul>\n    </li>\n</ul>\n<ul>\n    <li><strong>Auto Deploy</strong> - automatically deploy WARs from a configured folder\n    <ul>\n        <li>Portlets</li>\n        <li>Themes</li>\n        <li>Layouts</li>\n    </ul>\n    </li>\n    <li><strong>Portlets</strong>\n    <ul>\n        <li>Refactor all Portlets\n        <ul>\n            <li>Integrate Security Infrastructure</li>\n            <li>Improve UI and User Experience</li>\n        </ul>\n        </li>\n    </ul>\n    <ul>\n        <li>Journal\n        <ul>\n            <li>Improve Search Interface (One-step search and edit)</li>\n            <li>Tightly-integrated Article/Structure/Template generation</li>\n            <li>Velocity-based Templates (in addition to XSL)</li>\n        </ul>\n        </li>\n        <li>Message Boards\n        <ul>\n            <li>Add Categories</li>\n        </ul>\n        </li>\n        <li>Portlet Configuration Mode\n        <ul>\n            <li>Custom Portlet Configuration</li>\n            <li>Standard Look and Feel</li>\n            <li>Assign Permissions</li>\n        </ul>\n        </li>\n        <li>New Portlets\n        <ul>\n            <li>Admin (Super User)</li>\n            <li>Enterprise Admin</li>\n            <li>Organizatin Admin</li>\n            <li>Location Admin</li>\n            <li>Sample JSON</li>\n        </ul>\n        </li>\n    </ul>\n    </li>\n    <li><strong>Misc</strong>\n    <ul>\n        <li>Private Request Attributes</li>\n        <li>Italian Language Pack</li>\n        <li>Right-to-Left(RTL) Language Support</li>\n    </ul>\n    </li>\n</ul>', 'general', '', '', SPECIFIC_TIMESTAMP_20060703185500, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'DEVZONE-FEATURES-ROADMAP-COWPER', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Devzone Features and Roadmap Cowper', '', '<ul>     <li><strong>Security / Administration</strong>     <ul>         <li> Assign Communities to Organizations and Locations</li>         <li> User Collections</li>     </ul>     </li>     <li><strong>Portlets</strong>     <ul>         <li>Web 2.0 Mail Portlet<ul><li>Dynamic Display of Messages and Folders<br /></li><li>Drag and Drop Message Management</li><li>Optimized Attachment Management</li></ul></li><li> Sample DAO Portlet</li>     </ul>     </li>     <li><strong>Revamped Message Board Portlet</strong>     <ul>         <li> Recent Posts</li>         <li> Post Statistics</li>         <li> Ranking System</li>         <li> Threaded Views</li>         <li> Remove Topic from MB Hierarchy</li>         <li> Improved Usability / Look and Feel</li>         <li> Friendly URLs</li>     </ul>     </li>     <li><strong>LDAP Support</strong>     <ul>         <li> Out of the Box LDAP Integration</li>         <li> LDAP Administration GUI</li>         <li> Zero-Configuration LDAP Attribute Mapping</li>         <li> Novell eDirectory</li>         <li> Microsoft Active Directory</li>         <li> Apache DS</li>     </ul>     </li>     <li><strong>Integration / Applications Servers</strong>     <ul>         <li>Liferay / Alfresco Bundle</li>         <li>Geronimo 1.1</li>         <li>Websphere 6.0.x</li>     </ul>     </li> </ul>', 'general', '', '', SPECIFIC_TIMESTAMP_20060704225900, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'DEVZONE-FEATURES-ROADMAP-FUTURE-RELEASES', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Devzone Features and Roadmap Future Releases', '', '<div class=\"font-x-large\"><strong>Owen</strong></div><ul><li><strong>Integration</strong> <ul><li>OpenCMS </li><li>Vignette CMS </li><li>Java Content Repository (JCR) - JSR-170 <ul><li>Image Gallery </li><li>Journal </li><li>Message Boards </li><li>Blogs </li><li>Layouts </li><li>Themes </li></ul></li></ul></li><li><strong>Context-Sensitive Help</strong> </li><li><strong>Personalization</strong> <ul><li>Search </li><li>Ads </li></ul></li><li><strong>Portlets</strong> <ul><li>Sales Leads </li><li>Sales Quotes </li><li>Sales Pipeline </li></ul></li><li><strong>Themes</strong> <ul><li>Create themes at runtime </li></ul></li><li><strong>Installers</strong> <ul><li>Windows Installer </li><li>Linux RPM </li></ul></li></ul><div class=\"font-x-large\"><strong>Spurgeon</strong></div><ul><li><strong>Refactor Portal Core using JSF</strong> </li><li><strong>Integration</strong> <ul><li>Magnolia CMS </li><li>Lotus Notes </li><li>MVN Forum </li></ul></li><li><strong>Refactor Liferay Services</strong> <ul><li>Decouple Service Builder from Portal </li><li>Liferay Services Available to Portlet WARs through Liferay Kernel </li><li>Interface-Driven Design </li></ul></li><li><strong>Portlets</strong> <ul><li>Decouple Non-core Portlets from Portal </li><li>Refactor Portlets using JSF </li></ul></li><li><strong>Rudimentary JSR 286 Compliance (Portlet 2.0)</strong> </li></ul>', 'general', '', '', SPECIFIC_TIMESTAMP_20060703185900, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
