insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'GLOBAL-TEAM', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Global Team', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[Liferay is pleased to introduce the Liferay International Partner\nProgram and the Technology Partner Program.&nbsp; Liferay has established\nrelationships with companies worldwide to provide you with responsive\nand highly trained services. We recognize the significant contributions\nour partners make toward our success. This is why Liferay stands with\nour partners to ensure that our success is also their success. Fully\ncertified and trained by Liferay, Liferay International Partners offers\nthe same level of quality and innovation that Liferay is known for.&nbsp; <br><br><span style=\"font-weight: bold;\">International Partners </span>provide\na full offering of Liferay Portal professional services and support in\nspecific locales around the world. As Liferay certified Service\nPartners, you can rely on them in your area to provide the Liferay\nexpertise you need. To locate a Liferay International Partner please\nconsult our International Partner listing.<br><br><span style=\"font-weight: bold;\">Technology Partners </span>give\nyou peace of mind.&nbsp; Liferay\'s proven reliability in applications\nworldwide makes us the clear choice. For information regarding Liferay\nPortal\'s compatibility with a specific platform, application server, or\ndatabase please consult our Technology Partners listing.<br>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[Liferay se complace en presentar el Programa de Partners Internacionales de Liferay y el Programa de Partners Tecnológicos. Liferay ha establecido relaciones con compañías de todo el mundo para proporcionar servicios altamente preparados y con capacidad de respuesta. Reconocemos las significativas contribuciones de nuestros partners hacia nuestro éxito. Por eso Liferay está al lado de nuestros partners para garantizar que nuestro éxito es también el suyo. Plenamente certificados y formados por Liferay, los Partners Internacionales de Liferay ofrecen el mismo nivel de calidad e innovación por el que se conoce a Liferay.<br><br><span style=\"font-weight: bold;\">Los Partners Internacionales</span> proporcionan la oferta completa de los servicios profesionales y de soporte de Liferay en zonas específicas de todo el mundo. Como Partners certificados de Servicios Liferay, puede contar con ellos en su área para que le proporcionen la experiencia en Liferay que necesita. Para localizar un Partner Internacional de Liferay por favor consulte nuestro listado de Partners Internacionales.<br><br style=\"font-weight: bold;\"><span style=\"font-weight: bold;\">Los Partners Tecnológicos</span> le permiten estar tranquilo. La estabilidad probada de Liferay en aplicaciones de todo el mundo nos diferencia como la elección más clara. Para información relacionada con la compatibilidad de Liferay con una plataforma, servidor de aplicaciones o base de datos específica, por favor consulte nuestro listado de Partners Tecnológicos.<br>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606175600, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'GLOBAL-TEAM-GERMINUS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Global Team - Germinus', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.GLOBAL-TEAM-GERMINUS.image.1.0\">/image/journal/article?img_id=1.GLOBAL-TEAM-GERMINUS.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Germinus</b>&nbsp;-&nbsp;<a href=\"http://www.germinus.com\" _fcksavedurl=\"http://www.germinus.com\">www.germinus.com</a> </div>\n<div><br>Germinus is a consulting company based in Spain that specializes in consulting and advanced services in the field of new technologies. Germinus incorporates technological and innovative solutions in their products and services.<br><br>Germinus has used Liferay Portal extensively for the last four years. In 2005, Liferay entered into a partnership with Germinus, giving Germinus exclusive consulting and support rights in the Iberian Peninsula and South America. <br><br>The main shareholder of Germinus is ONI, the second largest telecommunications operator in Portugal.</div>\n<div>&nbsp;</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>Germinus</b>&nbsp;-&nbsp;<a href=\"http://www.germinus.com\" _fcksavedurl=\"http://www.germinus.com\">www.germinus.com</a> </div>\n<div><br>Germinus es una empresa de consultoría con sede en España que se especializa en servicios de consultoría avanzada y proyectos a medida en el campo de las nuevas tecnologías. Germinus se diferencia por el carácter innovador y altamente tecnológico de sus productos y servicios.<br><br>Germinus lleva utilizando Liferay de forma intensiva desde hace cuatro años. En 2005, Liferay llegó a un acuerdo de partnership con Germinus, otorgándole derechos exclusivos de consultoría y soporte de Liferay en la península Ibérica y América del Sur.&nbsp;</div>\n<div>&nbsp;</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
