insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'NEWS-RSS-LINE56', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Evaluating Open Source Enterprise Portal Software', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"creator\" type=\"text\">\n    <dynamic-content><![CDATA[www.line56.com]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"link\" type=\"text\">\n    <dynamic-content><![CDATA[http://www.line56.com/articles/default.asp?articleID=7045]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"description\" type=\"text_box\">\n    <dynamic-content><![CDATA[Web portal options are in the plenty and free open source products are tempting. But are they any good? Read about what James McGovern, an architect of a Fortune 100 company, has to say about Liferay Portal.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20051101000000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'NEWS-RSS-LINE56-2', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'A Free Portal', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"creator\" type=\"text\">\n    <dynamic-content><![CDATA[www.line56.com]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"link\" type=\"text\">\n    <dynamic-content><![CDATA[http://www.line56.com/articles/default.asp?articleID=7492]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"description\" type=\"text_box\">\n    <dynamic-content><![CDATA[Line56 investigates the free, open source Liferay.  It is both flexible and backed by options like services and training.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20060405000000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'NEWS-RSS-LINUXWORLD', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Open Source Portal Software Controls Security', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"creator\" type=\"text\">\n    <dynamic-content><![CDATA[www.linuxworld.com.au]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"link\" type=\"text\">\n    <dynamic-content><![CDATA[http://www.linuxworld.com.au/index.php/id;1997719577;fp;4;fpid;3]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"description\" type=\"text_box\">\n    <dynamic-content><![CDATA[With the help of Goodwill Industries International, Liferay Portal 4.0 lets individual users, groups and guests have portlet-level permissions. Administrators can set or restrict access to portlets and portlet objects, as well as delegate access authority to others.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20060406000000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'NEWS-RSS-MICROSOFT-PRICES', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Microsoft\'s high prices drive FSW to Linux, open source', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"creator\" type=\"text\">\n    <dynamic-content><![CDATA[searchopensource.techtarget.com]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"link\" type=\"text\">\n    <dynamic-content><![CDATA[http://searchopensource.techtarget.com/originalContent/0,289142,sid39_gci1176366,00.html]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"description\" type=\"text_box\">\n    <dynamic-content><![CDATA[High costs and an urgent need for standards compliance is driving FSW Inc. away from Microsoft and into new worlds, such as server virtualization on Linux, an open source and Linux backend and open source desktops.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20060328000000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'NEWS-RSS-NETWORKWORLD', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Open source: corporate playground?', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"creator\" type=\"text\">\n    <dynamic-content><![CDATA[www.networkworld.com]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"link\" type=\"text\">\n    <dynamic-content><![CDATA[http://www.networkworld.com/newsletters/datacenter/2005/1017datacenter1.html]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"description\" type=\"text_box\">\n    <dynamic-content><![CDATA[The world of open source allows coporations to take advantage of free software and harness the flexibility of addressing individual needs. With Liferay as his model, Andreas Antonopoulos writes: \"The open source community that emerges around such projects is a focal point for collaboration between companies pursuing similar goals.\" Each company, then, reaps the benefits of features and enhancements done by another.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20051017000000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'NEWS-RSS-TECHTARGET', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Open source portal lets Goodwill CIO help those in need', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"creator\" type=\"text\">\n    <dynamic-content><![CDATA[searchopensource.techtarget.com]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"link\" type=\"text\">\n    <dynamic-content><![CDATA[http://searchopensource.techtarget.com/originalContent/0,289142,sid39_gci1135884,00.html]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"description\" type=\"text_box\">\n    <dynamic-content><![CDATA[\"You\'re seeing in the news every day that the Suns and IBMs are moving toward this world as well. I was able to look to the Liferay client list that Brian [Chan] had put together and could see the tens of thousands using it. Seeing news like that makes you feel pretty confident in open source,\" said Stephen Bergman, CIO of Goodwill Industries.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20051020000000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'NEWS-RSS-TSS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Kito Mann to present JSF on top of Liferay at TheServerSide Symposium', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"creator\" type=\"text\">\n    <dynamic-content><![CDATA[www.theserverside.com]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"link\" type=\"text\">\n    <dynamic-content><![CDATA[http://javasymposium.techtarget.com/html/det_descriptions.htm#KMannJSF]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"description\" type=\"text_box\">\n    <dynamic-content><![CDATA[Component-oriented user interface frameworks such as JavaServer Faces (JSF) are growing in popularity, and organizations are also beginning to recognize the power of building application components with the Portlet API. What many don\'t realize, however, is the fact that JSF has integrated support for the Portlet API, making it a natural fit for building portlets. This session starts with a brief overview of JSF, portlets, and portals. It then explains how JSF portlet support works, and examines the process of developing portlets with JSF. Next, it walks through the development of a simple JSF application and deployment of that application as a portlet inside of Liferay Enterprise Portal, an open-source portal server.\n]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20060323000000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'NEWS-RSS-WALDEN', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'The Lion, the Witch, and Linux', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"creator\" type=\"text\">\n    <dynamic-content><![CDATA[www.newsforge.com]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"link\" type=\"text\">\n    <dynamic-content><![CDATA[http://software.newsforge.com/software/06/02/27/1622243.shtml?tid=132]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"description\" type=\"text_box\">\n    <dynamic-content><![CDATA[Walden Media, the company that produced The Chronicles of Narnia: The Lion, The Witch and the Wardrobe, wanted a community-driven Web site that would encourage discussion and collaboration on the educational products related to its movies. After considering the options, Walden Media chose Liferay\'s MIT-licensed open source content management system (CMS), running on Linux.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20060228000000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div>\n<div>\n<div><b>Open Source&nbsp;Innovation -- Enterprise Reliability</b><br></div>\n<div>At Liferay we believe that a passionate development team working\nin open&nbsp;collaboration will produce a product that rivals the best of\ncommercial offerings. The open source model empowers our team to bring\nrobust,&nbsp;innovative, and flexible&nbsp;products to market more quickly and at\nlower cost than traditional development models. In addition, subjecting\nthe source to the scrutiny of its users ensures our\nproduct&nbsp;reliability&nbsp;meets the highest standards.</div>\n<div>&nbsp;</div>\n<ul><li><span style=\"font-weight: bold;\"><a href=\"../../../../../web/guest/products/portal\" _fcksavedurl=\"../../../../../web/guest/products/portal\">Liferay Portal</a></span>\nis our flagship product.&nbsp;Now in its sixth year of development, Liferay\nPortal is JSR-168 compliant and runs on almost any major application\nserver, database, and operating system, rendering over 700 <a href=\"../../../../../web/guest/products/portal/deployment_matrix\" _fcksavedurl=\"../../../../../web/guest/products/portal/deployment_matrix\">deployment</a>&nbsp;combinations.&nbsp;<br><br>With\nover 50 portlets prebundled and more than 20 community-contributed\nthemes&nbsp;available, Liferay Portal is unsurpassed in out-of-the-box\nfunctionality, bringing immediate usability and accelerated development\npotential to a broad swath of portal-based internet application\nscenarios.<br><br></li><li><span style=\"font-weight: bold;\"><a href=\"../../../../../web/guest/products/journal\" _fcksavedurl=\"../../../../../web/guest/products/journal\">Liferay Journal</a> </span>is\na built-in content management system (CMS) leveraging the technologies\nof Liferay Portal.&nbsp; Separation of content from layout and roles-based\npermissioning and workflow help your organization maintain editorial\nrights and integrity, allow for content versioning, and increase\nproductivity, empowering writers and designers to focus on their\nrespective areas. A good content management system can significantly\nreduce the operating cost of publishing, managing,&nbsp;and maintaining web\ncontent and document libraries. <br><br></li><li><span style=\"font-weight: bold;\"><a href=\"../../../../../web/guest/products/mesh\" _fcksavedurl=\"../../../../../web/guest/products/mesh\">Liferay Mesh</a></span> is an upcoming technology from Liferay. Watch this space for more information soon.<br></li></ul></div></div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<div>\n<div>\n<div><b>Innovación en Software Libre – Fiabilidad para la Empresa. </b><br></div>\n<div>En Liferay creemos que un equipo de desarrollo apasionado, trabajando bajo una colaboración abierta, crea un producto que puede rivalizar perfectamente con la mejor oferta comercial. El modelo de software libre nos permite ofrecer al mercado productos robustos, innovadores y flexibles de forma más rápida y a un coste más bajo que los tradicionales modelos de desarrollo. Además la posibilidad de que los usuarios revisen el código fuente asegura que nuestro producto cumple los mayores estándares de calidad.</div>\n<div>&nbsp;</div>\n<ul><li><span style=\"font-weight: bold;\"><a href=\"../../../../../web/guest/products/portal\" _fcksavedurl=\"../../../../../web/guest/products/portal\">Liferay Portal</a></span>\nes nuestro producto estrella. Actualmente, en su sexto año de desarrollo, Liferay Portal cumple el estándar JSR 168, y puede ser desplegado en unas 700 <a _fcksavedurl=\"http://www.liferay.com/web/guest/products/portal/deployment_matrix\" href=\"http://www.liferay.com/web/guest/products/portal/deployment_matrix\">combinaciones de servidores de aplicaciones</a>, bases de datos y sistemas operativos.<br><br>Liferay Portal cuenta con más de 50 portlets integrados y más de 20 apariencias disponibles para su libre utilización. Posee las funcionalidades necesarias para cubrir las necesidades de comunicación y colaboración de cualquier compañía, grupo o institución además de una usabilidad inmediata gracias a su sencillez. Todo ello permite cubrir un amplio espectro de escenarios donde se necesite un portal o herramienta colaborativa.<br><br></li><li><span style=\"font-weight: bold;\"><a href=\"../../../../../web/guest/products/journal\" _fcksavedurl=\"../../../../../web/guest/products/journal\">Liferay Journal</a> (Diario de Liferay) </span>es un gestor de contenidos integrado basado en la tecnología de Liferay Portal. La separación entre el contenido y el diseño y los permisos basados en perfiles y&nbsp; flujos de trabajo (workflow), ayudan a las organizaciones a mantener la parte editorial de forma coherente y a mejorar la productividad. Un buen gestor de contenidos ayuda a reducir significativamente los costes de publicación, gestión y mantenimiento de los contenidos web y de bibliotecas de documentos.<br><br></li><li><span style=\"font-weight: bold;\"><a href=\"../../../../../web/guest/products/mesh\" _fcksavedurl=\"../../../../../web/guest/products/mesh\">Liferay Mesh</a></span> es una tecnología exclusiva de Liferay. En poco tiempo habrá más información en esta web.<br></li></ul></div></div></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606180400, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-CMS-DOCUMENT-LIBRARY', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products CMS Document Library', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-CMS-DOCUMENT-LIBRARY.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-CMS-DOCUMENT-LIBRARY.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div><b>Document Library</b></div><br>The Document Library provides a centralized repository for various documents (e.g., .PDF, .DOC, .XLS, etc.) to be stored and given a unique URL.&nbsp; A company\'s intranet running Liferay Portal&nbsp;can then be able to post internal documentation used for employees.&nbsp; Alternatively, since each document has its own URL, a portlet on your company\'s public website can reference any of the documents stored there as well.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<div><b>Biblioteca de Documentos</b></div><br>La Biblioteca de documentos ofrece un repositorio centralizado para diferentes tipos de documentos (PDF, DOC, XLS, etc.) bajo una única URL. En la intranet de una compañía que use Liferay Portal, es posible colgar información interna y documentación para que esté disponible para sus empleados. Además, cada documento cuenta con su propia URL, lo que permite que desde un portlet ubicado en el portal público de la compañía se haga referencia a algunos de los documentos almacenados en la base de datos de documentos.</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-CMS-IMAGE-GALLERY', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products CMS Image Gallery', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-CMS-IMAGE-GALLERY.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-CMS-IMAGE-GALLERY.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div><b>Image Gallery</b></div>\n<div>&nbsp;</div>\n<div>Like the Document Library, the Image Gallery provides a central repository for images used throughout the Liferay Portal by a unique URL.&nbsp; A company can add images, reference them in the portal, and view all your images through&nbsp;a slideshow.</div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><span style=\"font-weight: bold;\">Galería de Imágenes</span><br><br>Al igual que la Biblioteca de Documentos, la Galería de Imágenes es un repositorio central para imágenes y que puede ser usado a través de Liferay Portal desde una única URL. La compañía puede añadir imágenes, hacer referencia a ellas desde el portal, o ver todas las imágenes en forma de presentación.<br><br></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-CMS-STRUCTURED-CONTENT', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products CMS Structured Content', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-CMS-STRUCTURED-CONTENT.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-CMS-STRUCTURED-CONTENT.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Structured Content</b></div>\n<div>&nbsp;</div>\n<div>Liferay Journal segregates the creation of content between designer and writer.&nbsp; Less technical persons can create web content using one of the built-in WYSIWGs (What You See Is What You Get editors) without sifting through complex code.&nbsp; Assign a web designer to layout the data without having to come up with content.&nbsp; Liferay Journal divides the process into three different areas: structures, templates, and articles.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<span style=\"font-weight: bold;\">Contenido estructurado<br><br></span>El portlet de Diario separa las tareas de creación de contenidos entre el diseñador y el redactor. De esta forma el personal no técnico puede crear contenido para la web utilizando una herramienta WYSIWG (What You See Is What You Get, lo que ves es lo que obtienes), todo ello sin necesidad de bajar a nivel de código. Un diseñador web aplicará un diseño al contenido sin necesidad de trabajar con él directamente. El Diario de Liferay divide el proceso en tres áreas diferentes: estructuras, plantillas y artículos.<br><br>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-CMS-SUPPORTED-JSR170', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products CMS Supported JSR-170', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-CMS-SUPPORTED-JSR170.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-CMS-SUPPORTED-JSR170.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>JSR-170</b></div>\n<div>&nbsp;</div>\n<div>Developed under the Java Community Process (JCP), JSR-170 is a Content Repository API for CMS.&nbsp; The Document Library is currently compliant.&nbsp; More portlets will be migrated over in future releases.&nbsp; This will provide 100% JSR-170 and backwards compatibility.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>JSR-170</b></div>\n<div>&nbsp;</div>\n<div>Desarrollado bajo el Java Community Process (JCP), JSR-170 (también llamado JCR, Java Content Repository) es una API de acceso a repositorios de contenidos. Actualmente la&nbsp; Biblioteca de Documentos ya es compatible con JCR. Algunos portlets más serán migrados en próximas versiones, lo que ofrecerá una compatibilidad del 100% con JSR-170.</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT-WRAP', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-CMS-SUPPORTED-OTHER', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products CMS Supported Other', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-CMS-SUPPORTED-OTHER.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-CMS-SUPPORTED-OTHER.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div><b>Other Supported Technologies</b></div>\n<div>&nbsp;</div>\n<div>Just as Liferay runs on most application servers and databases, we strive to be content-agnostic as well.&nbsp;&nbsp;With Liferay, you have the freedom to integrate with your favorite content repository -- whether it be the Liferay Journal, another open-source alternative like <a href=\"http://www.alfresco.com/\" _fcksavedurl=\"http://www.alfresco.com/\">Alfresco</a> and <a href=\"http://www.magnolia.info/en/magnolia.html\" _fcksavedurl=\"http://www.magnolia.info/en/magnolia.html\">Magnolia</a>, or a closed-source vendor like <a href=\"http://www.vignette.com/\" _fcksavedurl=\"http://www.vignette.com/\">Vignette</a>.</div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><span style=\"font-weight: bold;\">Otras tecnologías soportadas</span><br><br>Liferay es compatible con la mayoría de los servidores de aplicaciones y bases de datos y por ello hemos querido que también sea agnóstico respecto a los repositorios de contenidos. Esto quiere decir que Liferay ofrece la posibilidad de integrarse con el repositorio de contenidos que se desee como el Diario de Liferay, otras alternativas en software libre como <a _fcksavedurl=\"http://www.alfresco.com/\" href=\"http://www.alfresco.com/\">Alfresco</a> y <a _fcksavedurl=\"http://www.magnolia.info/en/magnolia.html\" href=\"http://www.magnolia.info/en/magnolia.html\">Magnolia</a>, o de fabricantes como <a _fcksavedurl=\"http://www.vignette.com/\" href=\"http://www.vignette.com/\">Vignette</a>.<br><br></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-TEXTIMAGE-WRAP', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-CMS-XSL-EDITOR', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products CMS XSL Editor', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-CMS-XSL-EDITOR.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-CMS-XSL-EDITOR.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Flexible Templating Mechanism (XSL/VM)</b>\n<div>&nbsp;</div>Templates used for creating articles can be created in either XSL or Velocity (VM), giving developers flexibility in designing their websites.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>Mecanismo flexible de plantillas (XSL/VM)<br><br></b>Las plantillas usadas para crear los artículos pueden ser creadas en XLS o en Velocity (VM), ofreciendo a los desarrolladores flexibilidad en el diseño de sus sitios web.<b><br><br></b></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LICENSING', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Licensing', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>Liferay Portal is an open source portal that helps organizations\ncollaborate more efficiently by providing a consolidated view of\ndisparate applications. It is used by large and small organizations all\nover the world. Liferay has an extensive list of features that compares\nwith most commercial portals but without the high license fees.<br><br><b>Business Friendly&nbsp;Open Source License</b><br><br>Liferay is made available under the <a href=\"http://www.opensource.org/licenses/mit-license.php\" _fcksavedurl=\"http://www.opensource.org/licenses/mit-license.php\">MIT license</a>,\nwhich is essentially the same as the Apache and BSD licenses. Unlike\nthe GPL or LGPL licenses, the MIT/Apache/BSD licenses&nbsp;are business\nfriendly and contain no license fees or reciprocity clauses. This means\nyou can bundle Liferay or your branch of Liferay and even resell it if\nyou want. There are no strings attached.<br><br>Companies benefit when\nthey choose to contribute their bug fixes and modifications back to\nLiferay because they can more easily upgrade to the latest version of\nLiferay without paying the penalty of spending time and energy\nreintegrating their own custom changes. Our large community of\ncontributors can even improve on another company\'s contributions -\ncreating a steam roll effect of quality open source software.<br><br>Companies\nalso benefit when they can build value on top of Liferay and choose not\nto contribute it so they can resell their modifications. We don\'t\nbelieve that just because we give our product you have to also. We\nbelieve a business friendly open source license gives companies greater\nprotection in their investment by providing more options.<br><br><b>Liferay vs. Closed Source Portals</b><br><br>Liferay\nprovides the lowest total cost of ownership. There are ZERO license\nfees. Don\'t pay more because you add more users or need to scale with\nmore CPUs and servers.<br><br>Liferay has no application server agenda.\nDon\'t be forced into buying more licenses because your portal was made\nby the same vendor that made your application server. Mix and match so\nyou get the best of breed.<br><br>You can develop portlets with a full\nfeatured IDE like Eclipse, Netbeans, and JBuilder or with lightweight\neditor like EditPlus, VI, and Emacs. Commercial portals love to sell\ntools that <a href=\"http://www.theserverside.com/news/thread.tss?thread_id=31302#153713\" _fcksavedurl=\"http://www.theserverside.com/news/thread.tss?thread_id=31302#153713\">promise</a> to save you time and money but almost always end up wasting you time and money. This isn\'t 1999. Save money.<br><br>Open source means the code is in your hands. If you want to customize it, you <i>can</i>. If there are security flaws, everyone is made aware and the community can participate in fixing it quickly. There is actually <i>less</i>\nrisk in going with a sustained open source product than in going with a\ncommercial vendor. Vendors can go out of business. Vendors can merge\nand render your product dead in the water. Sustained open source\nproducts have an active community around it that works together.<br><br><b>Liferay vs. Open Source Portals</b><br><br>Not\nall open source licenses are created equal. Some licenses require that\nyour modifications be contributed if you choose to sell or distribute\nyour product. Some open source vendors still charge a license fee for\ncommercial use. Others are confused and flip flop from being closed\nsource one minute and open source the next minute. Liferay is open\nsource because we believe it is the next step in the evolution of\nsoftware development.<br><br>Liferay has a very active community as evidenced by our active <a href=\"http://forums.liferay.com/\" _fcksavedurl=\"http://forums.liferay.com/\">forums</a>.\nIf you\'re a small organization that doesn\'t require mission critical\nresponse times, you\'ll get the help you need. If your organization\nrequires mission critical support so that your project deadlines are\nnot compromised, you can get direct access to the developers of Liferay\nthrough professional support and consulting. <br><br>Contact us at <a href=\"mailto:sales@liferay.com\" _fcksavedurl=\"mailto:sales@liferay.com\">sales@liferay.com</a> for more information about our services.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>Liferay Portal es un portal basado en software libre, que ayuda a las organizaciones a colaborar de manera más eficiente para que sean más competitivas. Liferay Portal está implantado tanto en grandes como en pequeñas empresas de todo el mundo y su amplia lista de funcionalidades es similar a la de los grandes portales, pero con la ventaja de no tener que pagar el coste de las licencias.<br><br><b>Licencia de Software Libre amigable con entornos empresariales<br><br></b>El software de Liferay LLC está disponible bajo la licencia <a _fcksavedurl=\"http://www.opensource.org/licenses/mit-license.php\" href=\"http://www.opensource.org/licenses/mit-license.php\">MIT</a>, que es prácticamente similar a las de Apache y a las licencias BSD. Para las empresas, la licencia MIT no implica el pago de cuotas fijas ni dispone de cláusulas de reciprocidad, por lo que éstas pueden incluir a Liferay en sus catálogos e incluso llevar a cabo acciones como revendedores.<br><br>Las compañías que deciden contribuir con parches para arreglar defectos o realizar modificaciones se benefician de una actualización más sencilla a la última versión de liferay sin el coste de rehacer los cambios. Nuestra amplia comunidad de colaboradores puede incluso mejorar las contribuciones de la compañía – creando un efecto de mejora en cadena.<br><br>Las compañías que deciden desarrollar valor añadido sobre Liferay y no contribuirlo a Liferay también salen beneficiadas dado que pueden revender sus modificaciones. Nosotros no creemos que el que nuestro producto sea libre deba obligar a que el suyo también lo sea. Creemos en una licencia libre amigable con entornos empresariales que ofrezca una mayor protección a las inversiones realizadas al proporcionar más opciones.<b><br></b><br><b>Liferay vs. Portales Comerciales<br><br></b>Liferay proporciona el menor coste de propiedad posible. No hay coste de licencias, no hay coste por usuarios y tampoco hay que pagar si se añaden servidores o máquinas.<br><br>Liferay no está atado a un servidor de aplicaciones. No fuerza a comprar más licencias porque el portal este hecho por el mismo fabricante del servidor de aplicaciones. Esto permite seleccionar el mejor para cada circustancia.<br><br>Es posible desarrollar portlets con una herramienta de desarrollo con muchas funcionalidades como Eclipse, Netbeans y Jbuilder, o con un editor ligero como EditPlus, Vi y Emacs. Los portales comerciales acostumbran a vender herramientas con la promesa de ahorrar tiempo y dinero, pero al final acaba suponiendo una pérdida de ambos. No estamos en 1999, ¡ahorre dinero!<br><br>El software libre permite que el código fuente esté a disposición de la comunidad, de modo que si desea ser adaptado por una organización, empresa u organismo, pueda llevarse a cabo sin ningún problema. Esta situación permite que, si hay problemas de algún tipo con el software, una comunidad entera puede participar en la búsqueda de la solución, lo que facilita encontrar la solución al problema de forma mucho más rápida. <b><br></b><br><b>Liferay vs. Portales basados en Software Libre<br><br></b>No todas las licencias de software libre son iguales. Algunas licencias obligan a que las modificaciones que se efectúen en el código sean contribuidas si se va a emplear para su posterior venta o distribución. Algunos vendedores de Software Libre cargan con un fijo las licencias para uso comercial. En otros casos las empresas cambian de modelo con frecuencia, pasando de ser de Software Libre a ofrecer Software Comercial según su conveniencia, etc. Liferay es software libre porque creemos que es el siguiente paso en la evolución de desarrollo de software.<br><br>Liferay cuenta con una comunidad muy activa, lo que se ve reflejado en la actividad&nbsp; que presentan los <a _fcksavedurl=\"http://forums.liferay.com/\" href=\"http://forums.liferay.com/\">foros</a>. Si su empresa o compañía no se ve sometida a fechas límite críticas en lo que a entrega de proyectos se refiere, puede utilizar el código fuente de Liferay de forma gratuita y encontrar en estos foros la ayuda que necesita. Si por el contrario requiere de asesoramiento para terminar su proyecto en plazos, contará con acceso directo a los desarrolladores de Liferay a través del soporte profesional y la consultoria.<br>&nbsp;<br>Contacte con nosotros en <a _fcksavedurl=\"http://mailto:sales@liferay.com\" href=\"http://mailto:sales@liferay.com\">sales@liferay.com</a>.<b><br><br></b></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606180500, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAY-PORTAL-BENCHMARKS-RESULTS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Benchmarks - Results', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-LIFERAY-PORTAL-BENCHMARKS-RESULTS.image.1.1\">/image/journal/article?img_id=1.PRODUCTS-LIFERAY-PORTAL-BENCHMARKS-RESULTS.image&amp;version=1.1</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content><![CDATA[<strong><br />Notes on Testing Methodology</strong><br /><br />Additional client machines were added to create additional load as each previous client\'s processor&nbsp;usage of&nbsp;a client machine approached 90%. Above this threshold, clients begin to incorrectly simulate load. In the chart above, additional clients were added every 3000 threads, which was the point at which the 90% threshold was met.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGE-TEXT', SPECIFIC_TIMESTAMP_20060705235700, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-BENCHMARKS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Benchmarks', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content><![CDATA[<div style=\"margin: 0in 0in 0pt;\"> <div style=\"margin: 0in 0in 0pt;\"><font size=\"4\"><strong>Enterprise Performance in the Lab and the Real World</strong></font><em><br /></em><br /> Performance and scalability are essential to the successful deployment of an enterprise portal application. Liferay Portal has performed well in high-traffic, heavy-usage deployments in real world applications worldwide. <br /> <br /> Liferay has also exhibited impressive results in lab-based stress tests engineered both by independent testers and our internal development team. In a recent <a href=\"http://www.eweek.com/article2/0,1895,1983364,00.asp\">eWeek</a> benchmarking test, for example, a J2EE stack built on CentOS Linux, Apache, and Liferay (labelled &quot;Linux J2EE in comparison charts) was measured as delivering the highest <a href=\"http://www.eweek.com/slideshow_viewer/0,1205,l=&amp;s=26708&amp;a=182823&amp;po=4,00.asp\">average hits per second</a>, the second highest <a href=\"http://www.eweek.com/slideshow_viewer/0,1205,l=&amp;s=26708&amp;a=182823&amp;po=3,00.asp\">average throughput per second</a>, and the third highest <a href=\"http://www.eweek.com/slideshow_viewer/0,1205,l=&amp;s=26708&amp;a=182823&amp;po=5,00.asp\">average document download time</a> among eight different portal solutions. <br /> <br /> On the <a href=\"http://www.eweek.com/slideshow_viewer/0,1205,l=&amp;s=26708&amp;a=182823&amp;po=4,00.asp\">hits per second</a> and <a href=\"http://www.eweek.com/slideshow_viewer/0,1205,l=&amp;s=26708&amp;a=182823&amp;po=5,00.asp\">download time</a> metrics, Liferay outperformed JBoss Portal by a whopping factor of 23. On the <a href=\"http://www.eweek.com/slideshow_viewer/0,1205,l=&amp;s=26708&amp;a=182823&amp;po=3,00.asp\">throughput</a> metric, Liferay significantly outperformed all other configurations except .NET, by an average factor of 10.<br /> <br /> Liferay Portal has also been certified by <a href=\"http://www.azulsystems.com/products/verified_apps.htm?group=3\">Azul Systems</a>  on its 384 CPU environment, demonstrating its scalability in a highly-clustered environment. <br /> <br /> For our internal benchmarks of Liferay Portal, we used Microsoft\'s Web Application Stress Tool (WAST). The primary goal of the load tests was to determine the page load time of a 3400+ Sempron running Liferay under varying amounts of load. The Sempron was chosen to represent a budget&nbsp;server, to serve as a lower threshold of expected performance (more robust servers should deliver greater performance).</div> <div style=\"margin: 0in 0in 0pt;\">&nbsp;</div> <div style=\"margin: 0in 0in 0pt;\">These are the exact specifications of the test components: <br /> </div> <div style=\"margin: 0in 0in 0pt;\">&nbsp;</div> <div style=\"margin: 0in 0in 0pt;\"><strong>Server:<br /> <br /> </strong></div> <div style=\"margin: 0in 0in 0pt;\">AMD Sempron 3400+, 2 GB RAM running <a href=\"http://prdownloads.sourceforge.net/lportal/liferay-portal-tomcat-jdk5-4.0.0.zip?download\">Liferay Portal Professional 4.0.0 (Bundled with Tomcat for JDK 5.0)</a> with the following modifications:</div> <div style=\"margin: 0in 0in 0pt;\">&nbsp;</div> <ol type=\"1\" style=\"margin-top: 0in;\">     <li style=\"margin: 0in 0in 0pt;\">The maximum number of allowable threads was set to 20,000. </li>     <li style=\"margin: 0in 0in 0pt;\">The maximum size of the JVM heap was set to 1280. </li> </ol> <div style=\"margin: 0in 0in 0pt;\"><strong>Client Machines:</strong></div> <div style=\"margin: 0in 0in 0pt;\"><br /> (6) AMD 3400+ Sempron(s) with&nbsp;2 GB RAM running Microsoft Web Application Stress Tool</div> <div style=\"margin: 0in 0in 0pt;\">&nbsp;</div> <div style=\"margin: 0in 0in 0pt;\"><strong><br /> Understanding the Test<br /> <br /> </strong></div> <div style=\"margin: 0in 0in 0pt;\">Our tests dealt simply with threads from the client machines making requests to the server. In the absence of a true high volume of concurrent users, or of user emulation that accurately includes think time, threadwise performance can be multiplied (by a conservative or aggressive factor) to extrapolate an estimate of how many users can be handled by the server with the given hardware profile. The threadwise performance of the server is an easily comparable metric across test results.<br /> </div> <br /> <div style=\"margin: 0in 0in 0pt;\"><strong><br /> Metrics<br /> <br /> </strong></div> <div style=\"margin: 0in 0in 0pt;\">The primary metric WAST generates is Total Time until Last Byte is received (TTLB), commonly understood as page load time. This is an appropriate metric for a web-based application such as a portal, and is the determining factor of a user&rsquo;s psychological perception of a site\'s performance.</div> <div style=\"margin: 0in 0in 0pt;\">&nbsp;</div> <div style=\"margin: 0in 0in 0pt;\">The secondary metric is requests per second.&nbsp;This metric allows one to determine the server&rsquo;s peak point of operating efficiency. The server should never reach a point where there is a sharp drop in the number of requests per second it can handle.</div> <div style=\"margin: 0in 0in 0pt;\"><br /> &nbsp;</div> <div style=\"margin: 0in 0in 0pt;\"><strong>Results<br /> <br /> </strong></div> <div style=\"margin: 0in 0in 0pt;\">Liferay Portal supports up to 9,000 concurrent page requests with a page load time of 6 seconds on a 3400+ Sempron server.&nbsp;Even at 18,000 concurrent page requests, page load time only increased by 0.552s. A conservative user multiplier of 5 would yield 90,000 concurrent users with a page load time of about 6.6 seconds and little or no degradation in operating efficiency. A more aggressive multiplier of 15 would yield an estimate of 135,000 concurrent users.</div> </div>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-LIFERAYPORTAL-BENCHMARKS.image.1.1\">/image/journal/article?img_id=1.PRODUCTS-LIFERAYPORTAL-BENCHMARKS.image&amp;version=1.1</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-TEXT-IMAGE', SPECIFIC_TIMESTAMP_20060628224800, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
