insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-DRAGNDROP', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Drag \'n Drop', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-LIFERAYPORTAL-FEATURE-DRAGNDROP.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-LIFERAYPORTAL-FEATURE-DRAGNDROP.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<span style=\"font-weight: bold;\">Personalization</span><br><br>Portlets can be rearranged to the unique preferences of a user or community.&nbsp; Move things up and down and all around -- all by a simple drag-and-drop.&nbsp; Portlets can also be added easily by a \"Add Content\" pop-up palette.<br><br>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<span style=\"font-weight: bold;\">Personalización<br><br></span>Los portlets pueden ser seleccionados en base a las preferencias específicas de un usuario o de una comunidad. Los portlets pueden ser fácilmente desplazados y ubicados en cualquier parte de la pantalla, también pueden ser añadidos mediante un menú emergente donde el usuario los elige.<br><br><br>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT-WRAP', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-INTERNATIONALIZATION', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Internationalization', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-LIFERAYPORTAL-FEATURE-INTERNATIONALIZATION.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-LIFERAYPORTAL-FEATURE-INTERNATIONALIZATION.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Internationalization</b></div>\n<div>&nbsp;</div>\n<div>Liferay can display and receive input in multiple languages. Language resources for Chinese, Dutch, English, French, German, Greek, Italian, Japanese, Korean, Portuguese, Spanish, Turkish, and Vietnamese are already included.</div>\n<div>&nbsp;</div>\n<div>Portlets, CMS content, and page layouts can all be easily localized to the languages you want to support.&nbsp; You can add the Language portlet to any page and allow the end-user to quickly select a different localization on the fly.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>Internacionalización</b></div>\n<div>&nbsp;</div>\n<div>Liferay puede mostrar y recibir entradas en múltiples idiomas. Incluye recursos lingüísticos en Chino, Holandés, Inglés, Alemán, Griego, Italiano, Japonés, Coreano, Portugués, Español, Turco y Vietnamita.</div>\n<div>&nbsp;</div>\n<div>Tanto los portlets, como los contenidos del CMS, como las plantillas de las páginas pueden ser fácilmente traducidos a todos los lenguajes que deseen soportarse. Es posible añadir el portlet de idioma a cualquier página y permitir que el usuario final seleccione el idioma que desee. <br></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-TEXTIMAGE-WRAP', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-OUTOFBOXPORTLETS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Out of Box Portlets', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-LIFERAYPORTAL-FEATURE-OUTOFBOXPORTLETS.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-LIFERAYPORTAL-FEATURE-OUTOFBOXPORTLETS.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Out of the Box Portlets<br><br></b>Liferay provides over fifty (50) useful portlets: Blogs, Shopping, Message Boards, Wiki, Mail, Journal (CMS), Polls, RSS, etc.&nbsp; Most portlets can be restricted for different users with its fine-grained permissioning.<br><br>Portals are only useful so long as there are portlets that provide functionality. Our bundled portlets are a great starting point for a portal deployment. They also serve as a large code base of examples from which you can glean patterns for writing your own portlets.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>Gran variedad de Portlets incluidos<br><br></b>Liferay ofrece más de cincuenta (50) portlets útiles: blogs, tienda, wiki, correo electrónico (webmail), tablón de anuncios, encuestas, canales RSS, etc. La mayor parte de los portlets pueden configurarse de forma que sean usados sólo por algunos usuarios del entorno colaborativo, gracias a la granularidad que permite la gestión de usuarios de Liferay Portal.<br><br>Los portales sólo son útiles a largo plazo si sus portlets aportan ventajas. Los portlets que vienen incluidos en Liferay Portal pueden ser un buen punto de partida para el&nbsp; despliegue de un portal. Además, Liferay Portal sirve como un buen código fuente de ejemplo desde el que tomar ideas para posteriormente crear nuevos portlets.</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-TEXT-IMAGE', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-SERVERAGNOSTIC', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Application Server Agnostic', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-LIFERAYPORTAL-FEATURE-SERVERAGNOSTIC.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-LIFERAYPORTAL-FEATURE-SERVERAGNOSTIC.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Application Server Agnostic<br><br></b>Unlike portals that come from application server vendors, Liferay is designed to be application server agnostic so you are not locked into a specific server. Liferay will work on lightweight servlet containers like Jetty and Tomcat, or on J2EE compliant servers like Borland ES, JBoss+Jetty/Tomcat, JOnAS+Jetty/Tomcat, JRun, OracleAS, Orion, Pramati, RexIP, Sun JSAS, WebLogic, and WebSphere.<br><br>An added bonus of being a Java portal means Liferay will work on many operating systems: BSD (FreeBSD, NetBSD, OpenBSD), Linux (Fedora, Novell), Solaris, Mac OS X, and Windows.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>Independencia respecto a Servidores de Aplicaciones<br><br></b>Liferay está diseñado para poder ejecutarse sobre cualquier servidor de aplicaciones, de modo que el usuario no tenga que trabajar con un servidor en concreto. Liferay puede ejecutarse sobre contenedores ligeros como Tomcat o Jetty, o sobre servidores compatibles con J2EE como Borland ES, JBoss+Jetty/Tomcat, JOnAS+Jetty/Tomcat, JRun, OracleAS, Orion, Pramati, RexIP, Sun JSAS, WebLogic, y WebSphere<br><br>Un valor adicional de ser un portal basado en Java es que Liferay se ejecuta sobre múltiples sistemas operativos: BSD (FreeBSD, NetBSD, OpenBSD), Linux (Fedora, Novell), Solaris, Mac OS X y Windows.</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT-WRAP', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-SUBTHEMES', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Sub-themes', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-LIFERAYPORTAL-FEATURE-SUBTHEMES.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-LIFERAYPORTAL-FEATURE-SUBTHEMES.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div><b>Sub-Themes</b></div>\n<div>&nbsp;</div>\n<div>Each portlet can be customized further from the theme\'s look and feel.&nbsp; End-users can change colors, fonts, and links to their own tastes without dealing with stylesheets or complex code.</div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<div><b>Variantes de temas</b></div>\n<div>&nbsp;</div>\n<div>La apariencia de cada portlet puede ser personalizada desde los temas. El usuario final puede cambiar colores, fuentes y enlaces de forma sencilla sin necesidad de entrar en el código fuente o en hojas de estilo.</div></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-TEXTIMAGE-WRAP', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-TEXT1', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Text1', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div>Liferay Portal is an open source portal that helps organizations\ncollaborate more efficiently by providing a consolidated view of\ndisparate applications. It is used by large and small organizations all\nover the world. Liferay has an extensive list of features that compares\nwith most commercial portals but without the high license fees.</div>\n<div>&nbsp;</div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<div>Liferay Portal es un portal basado en software libre, que ayuda a las organizaciones a colaborar de forma más eficiente lo que les permite ser más competitivas. Liferay Portal está implantado tanto en grandes como en pequeñas empresas de todo el mundo y su amplia lista de funcionalidades es comparable a la de los grandes portales, pero con la ventaja de no tener que pagar el coste de las licencias.</div>\n<div>&nbsp;</div></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606180600, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-TEXT2', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Text2', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div><b>CMS<br><br></b>Liferay has its own built-in content management\nsystem to give your organization a flexible tool built on top of XSLT\nand Velocity technologies.&nbsp;This page you are reading is served by the\nCMS tool.&nbsp; Read&nbsp;<a href=\"../../../../../web/guest/products/journal\" _fcksavedurl=\"../../../../../web/guest/products/journal\">Liferay Journal</a> to find out more.<br></div>\n<div>&nbsp;</div>\n<div><b>SSO<br><br></b>Liferay provides a built in connector for <a href=\"http://tp.its.yale.edu/tiki/tiki-index.php?page=CentralAuthenticationService\" _fcksavedurl=\"http://tp.its.yale.edu/tiki/tiki-index.php?page=CentralAuthenticationService\">CAS</a>, Yale\'s single sign on engine. You can write custom hooks to integrate with other SSO engines like <a href=\"http://www.netegrity.com/\" _fcksavedurl=\"http://www.netegrity.com/\">Netegrity</a>.<br><br>Liferay\ncan also synchronize its user list between the portal and an external\ndata source like another database or LDAP server. A default connector\nfor Microsoft Exchange is bundled with the portal.<br><br><b>ASP Model</b><br><br>Liferay\nwas designed from the ground up to be used by application service\nproviders. This means you can host multiple instances of the portal\n(distinguished by unique URLs) on one application server and database.</div>\n<div>&nbsp;</div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<div><b>CMS – Gestión de contenidos<br><br></b>Liferay ofrece la funcionalidad de su propio sistema de gestión de contenidos (CMS) mediante un grupo de portlets. Así, las organizaciones pueden contar con una herramienta flexible construida con lo más avanzado en tecnología XSLT. <br></div>\n<div>&nbsp;</div>\n<div><b>SSO<br><br></b>Liferay proporciona un conector <a _fcksavedurl=\"http://tp.its.yale.edu/tiki/tiki-index.php?page=CentralAuthenticationService\" href=\"http://tp.its.yale.edu/tiki/tiki-index.php?page=CentralAuthenticationService\">CAS</a> integrado. CAS es el motor de Single Sign On de la Universidad de Yale. No obstante, Liferay permite desarrollar integraciones con otros motores de Single Sign On como SSO (http://www.netegrity.com/) como <a _fcksavedurl=\"http://www.netegrity.com/\" href=\"http://www.netegrity.com/\">Netegrity</a>.<br><br>Liferay es capaz de sincronizar listas de usuarios entre el portal y fuentes de datos externas como otra base de datos o con un servidor LDAP, etc. Además, el portal incluye un conector por defecto para Microsoft Exchange.<br><br><b>Modelo en hosting (ASP)</b><br><br>Liferay ha sido diseñado desde su concepción para ser usado en formato ASP (Application Service Provider) o hosting. Esto permite que se puedan alojar múltiples instancias del portal (simplemente diferenciadas por URL´s distintas) sobre un único servidor de aplicaciones y una base de datos.</div>\n<div>&nbsp;</div></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606180700, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-TEXT3', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Text3', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div><b>Spring, EJB, and AOP<br><br></b>Liferay\'s business beans are\nbuilt on top of Spring. This allows you to leverage Spring\'s AOP, IOC,\nand proxy features to customize Liferay.<br><br>We use Spring to decide\nwhether to call the POJO (Plain Old Java Object) implementation of a\nbusiness bean or the EJB wrapped implementation of a business bean.\nThis allows deployers to decide whether to deploy Liferay on a\nheavyweight application server like Borland ES (and thus leverage\nVisiBroker\'s transaction features) or a lightweight container like\nTomcat.<br><br><b>Database Agnostic</b><br><br>Liferay uses <a href=\"http://www.hibernate.org/\" _fcksavedurl=\"http://www.hibernate.org/\">Hibernate</a>\nas the ORM tool for the persistence layer which enables pluggable\ndatabases (DB2, Firebird, Hypersonic, InterBase, JDataStore, MySQL,\nOracle, PostgreSQL, SAP, and SQL Server). This allows your organization\nto leverage existing resources without having to purchase new database\nhardware and software.</div>\n<div>&nbsp;</div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<div><b>Spring, EJB, y AOP<br><br></b>Los componentes de Liferay se construyen sobre el framework Spring, lo que permite personalizar Liferay con las funcionalidades de AOP, IOC y proxy.<br><br>Empleamos el framework SPRING para poder decidir si la implementación se realiza con objetos JAVA estándar, ejecutables en un contenedor ligero como TOMCAT o si se realiza con EJB´s ejecutables en un contenedor J2EE como Jboss, Borland ES<br><br><span style=\"font-weight: bold;\">Independencia respecto a Bases de Datos</span><b></b><br><br>Liferay emplea Hibernate (http://www.hibernate.org/) como herramienta ORM para la capa de persistencia, lo que facilita que soporte cualquier base de datos como DB2, Firebird, Hypersonic, InterBase, JDataStore, MySQL, Oracle, PostgreSQL, SAP o SQL Server. Esto permite a las organizaciones mantener los recursos con los que cuenta y no tener que adquirir nuevo hardware y software de base de datos.</div>\n<div>&nbsp;</div></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606180800, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-TEXT4', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Liferay Portal - Feature - Text4', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Scalable N-Tier Cluster<br><br></b>Liferay is very scalable and uses <a href=\"http://www.opensymphony.com/oscache\" _fcksavedurl=\"http://www.opensymphony.com/oscache\">OSCache</a> to provide deployers with a clustered cache. You can scale by adding more nodes without sacrificing on caching.<br><br>You\ncan cluster the enterprise release of Liferay in multiple tiers:\npresentation tier, business logic tier, and database tier to meet your\nspecific load requirements.<br><br><b>Struts and Tiles</b><br><br>Liferay leverages off of <a href=\"http://struts.apache.org/\" _fcksavedurl=\"http://struts.apache.org/\">Struts</a>\nto follow the MVC pattern. Most programmers are already familiar with\nStruts, which means your developers will have an easier time of writing\nportlets in a familiar framework.<br><br>The look and feel of the\nportal can be easily customized and reskinned because the display logic\nis concentrated in a few template files read by <a href=\"http://struts.apache.org/userGuide/dev_tiles.html\" _fcksavedurl=\"http://struts.apache.org/userGuide/dev_tiles.html\">Tiles</a>.</div>\n<div>&nbsp;</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>Altamente escalable<br><br></b>Liferay es escalable y usa <a _fcksavedurl=\"http://www.opensymphony.com/oscache\" href=\"http://www.opensymphony.com/oscache\">OSCache</a> para ofrecer a las personas encargadas de llevar a cabo el despliegue una caché en Cluster. De este modo es posible escalar añadiendo más nodos sin sacrificar la cache.<br><br>Es posible dividir la versión para empresas de Liferay en varias capas: capa de presentación, capa de lógica de negocio y capa de datos, todo ello para adaptarlo a los requisitos concretos de carga.<br><br><b>Struts y Tiles<br><br></b>Liferay está basado en <a _fcksavedurl=\"http://struts.apache.org/\" href=\"http://struts.apache.org/\">Struts </a>para seguir el patrón Modelo-Vista-Controlador (MVC). La mayor parte de los programadores están ya acostumbrados a Struts, lo que significa que podrán desarrollar portlets en un entorno familiar.<br><br>La apariencia del portal puede ser fácilmente adaptada y rediseñada, ya que la lógica de presentación está basada en sencillas plantillas que son leídas mediante <a href=\"http://struts.apache.org/userGuide/dev_tiles.html\" _fcksavedurl=\"http://struts.apache.org/userGuide/dev_tiles.html\">Tiles</a>.</div>\n<div>&nbsp;</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606180900, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-TEXT5', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Text5', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<b>Administration</b><br><br>Liferay allows administrators to easily manage <b><i>users, organizations, locations, and roles</i></b>\nthrough a GUI interface. Organizations and locations signify a\ncollection of users. Roles signify permissions that a group or user can\nbe bound to. Access to portlets are also restricted to users based on\nroles.<br><br>Administrators can also specify <b><i>community pages</i></b> so that all users who belong to a certain group see the same page.<br>\n<div>&nbsp;</div>\n<div><b>Community Based Portlets<br><br></b>Many of the bundled portlets are community aware. For example, if you add an event to your <i>HR</i> community calendar, then <i>Marketing</i> would not be aware of it.</div>\n<div>&nbsp;</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<b>Administración</b><br><br>Liferay permite que los administradores del portal gestionen fácilmente usuarios, grupos, localizaciones y roles a través de herramientas visuales. Los grupos y localizaciones representan conjuntos de usuarios, y un rol es un conjunto de permisos que un grupo o un usuario poseen. El acceso a portlets está restringido a usuarios en función de su rol.<br><br>Los administradores pueden definir páginas de comunidades, de modo que sólo aquellos usuarios que pertenezcan a esas comunidades accedan a ellas.<br>\n<div>&nbsp;</div>\n<div><b>Community Based Portlets<br><br></b>Many of the bundled portlets are community aware. For example, if you add an event to your <i>HR</i> community calendar, then <i>Marketing</i> would not be aware of it.</div>\n<div>&nbsp;</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606180900, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-LIFERAYPORTAL-FEATURE-THEMES', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products - Liferay Portal - Feature - Themes', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-LIFERAYPORTAL-FEATURE-THEMES.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-LIFERAYPORTAL-FEATURE-THEMES.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Themes</b></div>\n<div>&nbsp;</div>\n<div>Liferay provides a hot deployable theme architecture that allows you to change the look and feel of the portal without modifying Liferay\'s core code. Graphic designers can easily mock up new themes because all of our pages utilize DIVs and CSS standards.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>Temas de apariencia</b></div>\n<div>&nbsp;</div>\n<div>Liferay ofrece una arquitectura de temas (denominados en inglés Themes), que permite llevar a cabo cambios en la apariencia del portal sin cambiar el código código fuente de Liferay. Es posible desplegar nuevos temas en caliente sin necesidad de tener que detener la ejecución el portal.&nbsp; El uso de CSS y capas en las páginas de Liferay facilita a diseñadores gráficos la creación de nuevos temas a partir de los existentes.</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-TEXT-IMAGE', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-MESH', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Mesh', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-MESH.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-MESH.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div align=\"center\"><b><font class=\"font-x-large\">Reach. It\'s just a click away.</font><br></b><br><i>Liferay Mesh</i>... coming soon.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div align=\"center\"><b><font class=\"font-x-large\">Al alcance, sólo en un clic.<br><br></font></b><font class=\"font-x-large\">Liferay Mesh.... pronto disponible.</font><b><font class=\"font-x-large\"><br></font><br></b><br><i></i><br></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGE-TEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-PORTAL-SUPPORTED-AJAX', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Portal Supported Ajax', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-PORTAL-SUPPORTED-AJAX.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-PORTAL-SUPPORTED-AJAX.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<p><b>AJAX</b></p>\n<p><b>A</b>synchronous <b>J</b>avaScript <b>A</b>nd <b>X</b>ML (AJAX) is a web development technique for creating interactive web applications, and is supported by Liferay.&nbsp; This allows small exchange of small amounts of data behind the scenes, so that the entire Web page does not have to be reloaded each time the user makes a change, giving Liferay increased interactivity, speed, and usability.</p></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<p><b>AJAX</b></p>\n<p>AJAX (Asynchronous JavaScript Above XML), es una técnica de desarrollo web para la creación de aplicaciones web interactivas que Liferay también soporta. Esta técnica permite intercambios de cantidades reducidas de datos, lo que permite una respuesta más rápida a las acciones del usuario gracias a que no es necesario recargar toda la página web. De esta forma Liferay ofrece una mayor interactividad, velocidad y usabilidad.</p></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-PORTAL-SUPPORTED-HIBERNATE', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Portal Supported Hibernate', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-PORTAL-SUPPORTED-HIBERNATE.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-PORTAL-SUPPORTED-HIBERNATE.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div>\n<p><b>JSR-220 (Hibernate)</b></p>\n<p>All data is persisted using Hibernate and is called through the POJO implementations. Liferay used to rely on CMP technology to achieve persistence, but switched over to Hibernate because of its raw speed and flexibility. Liferay is database agnostic and can run on a variety of popular databases.</p></div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<div>\n<p><b>JSR-220 (Hibernate)</b></p>\n<p>Todos los datos son persistidos usando Hibernate, que es invocado desde POJOs. Liferay se basaba en la tecnología CMP para realizar la persistencia, pero esta tecnología fue reemplazada por Hibernate debido a su mayor velocidad y flexibilidad. Liferay es agnóstico con respecto a la base datos, y soporta una gran variedad de ellas.</p></div></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-PORTAL-SUPPORTED-JSF', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Portal Supported JSF', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-PORTAL-SUPPORTED-JSF.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-PORTAL-SUPPORTED-JSF.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<p><b>JSR-127 (JSF)</b></p>\n<p>Liferay Portal supports JavaServer Faces. You can develop custom JSF portlets using the \"rapid user-interface development\" style that JSF promises - via a visual development environment.&nbsp; Use any JSF implementation of your choice.&nbsp; We have examples of portlets in JSF implementations by Sun and Apache MyFaces.</p></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<p><b>JSR-127 (JSF)</b></p>\n<p>Liferay Portal incluye soporte para JavaServer Faces. Es posible desarrollar portlets JSF a medida usando el modelo de “desarrollo rápido de interfaces de usuario? que JSF proporciona empleando un entorno de desarrollo visual. Es posible, además usar cualquier implementación de JSF. En nuestra web encontrará ejemplos de portlets con la implementación de JSF de Sun y con Apache MyFaces.</p></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-PORTAL-SUPPORTED-JSR168', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Portal Supported JSR168', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-PORTAL-SUPPORTED-JSR168.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-PORTAL-SUPPORTED-JSR168.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<p><b>JSR-168</b></p>You can hot deploy any JSR-168 compliant portlet. This means you can provide more functionality to your end users by writing custom portlets. Or, you can deploy portlets that you buy from a portlet vendor.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<p><b>JSR-168</b></p>Es posible desplegar en caliente cualquier portlet que siga el estándar JSR 168, de este modo se consigue añadir más funcionalidades a liferay desarrollando portlets a medida. Otra opción es desplegar portlets que se hayan adquirido a terceros.</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-PORTAL-SUPPORTED-SPRING', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Portal Supported Spring', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-PORTAL-SUPPORTED-SPRING.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-PORTAL-SUPPORTED-SPRING.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<p><b>Spring, EJB, and Aspect Oriented Programming (AOP)</b></p>\n<p>Liferay\'s business beans are built on top of Spring. This allows you to leverage Spring\'s AOP, IOC, and proxy features to customize Liferay.<br><br>We use Spring to decide whether to call the POJO implementation of a business bean or the EJB wrapped implementation of a business bean. This allows deployers to decide whether to deploy Liferay on a heavyweight application server like Borland ES (and thus leverage VisiBroker\'s transaction features) or a lightweight container like Tomcat.</p></div>\n<div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><div>\n<p><b>Spring, EJB y&nbsp; programación orientada a aspectos (AOP)</b></p>\n<p>Las clases Java que implementan la lógica de negocio se gestionan\nmediante Spring. Esto permite utilizar AOP, IOC y las funcionalidades\nde proxy de Spring para configurar Liferay.<br><br>Usando Spring es\nposible decidir si se invocan las clases con la lógica de negocio como\nPOJOs o si se invocan a través de una capa de EJBs. Esto permite a los\ndesarrolladores decidir entre desplegar Liferay en un servidor de\naplicaciones pesado como Borland ES, o un contenedor ligero como Tomcat.</p></div></div>\n<div></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-PORTAL-SUPPORTED-STRUTS', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Portal Supported Struts', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-PORTAL-SUPPORTED-STRUTS.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-PORTAL-SUPPORTED-STRUTS.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<p><b>Struts and Tiles</b></p>\n<p>Liferay leverages&nbsp;Struts and follows the MVC pattern. Most programmers are already familiar with Struts, which means your developers will have an easier time of writing portlets in a familiar framework.</p>\n<p>The look and feel of the portal can be easily customized and reskinned because the display logic is concentrated in a few template files read by Tiles.</p></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<p><b>Struts y Tiles</b></p>Liferay hace uso de Struts y sigue el patrón MVC. La mayoría de los programadores están familiarizados con Struts, lo que significa que será más sencillo para ellos programar portlets en un entorno conocido.<br><br>La apariencia del portal puede ser adaptada fácilmente&nbsp; gracias a que la lógica de presentación está concentrado en varias plantillas de Tiles.<br><br></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-PORTAL-SUPPORTED-VELOCITY', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Portal Supported Velocity', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-PORTAL-SUPPORTED-VELOCITY.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-PORTAL-SUPPORTED-VELOCITY.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<p><b>Velocity</b></p>\n<p>Velocity is a Java-based template engine.&nbsp; It currently is being used within portlets, to layout Journal articles, and to design themes.</p></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<p><b>Velocity</b></p>\n<p>Velocity es un motor de plantillas basado en Java. Actualmente está siendo usado para diseñar temas de apariencia y por el portlet de Diario para presentar sus artículos.</p></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PRODUCTS-PORTAL-SUPPORTED-WSRP', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Products Portal Supported WSRP', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.PRODUCTS-PORTAL-SUPPORTED-WSRP.image.1.0\">/image/journal/article?img_id=1.PRODUCTS-PORTAL-SUPPORTED-WSRP.image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<p><b>WSRP</b></p>\n<p>WSRP is a web services standard that allows you to <i>publish</i> portlets from your portal to external portals and allows you to <i>consume</i> portlets in your portal from external portals. This allows you to buy and sell portlet functionality as a service.</p></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<p><b>WSRP</b></p>\n<p>WSRP forma parte del conjunto de estándares de servicios web&nbsp; y permite publicar portlets desde nuestro portal a otro, así como importar portlets de otros portales al nuestro. Esto permite comprar y vender funcionalidades de portlet como si de servicios se tratara.</p></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'PUBLIC-TRAINING-EUROPE', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Public Training - Europe', '', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content><![CDATA[<div><div class=\"font-small\" style=\"PADDING-RIGHT: 10px; PADDING-LEFT: 10px; FLOAT: right; PADDING-BOTTOM: 10px; MARGIN: 10px; WIDTH: 200px; COLOR: rgb(0,31,64); PADDING-TOP: 10px; BACKGROUND-COLOR: rgb(255,255,255)\"><div><div><strong>Training Schedule</strong></div><div><a style=\"TEXT-DECORATION: underline\" href=\"../../../../../web/guest/services/training/public/course_topics\">Course Topics for US Training</a><br />Europe training schedule will differ.<br /></div><br /><div><span style=\"FONT-WEIGHT: bold\">Training Time</span><br />3 days, TBD<br />9am-5pm daily<br /><br /><span style=\"FONT-WEIGHT: bold\">Location</span><br /></div><div>To be determined<br /></div><div><br /><span style=\"FONT-WEIGHT: bold\">Additional Information</span><br />Please note that a laptop is required for this course.</div><div>&nbsp;</div><div><div><strong>How to Register</strong></div><div>Email <a href=\"mailto:sales@liferay.com\">sales@liferay.com</a>.</div></div></div></div><div><div><div><span style=\"FONT-WEIGHT: bold\">Overview</span><br /><br />Liferay will host a public training seminar in Europe (date and city to be determined).&nbsp; The seminar will be comprehensive, covering architecture, installation, portlet development, themes, and CMS.<br /><br />This course is designed to help you come up to speed on the functionality and development of Liferay Portal.&nbsp; It will help you to increase your productivity as well as introduce you to valuable new features, such as the <span style=\"FONT-STYLE: italic\">runtime-portlet</span> tag and hot-deployable themes.&nbsp; We will also&nbsp;teach you how to migrate your existing Struts applications to a portlet environment.<br /></div><div><span style=\"FONT-WEIGHT: bold\"></span>&nbsp;</div><div><span style=\"FONT-WEIGHT: bold\">NEW 4.0.0 Release</span> </div><div>&nbsp;</div><div>Come and learn about&nbsp;the&nbsp;new features Liferay Portal 4.0.0 has to offer!</div><div><br /><span style=\"FONT-WEIGHT: bold\">Hands-On Training</span><br /><br />We will take you through the entire process of portlet development and teach you industry best practices.&nbsp; You will learn how to code the action class, auto-generate business components, implement server-side validation, and perform database operations.</div><div>&nbsp;</div><div><strong>Subject Matter Experts</strong></div><div>&nbsp;</div><div>The course will be led by the Liferay experts who designed the software.</div><div><br /><span style=\"FONT-WEIGHT: bold\">Development Environment</span><br /><br />For development we will setup Tomcat on Eclipse and use MySQL for the database.&nbsp; Although Liferay can be used with any application server and database, the above setup is the fastest in terms of development.&nbsp; We may also touch upon installation with other application servers and databases as well.<br /><br /><span style=\"FONT-WEIGHT: bold\">Prerequisites</span><br /><br />Basic knowledge of Java and Servlets is required.&nbsp; The course will be intensive, but we will make sure to answer any questions you have to ensure that you have a solid grasp of the technology by the end of the course.</div><div>&nbsp;</div><div><strong>Please note that a laptop is required for this course.</strong></div><div>&nbsp;</div><div>Minimum System Requirements: 1.4 GHz 512MB RAM <div><div>Recommended System Requirements: 2.0 GHz 1GB RAM</div><div>Wireless Card Required</div></div></div></div></div></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060614002900, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
