insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'SAMPLE-PUBLICDEMO', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Public Demo', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<span lang=\"DE\">Our public demo provides an opportunity for those who have not experienced a demo of Liferay Portal to see what we are all about. For those who have familiarity using Liferay Portal, we will be going through many new and exciting advanced features. Liferay 4.0.0 is a major leap forward in web portals, sporting fancy AJAX drag and drop portlets, enterprise grade fine grained permissions, a redesigned CMS system, and much more.<br /><br />The next public demo is scheduled for Wed, July 12th at 1:00 PM PDT.<br /><br /></span><p class=\"MsoNormal\"><o:p></o:p>You can also request any specific items to be included during the demo when you e-mail <a href=\"mailto:sales@liferay.com\">sales@liferay.com</a> for your username and password!</p>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<span style=\"\" lang=\"DE\">La sesión de demo de liferay está pensada para todos aquellos que aún no conocen Liferay. Adicionalmente se repasarán, para aquellos que ya lo conocen, las nuevas y excitantes funcionalidades. Liferay 4.0.0 supone un salto cualitativo importante: construcción de webs, arrastrar y soltar portlets con AJAX, gestión de permisos detallada y cómo aprovechar al máximo el gestor de contenidos entre otras posibilidades.<br><br>También puede solicitar la inclusión de determinados aspectos de Liferay cuando escriba a sales@liferay.com para solicitar un nombre de usuario y contraseña.</span>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060608015600, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'SERVICES', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Services', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>The complexities of today\'s technology infrastructure present a\nchallenge for companies.&nbsp; Liferay offers a full scale of professional\nservices to assist you with every stage of the application lifecycle.&nbsp;\nFrom integrating your existing systems with Liferay Portal to onsite\ntraining, Liferay will help your company run more efficiently.<br><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"5\" width=\"100%\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\" width=\"33%\"><span style=\"font-weight: bold;\">Support</span></td>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\" width=\"33%\"><span style=\"font-weight: bold;\">Training</span></td>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\" width=\"33%\"><span style=\"font-weight: bold;\">Custom&nbsp;Development</span></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\" valign=\"top\">Liferay\noffers two lines of support services to meet your company\'s needs:\nLiferay Enterprise Support and Liferay Production Support.&nbsp; Hands-on\nLiferay experts are available to support your developers. Your\norganization will be given multiple accounts to our support site as\nneeded. Depending on the level of support, a reply to an issue is made\nwithin its respective response time. Custom support services can be\ntailored to&nbsp;your specific needs. Support is available 24 hours a day, 7\ndays a week. </td>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\" valign=\"top\">Liferay\nTraining is tailored to fit your organization\'s unique needs and can\nlast anywhere from&nbsp;three days to a few weeks. Topics include\ndevelopment of portlets according to the JSR-168 (Portlet API)\nspecification, customization of Liferay, and deployment of Liferay in a\nproduction environment. Customized training topics to meet your\ncompany\'s specific needs are also available.</td>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\" valign=\"top\">Liferay\nexperts are available to help you at every stage of the application\nlifecycle. Our services include, but are not limited to: assisting with\ninstallation issues, migrating your organization\'s existing\napplications to Liferay Portal, and writing custom portlets. Whether\nyou are just getting started or need our help meeting a deadline, we\nwill help you get on the ground running. </td></tr></tbody></table><br>Liferay consultants are available to visit your organization. Please contact us at <a href=\"mailto:sales@liferay.com\" _fcksavedurl=\"mailto:sales@liferay.com\">sales@liferay.com</a> for more information on any of our services.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>La complejidad de la infraestructura tecnológica de hoy en día presenta un reto para las compañías. Liferay ofrece un amplio abanico de servicios profesionales para dar soporte en cualquiera de las diferentes fases del ciclo de vida del desarrollo de aplicaciones. Desde la integración de Liferay Portal con sistemas existentes en su compañía hasta la formación onsite, Liferay ayuda a su organización para funcionar de forma más eficiente.<br><br>\n<table border=\"0\" cellpadding=\"5\" cellspacing=\"5\" width=\"100%\">\n<tbody>\n<tr>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\" width=\"33%\"><span style=\"font-weight: bold;\">Soporte Profesional <br></span></td>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\" width=\"33%\"><span style=\"font-weight: bold;\">Formación</span></td>\n<td style=\"color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\" width=\"33%\"><span style=\"font-weight: bold;\">Desarrollos a medida</span></td></tr>\n<tr>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\" valign=\"top\">Liferay ofrece dos líneas de servicios de soporte: Liferay Enterprise Support y Liferay Production Support. Expertos en Liferay están a su disposición para dar soporte a su equipo de desarrolladores. Su organización recibirá varias cuentas para acceder a nuestro sitio web de soporte, de acuerdo a sus necesidades. En función del tipo de soporte contratado, las respuestas a incidencias tienen un tiempo de respuesta máximo. Los servicios de soporte pueden también ser diseñados a medida para adaptarse a sus necesidades. El soporte está disponible 24 horas al día y 7 días a la semana. </td>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\" valign=\"top\">Los cursos de formación son confeccionados a medida, y pueden ser desde de tres días hasta de varias semanas. El temario disponible incluye desarrollo de portlets de acuerdo al estándar JSR 168, adaptación de Liferay a los sistemas con que cuenta el cliente y despliegue de Liferay en entornos de producción. También es posible confeccionar temarios a medida en función de necesidades particulares de su compañía.</td>\n<td style=\"color: rgb(0, 31, 64); background-color: rgb(255, 255, 255);\" valign=\"top\">Ponemos a su disposición expertos de Liferay para ayudar en cualquier fase dentro del ciclo de vida de su aplicación. Algunos de nuestros servicios son: asistencia para instalación, migración de aplicaciones existentes a Liferay Portal y desarrollo de portlets a medida. Ya sea para comenzar a trabajar con Liferay o para cumplir con una fecha de entrega, le ayudamos a acelerar cualquier desarrollo. </td></tr></tbody></table><br>Tiene a su disposición consultores de Liferay que pueden visitarle. Para más información contacte con nosotros en <a _fcksavedurl=\"http://mailto:sales@liferay.com\" href=\"http://mailto:sales@liferay.com\">sales@liferay.com</a>. &nbsp;</div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606181000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'SERVICES-CONSULTING', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Services Consulting', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div>\n<div>Liferay Consulting Services consistently provide innovative and\ncost-effective solutions.&nbsp; This has garnered us the attention of the\nindustry as being a company that enterprises can depend on. <br><br>Liferay\nConsulting Services help optimize your usage of Liferay Portal and\nmaximize your investment. Our consulting services are handled by the\nmost knowledgeable developers of Liferay Portal in the industry—they\nare the very people who designed our products.&nbsp; In addition to expert\nknowledge of Liferay\'s products, our developers have extensive\nexperience with different technologies used to build enterprise\narchitectures. Liferay\'s developers can assess your company\'s needs and\nprovide an implementation plan so that you will have a firm\nunderstanding of where you are now and the solutions that will take you\nto where you want to be. <br><br>Liferay Consulting Services is ready\nto help you at every stage of the application lifecycle. We provide you\nwith the security in knowing that you will be up and running faster and\nsafer. Our consulting services include, but are not limited to:<br>\n<div>\n<div>&nbsp; </div>\n<div>\n<table cellpadding=\"5\" cellspacing=\"5\" width=\"100%\">\n<tbody>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Installation assistance</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Assist with installing, deploying, and configuring Liferay Portal.</li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Migration assistance</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Assist with migrating from any portal to Liferay Portal. </li><li>Assist with migrating applications onto Liferay Portal. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Custom solutions</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Provide customized solutions to meet the diverse needs of enterprises. </li><li>Create a proof of concept pilot before investing heavily in your portal project. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">System analysis and design</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Analyze end user needs and business requirements within a portal infrastructure.</li><li>Assist with software design of your portal project. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Front end theme design</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Create front end themes that reflect your company\'s image. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Code validation</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Provides a validation service for your portal development projects.</li><li>Offers you peace of mind that your projects are on track.</li><li>Reduces cost and time spent troubleshooting errors. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Architecture assistance</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Ensure best practices approach to portal development.</li><li>Provide a detailed plan of recommended enhancements. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Performance tuning and scalability</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Provide recommendation of hardware/software environment tailored to your organization.</li><li>Optimize Liferay Portal performance.</li><li>Assist with setting up a clustered environment for scalability and fault tolerance. </li></ul></td></tr></tbody></table></div></div><br style=\"font-weight: bold;\"><span style=\"font-weight: bold;\">Liferay Custom Solutions</span><br><br>By\nchoosing Liferay Custom solutions, your team and&nbsp;members of our team\nwill work together towards building customized solutions for your\nenterprise. Our customized solutions can address every aspects of the\napplication lifecycle. As an added benefit to our services, your\ntechnology staff will acquire extensive product and development\nknowledge through working alongside our developers.&nbsp; <br><br><span style=\"font-weight: bold;\">Contact Liferay Consulting Services</span><br><br>For more information regarding Liferay Consulting Services, please email us at: <a href=\"mailto:sales@liferay.com\" _fcksavedurl=\"mailto:sales@liferay.com\">sales@liferay.com</a><br></div></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<div>Los servicios de consultoría de Liferay ofrecen consistentemente soluciones innovadoras y rentables. La calidad de estos servicios han permitido que Liferay sea considerada como una empresa en las que las compañías pueden confiar y de la que depender.<br><br>Los servicios de consultoría de Liferay ayudan a las empresas a sacar el máximo partido de Liferay Portal maximizando los resultados de su inversión. Nuestros servicios de consultoría son prestados por los desarrolladores más experimentados en Liferay Portal en la industria - son los propios desarrolladores de nuestros productos. Además de su conocimiento en los productos de Liferay, estos consultores tienen amplia experiencia en las diferentes arquitecturas utilizadas habitualmente en aplicaciones empresariales. Los desarrolladores de Liferay pueden asesorar a las empresas y proporcionar un plan de implementación, lo que permite a las empresas conocer en qué situación se encuentran y qué soluciones necesitan para llegar donde desean.<br><br>Los Servicios de Consultoría de Liferay pueden ayudarle en todas las fases del ciclo de vida del desarrollo de aplicaciones. Le proporcionamos la seguridad de que va a progresar de forma más rápida y más segura. Nuestros servicios de consultoría incluyen:<br>\n<div>\n<div>&nbsp; </div>\n<div>\n<table cellpadding=\"5\" cellspacing=\"5\" width=\"100%\">\n<tbody>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Asistencia a la instalación</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Asistencia en la instalación, despliegue y configuración de Liferay Portal.</li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Asistencia en migraciones</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Asistencia para llevar a cabo migraciones a Liferay Portal desde otros portales. </li><li>Asistencia para llevar a cabo migraciones de aplicaciones a Liferay Portal. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Soluciones a medida</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Soluciones a medida que cubren las diversas necesidades de las empresas. </li><li>Diseño de una prueba de concepto piloto antes de hacer una inversión significativa en un proyecto de portal. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Análisis y diseño de sistemas</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Análisis de las necesidades de los usuarios finales y de los requerimientos de negocio dentro de una infraestructura de portal.</li><li>Asistencia en el diseño software en proyectos de portal </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Diseño gráfico de temas para el&nbsp; “front-end?</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Creación de temas para el front-end que sigan la imagen corporativa de las compañías. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Validación de código</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Servicio de validación para sus proyectos de desarrollo de portales.</li><li>Le permite tener la tranquilidad de que sus proyectos marchan según lo previsto.</li><li>Reduce el coste y tiempo invertido en solucionar errores. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Asistencia en arquitectura</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Aproximación al desarrollo de portales basada en buenas prácticas.</li><li>Plan detallado de mejoras recomendadas. </li></ul></td></tr>\n<tr>\n<td style=\"font-weight: bold; color: rgb(255, 255, 255); background-color: rgb(0, 31, 64);\">Tuning de rendimiento y escalabilidad</td>\n<td style=\"background-color: rgb(148, 170, 191);\">\n<ul><li>Recomendación del entorno hardware/software más adecuado para su organización.</li><li>Optimización de rendimiento de Liferay Portal.</li><li>Asistencia para implantar un entorno en cluster para soluciones de escalabilidad o alta disponibilidad. <br> </li></ul></td></tr></tbody></table></div></div><br style=\"font-weight: bold;\"><span style=\"font-weight: bold;\">Servicio de desarrollos a medida de Liferay</span><br><br>Al elegir el servicio de desarrollo a medida de Liferay, su equipo y los desarrolladores de Liferay trabajarán juntos para desarrollar soluciones a medida para su empresa. Nuestras soluciones a medida pueden orientarse a cualquier aspecto del ciclo de vida del desarrollo de aplicaciones. Como un beneficio añadido de nuestros servicios, su personal técnico adquirirá amplia experiencia en el producto y en desarrollos basados en el mismo, a través del trabajo conjunto con nuestros desarrolladores.<br><br><span style=\"font-weight: bold;\">Contactar con los servicios de consultoría de Liferay</span><br><br>Para más información acerca de los servicios de consultoría de Liferay, por favor envíe un correo a <a _fcksavedurl=\"http://mailto:sales@liferay.com\" href=\"http://mailto:sales@liferay.com\">sales@liferay.com</a><a href=\"mailto:sales@liferay.com\" _fcksavedurl=\"mailto:sales@liferay.com\"></a><br></div></div>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606181000, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
