insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'SERVICES-TRAINING', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Services Training', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"textarea\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[Liferay provides two types of personal training for its customers: (1) onsite and (2) public.\n<br><br>\nThe custom onsite training will be personalized to your needs. We will\nsend our trainer(s) to your offices and work with your engineers on\nwhatever you need for the success of your product.\n<br><br>The public training is setup for 15-20 students where our\ntrainer(s) walk you through everything from setting up your development\nenvironment to creating CMS content to designing your own portlets.]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[Liferay proporciona dos tipos de formación para sus clientes : (1) onsite y (2) pública.<br><br>La formación onsite a medida será adaptada a sus necesidades. Le enviaremos nuestro(s) profesor(es) a sus oficinas y trabajarán con sus ingenieros en lo que usted necesite para el éxito de su producto.<br><br>La formación pública está orientada a 15-20 estudiantes donde nuestro(s) profesor(es) le enseñarán desde a preparar&nbsp; su entorno de despliegue a crear contenidos mediante el CMS o diseñar sus propios portlets.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-TEXT', 'BASIC-TEXT', SPECIFIC_TIMESTAMP_20060606181500, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
