insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'STORIES', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Stories', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"title\" type=\"text\">\n    <dynamic-content><![CDATA[Stories]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"subtitle\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><p style=\"MARGIN-BOTTOM: 0in\">Every day we at Liferay are asked about how&nbsp;we can get involved in various portal projects around the world. These businesses range from mom and pop stores to Fortune 500 firms. Their challenges are vast but the solution often ends up being the same: Liferay Portal. Take a glance at the stories of some of these organizations.&nbsp; You will be surprised by the success that so many have had with Liferay Portal.</p><br />If you would like to contribute your own story, contact us at <a href=\"mailto:staff@liferay.com\">staff@liferay.com</a>.<br /></div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div>\n<p style=\"margin-bottom: 0in;\">Casi cada día, alguien nos pregunta cómo podemos dar solución a proyectos de portales tan diferentes en todo el mundo. El espectro de clientes va desde “la tienda de la esquina? hasta empresas multinacionales. Sus retos son enormes, pero la solución, a menudo, acaba siendo la misma: Liferay Portal. Revise los casos de algunas de estas organizaciones. Se sorprenderá de cuántas han adoptado con éxito Liferay Portal.</p><br>Si quiere aportar su propio caso, contacte con nosotros en <a _fcksavedurl=\"http://mailto:staff@liferay.com\" href=\"http://mailto:staff@liferay.com\">staff@liferay.com</a>.<br></div>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"feature-logo-1\" type=\"text\">\n    <dynamic-content><![CDATA[@portal_url@/image/journal/article?img_id=1.STORIES-EDUCAMADRID.logo-image&version=1.0]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"feature-link-1\" type=\"text\">\n    <dynamic-content><![CDATA[@friendly_url_public@/guest/stories/educamadrid]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"feature-logo-2\" type=\"text\">\n    <dynamic-content><![CDATA[@portal_url@/image/journal/article?img_id=1.STORIES-GOODWILL.logo-image&version=1.0]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"feature-link-2\" type=\"text\">\n    <dynamic-content><![CDATA[@friendly_url_public@/guest/stories/goodwill]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"feature-logo-3\" type=\"text\">\n    <dynamic-content><![CDATA[@portal_url@/image/journal/article?img_id=1.STORIES-JASONS-DELI.logo-image&version=1.0]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"feature-link-3\" type=\"text\">\n    <dynamic-content><![CDATA[@friendly_url_public@/guest/stories/jasons_deli]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"feature-logo-4\" type=\"text\">\n    <dynamic-content><![CDATA[@portal_url@/image/journal/article?img_id=1.STORIES-OAKWOOD.logo-image&version=1.0]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"feature-link-4\" type=\"text\">\n    <dynamic-content><![CDATA[@friendly_url_public@/guest/stories/oakwood]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'STORIES', 'STORIES', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'STORIES-EDUCAMADRID', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Stories EducaMadrid', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"content-title\" type=\"text\">\n    <dynamic-content><![CDATA[EducaMadrid ]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"logo-image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.STORIES-EDUCAMADRID.logo-image.1.0\">/image/journal/article?img_id=1.STORIES-EDUCAMADRID.logo-image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"company-link\" type=\"text\">\n    <dynamic-content><![CDATA[www.educa.madrid.org]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"quote\" type=\"text_box\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[&quot;Its open source nature has made possible a big deal of changes and adaptation which would have been impossible or very costly with a commercial product.&quot; - Jorge Ferrer, Germinus]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[&quot;Al ser software libre, ha sido posible realizar una gran cantidad de cambios y adaptaciones que habrían sido imposibles o muy costosas de implantar sobre un producto comercial&quot; - Jorge Ferrer, Germinus]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Company Profile</b><br><br>EducaMadrid represents the Madrid, Spain school system which accounts for over 500,000 students across 1,600 schools. EducaMadrid is sponsored by the Consejería de Educación de la Comunidad Autonoma de Madrid, an organism of the Spanish public administration.<br><br><b>Challenge</b><br><br>EducaMadrid needed an application which would not only tie together students within a class but also establish relationships between other classes, grades, and even schools. With 500,000 potential users, EducaMadrid required an application that would scale to&nbsp;meet their large user base&nbsp;and&nbsp;best fit their budget. Commercial applications often come with a high price tag along with a per user cost. EducaMadrid needed the following from an application:\n<ul>\n<li>Ability to scale to the total number of users and students. </li>\n<li>Flexible and customizable layouts dependent on the user. </li>\n<li>Community resources accessible to everyone.</li></ul><b>Resolution</b><br><br>Germinus, a consulting company located in Spain, selected Liferay Enterprise Portal as the framework application for EducaMadrid. Liferay\'s framework provided the ability to create an extranet comprised of a set of websites with educative news and contents, as well as an intranet that contained deeply personalizable websites dependent on the hierarchal role of the registered user. <br><br>By leveraging Liferay\'s capabilities, Germinus was able to:\n<ul>\n<li>Implement a Web builder (creation of websites based on portlets) </li>\n<li>Establish virtual communities (group chat, classroom email, etc.) </li>\n<li>Implement LDAP authentication and user management </li>\n<li>Allow content management </li>\n<li>Integrate with MvnForum </li>\n<li>Integrate with legacy applications made with struts </li>\n<li>Develop specific tools: contextual help system, schools management, etc.</li></ul>With Liferay\'s robust UI, Germinus was able customize the look and feel of the portal according to the user\'s hierarchal role and/or grade. Teachers have a \"desktop\" that displayed classroom materials, and students would see more student oriented UI. As the students progressed to higher grades, the actual interface becomes increasingly more complex (see screen shots). <br><br>EducaMadrid saved hundreds of thousands of dollars by implementing Liferay Portal. With each portlet acting like a standalone web application, Liferay provides the means to extend functionality without huge refactoring costs.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>Perfil de la Compañía<br><br></b>EducaMadrid representa al sistema escolar de Madrid, en España, que incluye alrededor de 500.000 estudiantes en 1.600 escuelas. EducaMadrid está promovido por la Consejería de Educación de la Comunidad Autónoma de Madrid, un organismo de la administración pública española.<br><br><b>El Reto</b><br><br>EducaMadrid necesitaba una aplicación que no sólo relacionase estudiantes dentro de una clase sino que estableciese relaciones entre otras clases, cursos e, incluso, escuelas. Con 500.000 potenciales usuarios, EducaMadrid requería una aplicación que escalase para soportar su gran base de usuarios y se adaptase a su presupuesto. Las aplicaciones comerciales, a menudo, están acompañadas de un alto coste por usuario. EducaMadrid necesitaba lo siguiente de una aplicación:\n<ul>\n<li>Capacidad para escalar al número total de usuarios y estudiantes </li>\n<li>Que proporcionase apariencias flexibles y personalizables dependientes del usuario. </li>\n<li>Recursos de la comunidad libremente accesibles por sus miembros.</li></ul><b>La Solución</b><br><br>Germinus, una compañía de consultoría radicada en España, seleccionó Liferay Enterprise Portal como el marco de aplicaciones para EducaMadrid. El marco de Liferay proporcionó la capacidad para crear una extranet formada por un conjunto de sitios web con noticias y contenidos educativos, así como una intranet que ofrecía websites altamente personalizables dependientes del rol y la jerarquía del usuario registrado.<br><br>Al adoptar las características de Liferay, Germinus fue capaz de:&nbsp;\n<ul>\n<li>Implementar un creador de Webs (creación de sitios webs basados en portlets) </li>\n<li>Establecer comunidades virtuales (chat de grupo, dirección de correo electrónico de la clase, etc.) </li>\n<li>Implementar autenticación sobre LDAP y gestión de usuarios </li>\n<li>Permitir la gestión de contenidos </li>\n<li>Integrar con MvnForum </li>\n<li>Integrar con aplicaciones propietarias hechas con struts </li>\n<li>Desarrollar herramientas específicas: sistema de ayuda contextual, gestión de colegios, etc</li></ul>Con la robusta interfaz de usuario de Liferay, Germinus fue capaz de personalizar la apariencia del portal de acuerdo con el rol jerárquico del usuario y/o curso. Los profesores tuvieron un “escritorio? que mostraba los materiales de clase y los estudiantes verían una interfaz de usuario más orientada hacia ellos. Con la evolución de los alumnos y la promoción a cursos superiores, la interfaz se hace cada vez más compleja (ver capturas de pantalla).<br><br>EducaMadrid ahorró cientos de miles de dólares al implantar Liferay Portal. Con cada portlet actuando como una aplicación web independiente, Liferay proporciona los mecanismos para ampliar funcionalidad sin altos costes de refactorización.</div>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-1\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-thumbnail-1.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-1\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-full-1.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-2\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-thumbnail-2.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-2\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-full-2.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-3\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-thumbnail-3.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-3\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-full-3.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-4\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-thumbnail-4.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-4\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-full-4.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-5\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-thumbnail-5.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-5\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-full-5.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-6\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-thumbnail-6.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-6\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-full-6.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-7\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-thumbnail-7.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-7\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-full-7.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-8\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-thumbnail-8.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-8\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-EDUCAMADRID.screenshot-full-8.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-9\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-9\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-10\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-10\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"title\" type=\"text\">\n    <dynamic-content><![CDATA[EducaMadrid]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"subtitle\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-SHOWCASE', 'BASIC-SHOWCASE', SPECIFIC_TIMESTAMP_20060501180200, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, groupId, articleId, version, userId, userName, createDate, modifiedDate, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 1, 'STORIES-GOODWILL', 1.0, 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Stories Goodwill', '', '<?xml version=\"1.0\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US,es_ES\">\n  <dynamic-element name=\"content-title\" type=\"text\">\n    <dynamic-content><![CDATA[Goodwill]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"logo-image\" type=\"image\">\n    <dynamic-content id=\"liferay.com.journal.article.1.STORIES-GOODWILL.logo-image.1.0\">/image/journal/article?img_id=1.STORIES-GOODWILL.logo-image&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"company-link\" type=\"text\">\n    <dynamic-content><![CDATA[www.goodwill.org]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"quote\" type=\"text_box\">\n    <dynamic-content language-id=\"es_ES\"><![CDATA[&quot;Liferay Portal es, económicamente hablando, la mejor solución y la tecnología de portal más eficiente que hemos analizado&quot; - Steve Bergman, CIO - Goodwill Industries International ]]></dynamic-content>\n    <dynamic-content language-id=\"en_US\"><![CDATA[&quot;Liferay Portal was by far the most cost effective and efficient portal technology we considered.&quot; - Steve Bergman, CIO - Goodwill Industries International ]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content language-id=\"es_ES\"><![CDATA[<div><b>Perfil de la Compañía</b><br><br>Goodwill Industries International es una de las organizaciones sin ánimo de lucro más grandes del mundo que proporcionan educación, formación y otros servicios para personas cuyas necesidades van desde la asistencia social, ayuda a la dependencia, falta de formación o experiencia laboral y aquellos con alguna discapacidad físca, mental o emocional. El pasado año, Goodwill, a través de sus servicios locales proporción empleo y servicios de formación a más de 720.000 personas.<br><br><span style=\"font-weight: bold;\">El Reto</span><br><br>Goodwill necesitaba una solución que proporcionase el marco tecnológico para soportar tanto su web pública <a href=\"http://www.goodwill.org/\" _fcksavedurl=\"http://www.goodwill.org/\">www.goodwill.org</a> como su nueva intranet. Denominado como MyGoodwill, esta nueva intranet debía suponer la herramienta de referencia dentro de la compañía internacional sin ánimo de lucro, permitiendo a los miles de miembros de Goodwill de todo el mundo colaborar, compartir métodos de trabajo y proporcionar aprendizaje en tiempo real. Adicionalmente en Goodwill, como en otras organizaciones no lucrativas, mantener una inversión moderada es siempre un asunto primordial. Era de vital importancia conseguir una solución que proporcionase la tecnología más potente y flexible con el mínimo coste. <br><br><span style=\"font-weight: bold;\">La solución</span> <br><br>En el momento en el que Goodwill seleccionó Liferay, su equipo de IT tenía muchas reservas sobre el hecho de apostar por un producto de software libre. Al arrancar el proyecto, los miedos en Goodwill rápidamente desaparecieron. Bergman dijo, “Primero y más importante, supone un acelerado retorno de la inversión, lo que posiblemente nos permitirá implantar antes nuevas características en el portal. Liferay Portal se adapta fácilmente a la arquietectura del data center de Goodwill y a la diversa combinación de tecnologías que empleamos. Finalmente, ofrece una robustez y escalabilidad excepcionales. Liferay Portal es, económicamente hablando, la mejor solución y la tecnología de portal más eficiente que hemos analizado.? Liferay proporcionó las siguientes características:\n<ul>\n<li>Chat colaborativo y foros</li>\n<li>Distribución de conocimiento mediante blogs</li>\n<li>Páginas “wiki? que son actualizadas regularmente por los visitantes</li>\n<li>Módulos de aprendizaje basados en ordenador</li>\n<li>Repositorio de documentación</li></ul>Liferay superó con facilidad todas las expectativas en Goodwill. Adaptaciones que Bergman esperaba que se implantaran en seis u ocho semanas, se realizaron en un fin de semana. No sólo proporcionó la funcionalidad deseada, sino que que supuso un ahorro de costes a largo plazo con el que no se contaba. Todas las reticencias se olvidaron cuando la implantación de Liferay fue completada.</div>]]></dynamic-content>\n    <dynamic-content language-id=\"en_US\"><![CDATA[<div><b>Company Profile</b> <br><br>Goodwill Industries International is one of the world\'s largest nonprofit providers of education, training, and career services for people with disadvantages, such as welfare dependency, homelessness, lack of education or work experience, and those with physical, mental, and emotional disabilities. Last year, local Goodwills collectively provided employment and training services to more than 720,000 individuals. <br><br><b>Challenge</b> <br><br>Goodwill needed a solution that would provide the technology framework behind its public web site <a href=\"http://www.goodwill.org/\" _fcksavedurl=\"http://www.goodwill.org/\">www.goodwill.org</a> and its new intranet site. Known as MyGoodwill, this new site was to create a landmark organizational tool for the international nonprofit company, enabling thousands of Goodwill staff worldwide to collaborate, share best practices, and engage in real-time learning. Additionally, at Goodwill, as with other nonprofits, staying within budget is always a major concern. A solution that provided the most powerful and flexible technologies at the lowest cost was a high priority. <br><br><b>Resolution</b> <br><br>When Goodwill first selected Liferay, their IT team had many reservations about going with an open source product. As progress continued, Goodwill\'s fears quickly dissipated. Bergman said, \"First and foremost is an accelerated return on investment, which allows us to make additional site features possible sooner. Liferay Portal is easily adaptable to Goodwill\'s data center architecture and the diverse combination of technologies that we employ. Finally, it offers exceptional robustness and scalability. Liferay Portal was by far the most cost effective and efficient portal technology we considered.\" Liferay implemented the following:\n<ul>\n<li>Collaborative chat and message board capabilities</li>\n<li>Distribution of knowledge via Blogs</li>\n<li>\"Wiki\" pages that were regularly updated by visitors</li>\n<li>Interface to Computer-Based Training modules</li>\n<li>Document repository</li></ul>Liferay easily exceeded all of Goodwill\'s expectations. Customizations that Bergman expected to be done in six to eight weeks were done over one weekend. Not only did it provide all the desired functionality, it also provided an unexpected, long-term cost savings. All fears were relieved when the Liferay implementations were completed.</div>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-1\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-thumbnail-1.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-1\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-full-1.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-2\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-thumbnail-2.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-2\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-full-2.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-3\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-thumbnail-3.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-3\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-full-3.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-4\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-thumbnail-4.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-4\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-full-4.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-5\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-thumbnail-5.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-5\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-full-5.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-6\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-thumbnail-6.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-full-6\" type=\"text\">\n    <dynamic-content><![CDATA[content.liferay.com/4.0.0/images/COMPANY-CASE-STUDIES-GOODWILL.screenshot-full-6.jpg]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"screenshot-thumbnail-7\" type=\"text\">\n    <dynamic-content><![CDATA[]]></d