insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'ABOUTUS', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us', '<DIV><I><B>ENTERPRISE.</B> Open Source. For Life.</I><BR>&nbsp;<BR><SPAN style=\"FONT-SIZE: 9pt\"><FONT size=3>Liferay is the industry leader for open source solutions.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Companies of all sizes and industries are turning to open source solutions for reliable and cost-effective measures to increase performance, reliability, and flexibility.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Liferay stands at the forefront of <?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /><st1:City w:st=\"on\"><st1:place w:st=\"on\">OSS</st1:place></st1:City> technology and is a trusted source for companies worldwide.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Our unique and cost-effective solutions have garnered us the attention of the industry as being a cutting-edge company that enterprises can depend on. We are committed to providing innovative, user-friendly products and highly responsive support services. Liferay Portal has been carefully designed with high scalability and robust performance to handle the most demanding applications for companies across all industries. Adherence to J2EE standards means Liferay Portal can be deployed in many contexts without restricting its usage to any specific operating system, application server, or database. <?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN>\n<P class=MsoNormal style=\"MARGIN: 0in 0in 0pt\"><SPAN style=\"FONT-SIZE: 9pt\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\n<P class=MsoNormal style=\"MARGIN: 0in 0in 0pt\"><SPAN style=\"FONT-SIZE: 9pt\"><FONT size=3>To maximize usage of Liferay products, we offer a variety of technical support, custom development, and training services. Our professional service team is backed by comprehensive Liferay product knowledge and years of consulting experience with Fortune 500 companies.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Companies turn to Liferay for unparalleled support. <o:p></o:p></FONT></SPAN></P><BR><I>Enterprise. <B>OPEN SOURCE.</B> For Life.</I><BR>&nbsp;<BR>\n<P class=MsoNormal style=\"MARGIN: 0in 0in 0pt\"><SPAN style=\"FONT-SIZE: 9pt\"><FONT size=3>Liferay is committed to innovation, reliability, and community. We are a safe technology investment for our clients because our open source products lead to wider adoption by the technology community, the development of an expert user base, and acceptance as a standard portal platform. Our MIT license is business friendly and allows users to use, modify, or distribute Liferay with no licensing fees.<o:p></o:p></FONT></SPAN></P>\n<P class=MsoNormal style=\"MARGIN: 0in 0in 0pt\"><SPAN style=\"FONT-SIZE: 9pt\"><FONT size=3>&nbsp;<o:p></o:p></FONT></SPAN></P>\n<P class=MsoNormal style=\"MARGIN: 0in 0in 0pt\"><SPAN style=\"FONT-SIZE: 9pt\"><FONT size=3>Liferay\'s open source products are extremely reliable as users in mission critical applications report enhancements and upgrades directly back to us. <o:p></o:p></FONT></SPAN></P>\n<P class=MsoNormal style=\"MARGIN: 0in 0in 0pt\"><SPAN style=\"FONT-SIZE: 9pt\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\n<P class=MsoNormal style=\"MARGIN: 0in 0in 0pt\"><SPAN style=\"FONT-SIZE: 9pt\"><FONT size=3>Liferay\'s commitment to open source solutions reflects our philosophy of helping improve the world community. The wide use of Liferay as a free portal solution by various not-for-profit organizations is a testament to our success in this endeavor.<o:p></o:p></FONT></SPAN></P>&nbsp;<BR><I>Enterprise. Open Source. <B>FOR LIFE.</B></I><BR>&nbsp;\n<P class=MsoNormal style=\"MARGIN: 0in 0in 0pt\"><SPAN style=\"FONT-SIZE: 9pt\"><FONT size=3>Liferay is committed to making a difference in the world. Each month Liferay donates a percentage of its profits to various nonprofit organizations. In the past we have made financial contributions toward AIDS relief and the <st1:country-region w:st=\"on\"><st1:place w:st=\"on\">Sudan</st1:place></st1:country-region> refugee crisis through well-respected organizations such as Samaritan\'s Purse and World Vision. The desire to help those in need is the heart of our company, and ultimately the reason why we exist.<o:p></o:p></FONT></SPAN></P><BR> \n<DIV></DIV></DIV>', 'general', '', '', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'ABOUTUS-CONTACTUS', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us - Contact Us', '<b>Email</b><ul><li>Sales Inquiries: <a href=\"mailto:sales@liferay.com\">sales@liferay.com</a></li><li>General Inquiries: <a href=\"mailto:staff@liferay.com\">staff@liferay.com</a></li><li>Support: <a href=\"mailto:support@liferay.com\">support@liferay.com</a></li></ul><br>Liferay is also supported by a large community. To join our community:<ul><li>Visit <a href=\"http://forums.liferay.com\">forums.liferay.com</a></li><li><a href=\"http://lists.sourceforge.net/lists/listinfo/lportal-development\">Subscribe</a> or <a href=\"http://sourceforge.net/mailarchive/forum.php?forum=lportal-development\">browse</a>&nbsp;our mailing list</li></ul>', 'general', '', '', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'ABOUTUS-LEADERSHIP', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us - Leadership', '<DIV>Liferay was formed by four partners from diverse technological and professional backgrounds. Our seasoned team endeavors to pioneer innovative enterprise solutions for companies worldwide.<BR><BR>\n<TABLE cellSpacing=0 cellPadding=5 border=0>\n<TBODY>\n<TR>\n<TD style=\"COLOR: rgb(255,255,255); BACKGROUND-COLOR: rgb(0,31,64)\"><B>Brian Chan</B></TD></TR>\n<TR>\n<TD style=\"COLOR: rgb(0,31,64); BACKGROUND-COLOR: rgb(255,255,255)\">Brian Chan, CEO and company founder, initially formed Liferay in 2000 to provide nonprofit organizations with an open source solution to help them collaborate on the Internet.&nbsp; He has since guided Liferay to become a leading industry-wide provider of open source enterprise solutions. With a strong foundation in economics and software architecture, Brian has solidified open source as a low-cost, high performance solution for companies. Brian holds a B.S. in Economics and Computer Science from University of Chicago.</TD></TR></TBODY></TABLE><BR>\n<TABLE cellSpacing=0 cellPadding=5 border=0>\n<TBODY>\n<TR>\n<TD style=\"COLOR: rgb(255,255,255); BACKGROUND-COLOR: rgb(0,31,64)\"><B>Bryan Cheung</B></TD></TR>\n<TR>\n<TD style=\"COLOR: rgb(0,31,64); BACKGROUND-COLOR: rgb(255,255,255)\">Bryan Cheung comes to Liferay with extensive consulting experience. His experience in portal software includes the advertising campaign management portal, one of the first commercial uses of Liferay. He has also worked on custom Epicentric portlet development for Genentech, and has done general consulting for Universal Music Group, Sony Consumer Electronics, Accenture Consulting, and Gateway Computers. Bryan holds a B.S. from University of California, Berkeley.</TD></TR></TBODY></TABLE><BR>\n<TABLE cellSpacing=0 cellPadding=5 border=0>\n<TBODY>\n<TR>\n<TD style=\"COLOR: rgb(255,255,255); BACKGROUND-COLOR: rgb(0,31,64)\"><B>Brian Kim</B></TD></TR>\n<TR>\n<TD style=\"COLOR: rgb(0,31,64); BACKGROUND-COLOR: rgb(255,255,255)\">Brian Kim is an experienced technology consultant. At HubbardOne, Brian worked alongside upper management, including the CEO and CFO, to lead several core internal projects optimizing the company\'s infrastructure. Previously, he served as a technical team lead at Accenture where he headed projects for many Fortune 500 companies including Bank of America and Wachovia Securities.&nbsp; Brian holds a B.S. and a M.S. in Computer Science from University of Chicago.</TD></TR></TBODY></TABLE><BR>\n<TABLE cellSpacing=0 cellPadding=5 border=0>\n<TBODY>\n<TR>\n<TD style=\"COLOR: rgb(255,255,255); BACKGROUND-COLOR: rgb(0,31,64)\"><B>Michael Young</B></TD></TR>\n<TR>\n<TD style=\"COLOR: rgb(0,31,64); BACKGROUND-COLOR: rgb(255,255,255)\">Michael Young brings over 10 years of enterprise computing experience to Liferay. Michael has held positions at Sony Electronics, Syntricity, and the United States Navy. He led the overhaul of Syntricity\'s dataConductor EP data management and reporting software platform, paving Syntricity\'s way to profitability. As a contractor for the US Navy, Michael developed solutions that improved collaboration between targeting and image systems. Michael holds a B.S. in Computer Science from University of California, San Diego.</TD></TR></TBODY></TABLE></DIV>', 'general', '', '', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'ABOUTUS-NEWS', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us - News', '<?xml version=\"1.0\"?>\n\n<root> \n  <dynamic-element name=\"title\" type=\"text\"> \n    <dynamic-content><![CDATA[News Highlights]]></dynamic-content> \n  </dynamic-element> \n</root>', 'general', 'ABOUTUS-NEWS', 'ABOUTUS-NEWS', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'ABOUTUS-NEWS-ANOUNCEMENTS', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us - News - Anouncements', '<DIV>\n<TABLE cellSpacing=15 cellPadding=0 boder=\"0\">\n<TBODY>\n<TR>\n<TD vAlign=top><B>04.03.2006</B></TD>\n<TD>The much anticipated Liferay Portal 4.0.0 RC1 is out!!</TD></TR>\n<TR>\n<TD vAlign=top><B>03.01.2006</B></TD>\n<TD width=\"90%\"><I>The Lion, The Witch, and The Wardrobe</I> film maker, <A href=\"/web/guest/stories/walden_media\">Walden Media</A>, uses Liferay Portal for website and community collaboration. See <A href=\"http://www.prnewswire.com/cgi-bin/stories.pl?ACCT=104&amp;STORY=/www/story/03-01-2006/0004311362\"><A href=\"http://www.prnewswire.com/cgi-bin/stories.pl?ACCT=104&amp;STORY=/www/story/03-01-2006/0004311362&amp;Edatetime=\">press release</A> or <A href=\"http://software.newsforge.com/software/06/02/27/1622243.shtml?tid=132\">article</A>.</A></TD></TR>\n<TR>\n<TD vAlign=top><B>11.08.2005</B></TD>\n<TD><A href=\"/web/guest/stories/goodwill\">Goodwill.org</A> launches its new website on top of Liferay Portal. See <A href=\"http://www.prnewswire.com/cgi-bin/stories.pl?ACCT=104&amp;STORY=/www/story/11-08-2005/0004211036\">press release</A>.</TD></TR>\n<TR>\n<TD vAlign=top><B>08.26.2005</B></TD>\n<TD>Liferay Portal 3.6.1 is released. See <A href=\"http://www.prnewswire.com/cgi-bin/stories.pl?ACCT=104&amp;amp;amp;amp;amp;amp;amp;STORY=/www/story/09-13-2005/0004106422\">press release</A>.</TD></TR>\n<TR>\n<TD vAlign=top><B>03.15.2005</B></TD>\n<TD>Brian Chan presents Liferay at the <A href=\"http://www.sdjug.com/\">San Diego Java Users Group</A>.</TD></TR>\n<TR>\n<TD vAlign=top><B>03.04.2005</B></TD>\n<TD>Brian Chan presents Liferay at the <A href=\"http://symposiumwiki.theserverside.com/tsswiki/display/symposium2005/Meetings+and+BOFS\">TheServerSide Java Symposium</A> in Las Vegas.</TD></TR>\n<TR>\n<TD vAlign=top><B>02.05.2005</B></TD>\n<TD>Brian Chan presents Liferay at the <A href=\"https://hkjug.dev.java.net/servlets/NewsItemView?newsItemID=1808\">Hong Kong Java Users Group</A>.</TD></TR>\n<TR>\n<TD vAlign=top><B>12.14.2004</B></TD>\n<TD>Liferay Portal 3.1.0 is 100% WSRP <A href=\"http://www.oasis-open.org/committees/tc_home.php?wg_abbrev=wsrp\">compliant</A>.</TD></TR>\n<TR>\n<TD vAlign=top><B>03.31.2004</B></TD>\n<TD>Liferay Portal Enterprise 2.1.0 is 100% JSR- 168 compliant.</TD></TR></TBODY></TABLE></DIV>', 'general', '', '', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
