 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'ABOUTUS-NEWS-IN-THE-NEWS', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'About Us - News - In The News', '<DIV>\n<TABLE cellSpacing=15 cellPadding=0 boder=\"0\">\n<TBODY>\n<TR>\n<TD vAlign=top><B>\n<P><STRONG>04.05.2006</STRONG></P></B></TD>\n<TD><A href=\"http://www.networkworld.com/news/2006/041006-goodwill-open-source-liferay.html\">Open Source Portal Software Controls Security</A><BR>Ann Bednarz of Network World introduces version 4.0 and tells the story of Goodwill\'s Liferay implementation</TD></TR>\n<TR>\n<TD vAlign=top><B>\n<P><STRONG>04.05.2006</STRONG></P></B></TD>\n<TD><A href=\"http://www.line56.com/articles/default.asp?articleID=7492&amp;TopicID=5\">A Free Portal</A><A href=\"http://www.line56.com/articles/default.asp?articleID=7045&amp;TopicID=7\"><BR><FONT color=#000000>Demir Barlas recommends Liferay to the line56 community</FONT></A></TD></TR>\n<TR>\n<TD vAlign=top><B>\n<P><B>02.28.2006</B></P></B></TD>\n<TD>\n<P><A href=\"http://software.newsforge.com/software/06/02/27/1622243.shtml?tid=132\">The Lion, the Witch, and Linux</A><BR>newsforge.com</P></TD></TR>\n<TR>\n<TD vAlign=top><B>11.01.2005</B></TD>\n<TD><A href=\"http://www.line56.com/articles/default.asp?articleID=7045&amp;TopicID=7\">Evaluating Open Source Enterprise Portal Software</A><BR>line56.com</TD></TR>\n<TR>\n<TD vAlign=top><B>10.31.2005</B></TD>\n<TD><A href=\"http://www.webbuyersguide.com/sub/enterpriseapps/reviews/2935-wbgenterpriseapps_reviews.html\">Enterprise Applications Buyer\'s Guide</A><BR>webbuyersguide.com</TD></TR>\n<TR>\n<TD vAlign=top><B>10.31.2005</B></TD>\n<TD><A href=\"http://www.eweek.com/article2/0,1759,1879782,00.asp\">Liferay Brings Desktop Capabilities to the Web</A><BR>eweek.com</TD></TR>\n<TR>\n<TD vAlign=top><B>10.31.2005</B></TD>\n<TD><A href=\"http://www.ddj.com/documents/s=9893/ddj0510e/0510e.html\">Developing JSR-168 Portlets</A><BR>ddj.com</TD></TR>\n<TR>\n<TD vAlign=top><B>10.28.2005</B></TD>\n<TD><A href=\"http://software.newsforge.com/software/05/10/12/153238.shtml?tid=74\">Liferay Portal Provides Lifeline for Corporate Housing Firm</A><BR>newsforge.com</TD></TR>\n<TR>\n<TD vAlign=top><B>10.20.2005</B></TD>\n<TD><A href=\"http://searchwin2000.techtarget.com/originalContent/0,289142,sid39_gci1135884,00.html\">Open source portal lets Goodwill CIO help those in need</A><BR>techtarget.com</TD></TR>\n<TR>\n<TD vAlign=top><B>10.20.2005</B></TD>\n<TD><A href=\"http://www.computerwoche.de/index.cfm?pid=381&amp;pk=567617\">Portale: Open Source im Anmarsch</A><BR>(See English translation: <A href=\"http://translate.google.com/translate?u=http%3A%2F%2Fwww.computerwoche.de%2Findex.cfm%3Fpid%3D381%26pk%3D567617&amp;langpair=de%7Cen&amp;hl=en&amp;ie=UTF-8&amp;oe=UTF-8&amp;prev=%2Flanguage_tools\">Portals: Open Source in the advance</A>)<BR>computerwoche.de</TD></TR>\n<TR>\n<TD vAlign=top><B>10.18.2005</B></TD>\n<TD><A href=\"http://www.it-analysis.com/article.php?articleid=12945\">Liferay after Plumtree</A><BR>it-analysis.com</TD></TR>\n<TR>\n<TD vAlign=top><B>10.18.2005</B></TD>\n<TD><A href=\"http://www.networkworld.com/newsletters/datacenter/2005/1017datacenter1.html\">Open source: corporate playground?</A><BR>networkworld.com</TD></TR>\n<TR>\n<TD vAlign=top><B>08.08.2005</B></TD>\n<TD>Liferay is featured in <A href=\"http://www.infoworld.com/article/05/08/08/32FEossportal_1.html\">InfoWorld</A> as a leader in open source portals.</TD></TR>\n<TR>\n<TD vAlign=top><B>07.15.2005</B></TD>\n<TD><A href=\"http://www.nwc.com/showArticle.jhtml?articleID=165701617\">NWC</A> highlights Liferay Portal and its role in helping a Connecticut healthcare and social services nonprofit.</TD></TR>\n<TR>\n<TD vAlign=top><B>12.06.2004</B></TD>\n<TD>Liferay helps