insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'GLOBAL-TEAM-STRATOS', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'GLOBAL-TEAM-STRATOS', '<?xml version=\"1.0\"?>\n\n<root> \n  <dynamic-element name=\"image\" type=\"image\"> \n    <dynamic-content id=\"liferay.com.journal.article.GLOBAL-TEAM-STRATOS.image.1.0\">/image/journal/article?img_id=GLOBAL-TEAM-STRATOS.image&amp;version=1.0</dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"textarea\" type=\"text_area\"> \n    <dynamic-content><![CDATA[<B>STRATOS</B>&nbsp;-&nbsp;<A href=\"http://www.stratos-tms.de/\">www.stratos-tms.de</A><BR><BR>Founded in 1990 STRATOS Software GmbH is the Premium Liferay Partner for German speaking countries.<BR><BR>Stratos combines the knowledge of a software-development company with the efficiency and competence of an open source specialist. Stratos always keeps&nbsp;the demands of the market in sight and never misses global business processes. True to this axiom, STRATOS delivers efficient, custom tailored portal solutions that&nbsp;provides an excellent cost-value ratio.<BR><BR>Being a Premium Liferay Partner, Stratos offers 24/7 Support and Consultancy services in German and other European languages.<BR>]]></dynamic-content> \n  </dynamic-element> \n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-IMAGETEXT', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
