insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'HOME-ANNOUNCEMENTS', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Home Announcements', '<?xml version=\"1.0\"?>\n\n<root> \n  <dynamic-element name=\"item-1\" type=\"text_box\"> \n    <dynamic-content><![CDATA[Kito Mann to present JSF on top of Liferay Portal at TheServerSide Symposium.]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-url-1\" type=\"text\"> \n    <dynamic-content><![CDATA[@friendly_url_public@/guest/downloads]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-2\" type=\"text_box\"> \n    <dynamic-content><![CDATA[Walden Media, the company that produced the movie <i>The Lion, The Witch, and the Wardrobe</i>, chose Liferay for their website.]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-url-2\" type=\"text\"> \n    <dynamic-content><![CDATA[http://java.sun.com/javaone/sf/]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-3\" type=\"text_box\"> \n    <dynamic-content><![CDATA[Liferay is represented at this years CeBIT through the Premium Partner Stratos Software GmbH. Visit Liferay on CeBIT at Hall 5 Booth# D58/9. ]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-url-3\" type=\"text\"> \n    <dynamic-content><![CDATA[http://software.newsforge.com/software/06/02/27/1622243.shtml?tid=132]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-4\" type=\"text_box\"> \n    <dynamic-content><![CDATA[Liferay reviewed in eWeek. See why you should be using Liferay Portal for your organization.]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-url-4\" type=\"text\"> \n    <dynamic-content><![CDATA[http://sourceforge.net/projects/lportal]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-5\" type=\"text_box\"> \n    <dynamic-content><![CDATA[]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-url-5\" type=\"text\"> \n    <dynamic-content><![CDATA[http://searchopensource.techtarget.com/originalContent/0,289142,sid39_gci1176366,00.html]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-text-1\" type=\"text\"> \n    <dynamic-content><![CDATA[<b><font color=\"red\">Liferay Portal 4.0.0 RC1 released!</font></b>]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-text-2\" type=\"text\"> \n    <dynamic-content><![CDATA[2006 Java One Conference May&nbsp;16-19 San Francisco. Meet the Liferay development team at Booth 935]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-text-3\" type=\"text\"> \n    <dynamic-content><![CDATA[Walden Media, the film production company behind <I>The Lion, The Witch, and the Wardrobe</I>, uses Liferay Portal for Walden.com.]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-text-4\" type=\"text\"> \n    <dynamic-content><![CDATA[Liferay Portal source moved to SVN on SourceForge.net]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"item-text-5\" type=\"text\"> \n    <dynamic-content><![CDATA[Microsoft\'s high prices drive FSW to Linux, open source]]></dynamic-content> \n  </dynamic-element> \n</root>', 'general', 'BASIC-ITEMS-LIST', 'BASIC-ITEMS-LIST', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
