insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'HOME-DEMO-IMAGE', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Home Demo Image', '<?xml version=\"1.0\"?>\n\n<root> \n  <dynamic-element name=\"image\" type=\"image\"> \n    <dynamic-content id=\"liferay.com.journal.article.HOME-DEMO-IMAGE.image.1.0\">/image/journal/article?img_id=HOME-DEMO-IMAGE.image&amp;version=1.0</dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"textarea\" type=\"text_area\"> \n    <dynamic-content><![CDATA[<DIV>\n<DIV>\n<DIV>\n<DIV><STRONG>Liferay </STRONG>is the world\'s leading open source portal&nbsp;platform. Now in its sixth year of development, Liferay Portal is backed by a comprehensive professional services network <A href=\"@friendly_url@/guest/services\">offering</A> custom development, training, and support across <A href=\"@friendly_url@/guest/team\">four continents</A>. </DIV>\n<DIV>&nbsp;</DIV>\n<DIV><STRONG><A href=\"http://demo.liferay.net/\"></A></STRONG></DIV>\n<DIV><STRONG>Preview Liferay Now</STRONG></DIV></DIV>\n<DIV><A href=\"http://demo.liferay.net/\">Explore the demo</A> to see how Liferay can bring value and innovation to your organization, or contact our <A href=\"mailto:sales@liferay.com\">sales team</A>&nbsp;today.</DIV></DIV></DIV>]]></dynamic-content> \n  </dynamic-element> \n</root>', 'general', 'BASIC-IMAGE-TEXT', 'BASIC-TEXT-IMAGE', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
