insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'SERVICES-PUBLICTRAINING', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Services - Public Training', '<DIV>\n<DIV class=font-small style=\"PADDING-RIGHT: 10px; PADDING-LEFT: 10px; FLOAT: right; PADDING-BOTTOM: 10px; MARGIN: 10px; WIDTH: 200px; COLOR: rgb(0,31,64); PADDING-TOP: 10px; BACKGROUND-COLOR: rgb(255,255,255)\">\n<DIV>\n<DIV><STRONG>Training Schedule</STRONG></DIV>\n<DIV><A style=\"TEXT-DECORATION: underline\" href=\"/web/guest/services/public_training/course_topics\">Course Topics</A></DIV><BR>\n<DIV><SPAN style=\"FONT-WEIGHT: bold\">Training Time</SPAN><BR>June 5-7, 2006<BR>9am-5pm daily<BR><BR><SPAN style=\"FONT-WEIGHT: bold\">Location</SPAN><BR><A style=\"TEXT-DECORATION: underline\" href=\"http://maps.google.com/maps?q=6161+West+Centinela+Avenue,+Culver+City+CA+90230-6306&amp;ll=33.983403,-118.397298&amp;spn=0.015145,0.041952&amp;iwloc=A&amp;hl=en\">Radisson Hotel Los Angeles Westside</A></DIV>\n<DIV>6161 West Centinela Avenue</DIV>\n<DIV>Culver City CA 90230-6306</DIV>\n<DIV><BR><SPAN style=\"FONT-WEIGHT: bold\">Additional Information</SPAN><BR>Please note that a laptop is required for this course.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV>\n<DIV><STRONG>How to Register</STRONG></DIV>\n<DIV>Please click <A style=\"TEXT-DECORATION: underline\" href=\"/web/guest/services/public_training/registration\">here</A> to register.</DIV></DIV></DIV></DIV>\n<DIV>\n<DIV>\n<DIV><SPAN style=\"FONT-WEIGHT: bold\">Overview</SPAN><BR><BR>Liferay will host a public training seminar on&nbsp;June 5-7,&nbsp;2006 in Los Angeles, CA.&nbsp; The seminar will be comprehensive, covering architecture, installation, portlet development, themes, and CMS.<BR><BR>This course is designed to help you come up to speed on the functionality and development of Liferay Portal.&nbsp; It will help you to increase your productivity as well as introduce you to valuable new features, such as the <SPAN style=\"FONT-STYLE: italic\">runtime-portlet</SPAN> tag and hot-deployable themes.&nbsp; We will also&nbsp;teach you how to migrate your existing Struts applications to a portlet environment.<BR></DIV>\n<DIV><SPAN style=\"FONT-WEIGHT: bold\"></SPAN>&nbsp;</DIV>\n<DIV><SPAN style=\"FONT-WEIGHT: bold\">NEW 4.0.0 Release</SPAN> </DIV>\n<DIV>&nbsp;</DIV>\n<DIV>Come and learn about&nbsp;the&nbsp;new features Liferay Portal 4.0.0 has to offer!</DIV>\n<DIV><BR><SPAN style=\"FONT-WEIGHT: bold\">Hands-On Training</SPAN><BR><BR>We will take you through the entire process of portlet development and teach you industry best practices.&nbsp; You will learn how to code the action class, auto-generate business components, implement server-side validation, and perform database operations.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><STRONG>Subject Matter Experts</STRONG></DIV>\n<DIV>&nbsp;</DIV>\n<DIV>The course will be led by the Liferay experts who designed the software.</DIV>\n<DIV><BR><SPAN style=\"FONT-WEIGHT: bold\">Development Environment</SPAN><BR><BR>For development we will setup Tomcat on Eclipse and use MySQL for the database.&nbsp; Although Liferay can be used with any application server and database, the above setup is the fastest in terms of development.&nbsp; We may also touch upon installation with other application servers and databases as well.<BR><BR><SPAN style=\"FONT-WEIGHT: bold\">Prerequisites</SPAN><BR><BR>Basic knowledge of Java and Servlets is required.&nbsp; The course will be intensive, but we will make sure to answer any questions you have to ensure that you have a solid grasp of the technology by the end of the course.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><STRONG>Please note that a laptop is required for this course.</STRONG></DIV>\n<DIV>&nbsp;</DIV>\n<DIV>Minimum System Requirements: 1.4 GHz 512MB RAM \n<DIV>\n<DIV>Recommended System Requirements: 2.0 GHz 1GB RAM</DIV>\n<DIV>Wireless Card Required</DIV></DIV></DIV></DIV></DIV></DIV>', 'general', '', '', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'SERVICES-PUBLICTRAINING-COURSETOPICS', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Services - Public Training - Course Topics', '<DIV>\n<DIV style=\"PADDING-RIGHT: 5px; PADDING-LEFT: 7px; FONT-WEIGHT: bold; PADDING-BOTTOM: 5px; COLOR: rgb(255,255,255); MARGIN-RIGHT: 5px; PADDING-TOP: 5px; BACKGROUND-COLOR: rgb(0,31,64)\">Day 1</DIV>\n<DIV style=\"PADDING-RIGHT: 5px; PADDING-LEFT: 5px; PADDING-BOTTOM: 5px; COLOR: rgb(0,31,64); MARGIN-RIGHT: 5px; PADDING-TOP: 5px; BACKGROUND-COLOR: rgb(255,255,255)\">\n<UL>\n<LI>Introduction to Liferay</LI>\n<LI>Installation \n<UL>\n<LI>App Server: Tomcat </LI>\n<LI>Database: MySQL </LI>\n<LI>IDE: Eclipse </LI>\n<LI>Tools: Ant, Jikes</LI></UL></LI>\n<LI>Overview of JSR-168</LI>\n<LI>Creating&nbsp;Portlets</LI>\n<UL>\n<LI>JSP Portlets</LI>\n<LI>Struts &amp; Tiles Portlets</LI>\n<LI>JSF Portlets</LI></UL></UL></DIV><BR>\n<DIV style=\"PADDING-RIGHT: 5px; PADDING-LEFT: 7px; FONT-WEIGHT: bold; PADDING-BOTTOM: 5px; COLOR: rgb(255,255,255); MARGIN-RIGHT: 5px; PADDING-TOP: 5px; BACKGROUND-COLOR: rgb(0,31,64)\">Day 2</DIV>\n<DIV style=\"PADDING-RIGHT: 5px; PADDING-LEFT: 5px; PADDING-BOTTOM: 5px; COLOR: rgb(0,31,64); MARGIN-RIGHT: 5px; PADDING-TOP: 5px; BACKGROUND-COLOR: rgb(255,255,255)\">\n<UL>\n<LI>Overview of Spring</LI>\n<LI>Modifying the Struts Portlet to Perform Database Operations \n<UL>\n<LI>Autogenerating EJBs Using the service.xml</LI>\n<LI>Adding Methods to the Impl Class</LI>\n<LI>Repopulating Your Database</LI>\n<LI>Modifying the Action Class</LI></UL></LI>\n<LI>Modifying the Struts Portlet to Implement Validation </LI>\n<LI>Migrating Struts Applications to a Portlet Environment</LI></UL></DIV><BR>\n<DIV style=\"PADDING-RIGHT: 5px; PADDING-LEFT: 7px; FONT-WEIGHT: bold; PADDING-BOTTOM: 5px; COLOR: rgb(255,255,255); MARGIN-RIGHT: 5px; PADDING-TOP: 5px; BACKGROUND-COLOR: rgb(0,31,64)\">Day 3</DIV>\n<DIV style=\"PADDING-RIGHT: 5px; PADDING-LEFT: 5px; PADDING-BOTTOM: 5px; COLOR: rgb(0,31,64); MARGIN-RIGHT: 5px; PADDING-TOP: 5px; BACKGROUND-COLOR: rgb(255,255,255)\">\n<UL>\n<LI>Overview of WSRP </LI>\n<LI>Using Web Services in Liferay </LI>\n<LI>CMS - How to Build a Website on Top of the Portal \n<UL>\n<LI>Using the Journal Content Portlet to Add an Article</LI>\n<LI>Adding Portlets to Your Website Using the the Portal</LI></UL></LI>\n<LI>Creating a Theme \n<UL>\n<LI>Configuring Properties</LI>\n<LI>Editing the Templates </LI>\n<LI>Editing the CSS </LI>\n<LI>Adding JavaScript Functions</LI></UL></LI></UL></DIV></DIV>', 'general', '', '', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'SERVICES-PUBLICTRAINING-REGISTRATION', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Services - Public Training - Registration', '<STRONG>How to Register</STRONG><BR><BR>Please contact us at <A href=\"mailto:sales@liferay.com\">sales@liferay.com</A> to register. Space is limited.<BR><BR><STRONG>Registration Cost</STRONG> <BR><BR>\n<TABLE cellSpacing=0 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD>Registration before 5/26/2006:</TD>\n<TD>&nbsp;</TD>\n<TD align=right>$1920 </TD></TR>\n<TR>\n<TD>Registration after 5/26/2006:</TD>\n<TD>&nbsp;</TD>\n<TD align=right>$2400</TD></TR></TBODY></TABLE>', 'general', '', '', CURRENT_TIMESTAMP, TRUE, 'liferay.com.1', 'Joe Bloggs', FALSE);
insert into JournalArticle (companyId, articleId, version, groupId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired) values ('liferay.com', 'STORIES', 1.0, '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Stories', '<?xml version=\"1.0\"?>\n\n<root> \n  <dynamic-element name=\"title\" type=\"text\"> \n    <dynamic-content><![CDATA[Stories]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"subtitle\" type=\"text\"> \n    <dynamic-content><![CDATA[]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"main-text\" type=\"text_area\"> \n    <dynamic-content><![CDATA[<div>\n<p style=\"margin-bottom: 0in;\">Every day we at Liferay are asked about how&nbsp;we can get involved in various portal projects around the world. These businesses range from mom and pop stores to Fortune 500 firms. Their challenges are vast but the solution often ends up being the same: Liferay Portal. Take a glance at the stories of some of these organizations.&nbsp; You will be surprised by the success that so many have had with Liferay Portal.</p><br>If you would like to contribute your own story, contact us at <a href=\"mailto:staff@liferay.com\">staff@liferay.com</a>.<br></div>]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"feature-logo-1\" type=\"text\"> \n    <dynamic-content><![CDATA[@portal_url@/image/journal/article?img_id=STORIES-EDUCAMADRID.logo-image&version=1.0]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"feature-link-1\" type=\"text\"> \n    <dynamic-content><![CDATA[@friendly_url_public@/guest/stories/educamadrid]]></dynamic-content> \n  </dynamic-element>  \n  <dynamic-element name=\"feature-logo-2\" type=\"text\"> \n    <dynamic-content><![CDATA[@portal_url@/image/journal/article?img_id=STORIES-GOODWILL.logo-image&version=1.0]]></dynamic-content> \n  </