insert into JournalStructure (companyId, structureId, groupId, userId, userName, createDate, modifiedDate, name, description, xsd) values ('liferay.com', 'SAMPLE-STRUCTURE', '2', 'liferay.com.1', 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'Samle Structure', 'Sample structure showing the different element types.', '<?xml version=\"1.0\"?>\n\n<root> \n  <dynamic-element name=\"sample-text\" type=\"text\"/>  \n  <dynamic-element name=\"sample-text-box\" type=\"text_box\"/>  \n  <dynamic-element name=\"sample-text-area\" type=\"text_area\"/>  \n  <dynamic-element name=\"sample-image\" type=\"image\"/>  \n  <dynamic-element name=\"sample-boolean-flag\" type=\"boolean\"/>  \n  <dynamic-element name=\"sample-selection-list\" type=\"list\"> \n    <dynamic-element name=\"coke\" type=\"Coke\"></dynamic-element>  \n    <dynamic-element name=\"pepsi\" type=\"Pepsi\"/> \n  </dynamic-element>  \n  <dynamic-element name=\"sample-multi-selection-list\" type=\"multi-list\"> \n    <dynamic-element name=\"chocolate\" type=\"Chocolate\"></dynamic-element>  \n    <dynamic-element name=\"strawberry\" type=\"Strawberry\"></dynamic-element>  \n    <dynamic-element name=\"vanilla\" type=\"Vanilla\"/> \n  </dynamic-element> \n</root>');
