insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3437, 3438, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3229', 1.0, 'PR-Triton_Partnership', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;p align=\"center\"&gt;&lt;strong&gt;Liferay Announces Partnership with Triton Services&lt;/strong&gt;&lt;/p&gt; &lt;p&gt;LOS ANGELES, CA &amp;ndash;&amp;ndash; Liferay, provider of the world&amp;rsquo;s leading enterprise-class, open source portal and content management system announced a new partnership with Triton Services, Inc. As a Liferay Preferred Partner, Triton will offer Liferay consulting services specific to the unique operational needs of US Government clients such as the Department of Defense (DoD), Intelligence Communities and other Federal, State and local agencies and offices.&lt;/p&gt; &lt;p&gt;Triton\'s expertise and experience in the public sector is an answer to Liferay\'s increasing opportunities with government organizations,&amp;quot; said Bryan Cheung, Liferay\'s Chief Exectutive Officer. &amp;ldquo;Triton was already active in our community and is best equipped to deliver Liferay technology to its sector of expertise. For instance, the Triton team is versed in NetCentric Warfare and requirements, processes and protocols of Military clients working within unclassified and classified network environments.&lt;/p&gt; &lt;p&gt;In addition to providing services around Liferay Portal, Triton has agreed to contribute all of its research and development around JavaServer Faces (JSF) technology to Liferay\'s community, including portlets for accounting, time keeping, human resources, and recruiting. The company has taken a leadership role in the open source realm with regard to testing and debugging ICEfaces, an AJAX-enabled component suite based on JSF technology, within Liferay. By utilizing Liferay and ICEfaces, Triton is building highly responsive rich internet portlets based on AJAX technology.&lt;/p&gt; &lt;p&gt;Liferay Portal was already a core part of our ongoing efforts around JSF prior to this partnership,&amp;rdquo; said Triton Chairman, Ray Kwong. &amp;quot;Triton\'s goal from the beginning was to fortify Liferay Portal with tools that provide value to government offices trying to ease their IT infrastructure burden. Becoming a Preferred Partner only underscores the cutting-edge work we\'ve done with Liferay, and it allows us to serve government clients with Liferay products and services.&lt;/p&gt; &lt;p&gt;For more about Liferay Portal, visit www.liferay.com. Additional information about Triton Services can be found at http://www.tritonsvc.com.&lt;/p&gt; &lt;p&gt;&lt;strong&gt;&lt;em&gt;About Liferay:&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt; &lt;p&gt;&lt;em&gt;Liferay Portal is the world\'s leading enterprise open source portal framework with Fortune 500 clients in the U.S. and Europe. Liferay Professional Services offers technical support, custom development and professional training to ensure successful deployment of its flagship product in the most demanding IT environments. Liferay clients include Discovery Education, Goodwill Industries, and the Christian Science Monitor. The company is headquartered in Los Angeles. For more information, please visit www.liferay.com.&lt;/em&gt;&lt;/p&gt; &lt;p&gt;&lt;strong&gt;&lt;em&gt;About Triton:&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt; &lt;p&gt;&lt;em&gt;Headquartered in Bowie, MD, Triton Services, Inc. is a leading engineering services and electron device manufacturing firm specializing in engineering and program management, full life cycle software development, logistics integration, network infrastructure design and support, and other related engineering services. Triton also manufactures a full range of traveling wave tubes, klystrons, power grid amplifiers, microwave power modules and large sunlight readable high definition displays systems for defense and commercial applications.&lt;/em&gt;&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070808213900, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
