insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3453, 3454, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2483', 1.0, 'About_us-Events-web2fordev', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[Web2forDev 2007]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header-link\" type=\"text\">\n		<dynamic-content><![CDATA[http://www.web2fordev.net/]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-header-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"date\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"type\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image\" type=\"image\">\n		<dynamic-content id=\"12675\">/image/journal/article?img_id=1921&amp;t=1207241495204</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-image-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"flow\" type=\"list\">\n		<dynamic-content><![CDATA[default]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p>September 25th-27th, 2007<br /> Rome, Italy</p><p>Web2ForDev 2007 is the first conference devoted to exploring the ways in which international development stakeholders can take advantage of the technical and organizational opportunities provided by Web 2.0 methods, approaches and applications.</p> <p>The Conference will look at how Web 2.0 can be used to the utmost advantage of Southern development actors to enable them to more effectively network, collaborate and exchange knowledge in the sectors of Agricultural and Rural Development and Natural Resource Management.</p><p>&nbsp;</p><p><a target=\"_blank\" href=\"http://www.liferay.com/web/bcheung\">Read Blog post...</a></p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-ARTICLE', 'BASIC-IMAGE-TEXT', SPECIFIC_TIMESTAMP_20070904194200, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
