insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3489, 3490, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3255', 1.0, 'Services-Training-Sydney_Course_Topics', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;p&gt;&lt;strong&gt;Sydney, Australia&lt;/strong&gt;&lt;/p&gt; &lt;div&gt; &lt;div class=\"stct-course-header\"&gt;Day 1&lt;/div&gt; &lt;div class=\"stct-course-content\"&gt; &lt;ul&gt; &lt;li&gt;Introduction to Liferay&lt;/li&gt; &lt;li&gt;Installation &lt;ul&gt; &lt;li&gt;Install the Liferay-Tomcat Bundle&lt;/li&gt; &lt;li&gt;Install Ant &amp;amp; Jikes&lt;/li&gt; &lt;li&gt;Build the EXT (Extension) Environment&lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;li&gt;Portlet Fundamentals &lt;ul&gt; &lt;li&gt;Overview of JSR 168 (Portlet Specification)&lt;/li&gt; &lt;li&gt;JSP Portlet&lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;li&gt;Portlet MVC (Model View Controller) &lt;ul&gt; &lt;li&gt;Overview of Struts&lt;/li&gt; &lt;li&gt;Struts Portlet&lt;/li&gt; &lt;li&gt;Overview of the Portlet Lifecycle&lt;/li&gt; &lt;li&gt;Add the Controller&lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;/ul&gt; &lt;/div&gt; &lt;div class=\"stct-course-header\"&gt;Day 2&lt;/div&gt; &lt;div class=\"stct-course-content\"&gt; &lt;ul&gt; &lt;li&gt;Configure Liferay to Use a MySQL Database&lt;/li&gt; &lt;li&gt;Install Eclipse &lt;ul&gt; &lt;li&gt;Leverage Eclipse to Speed Up Development&lt;/li&gt; &lt;li&gt;Debug Java Classes Using Eclipse&lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;li&gt;Create and Retrieve Records from the Database &lt;ul&gt; &lt;li&gt;Auto-generate the Service and Persistence Layer&lt;/li&gt; &lt;li&gt;Implement Business Logic&lt;/li&gt; &lt;li&gt;Validate Input&lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;li&gt;Overview of Permissions &lt;ul&gt; &lt;li&gt;Integrate Permissions into the Struts Portlet&lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;/ul&gt; &lt;/div&gt; &lt;div class=\"stct-course-header\"&gt;Day 3&lt;/div&gt; &lt;div class=\"stct-course-content\"&gt; &lt;ul&gt; &lt;li&gt;Modular Development &lt;ul&gt; &lt;li&gt;JSF Portlet WAR &lt;ul&gt; &lt;li&gt;JSF Overview&lt;/li&gt; &lt;li&gt;Backing Beans and UI Components&lt;/li&gt; &lt;li&gt;Validation and Messages&lt;/li&gt; &lt;li&gt;Navigation and Event Listeners&lt;/li&gt; &lt;li&gt;Accessing Liferay Services From a WAR&lt;/li&gt; &lt;li&gt;Message Bundles&lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;li&gt;Portal Administration&lt;/li&gt; &lt;li&gt;CMS - How to Build a Website on Top of the Portal &lt;ul&gt; &lt;li&gt;Using the Journal Content Portlet to Add an Article&lt;/li&gt; &lt;li&gt;Adding Portlets to Your Website Using the the Portal&lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;li&gt;Create a Theme &lt;ul&gt; &lt;li&gt;Configuring Properties&lt;/li&gt; &lt;li&gt;Editing the Templates&lt;/li&gt; &lt;li&gt;Editing the CSS&lt;/li&gt; &lt;li&gt;Adding JavaScript Functions&lt;/li&gt; &lt;/ul&gt; &lt;/li&gt; &lt;/ul&gt; &lt;/div&gt; &lt;/div&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070924151500, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3491, 3492, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3261', 1.0, 'PR-Terracotta_Partnership', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;div style=\"text-align: center;\"&gt;&lt;span style=\"font-weight: bold;\"&gt;LIFERAY AND TERRACOTTA PARTNER TO PROVIDE COST-EFFECTIVE HIGH AVAILABILITY FOR THE ENTERPRISE&lt;/span&gt;&lt;br /&gt;&lt;span style=\"font-style: italic;\"&gt;World&amp;rsquo;s Most Widely Used Portal Framework to Bundle Terracotta&amp;rsquo;s Leading Infrastructure Software&lt;/span&gt;&lt;/div&gt;&lt;p&gt;&lt;br /&gt;&amp;nbsp;&lt;br /&gt;&lt;span style=\"font-weight: bold;\"&gt;LOS ANGELES&lt;/span&gt; &amp;ndash; (October 1, 2007) &amp;ndash; Liferay, Inc., provider of the world&amp;rsquo;s leading enterprise-class open source portal, and Terracotta, a leader in infrastructure software for enterprise Java high availability and scalability, today announced a partnership to respond to growing global demand for the Liferay Portal feature set combined with the scalability and high availability provided by Terracotta. In addition to technical integration, Liferay will bundle Terracotta and serve as a reseller of support.&lt;br /&gt;&lt;br /&gt;Terracotta offers IT organizations a lightweight approach to scalability that lowers costs and simplifies deployment by reducing development effort and easing the load on application servers and databases.&amp;nbsp; Terracotta uses high-performance mapping of server memory changes, called Network-attached Memory, to share temporary &amp;ldquo;work-in-progress&amp;rdquo; data among servers. That makes an application highly available without placing such temporary data in an expensive relational database. It also provides dramatic cost savings and much higher performance and scalability than either databases or application-tier caches.&lt;br /&gt;&amp;nbsp;&lt;br /&gt;&amp;ldquo;Terracotta takes a smart approach to scalability that works well with Liferay Portal,&amp;rdquo; said Bryan Cheung, Liferay&amp;rsquo;s chief executive officer. &amp;ldquo;Our enterprise users will be pleased with how easily Terracotta enhances their most performance-critical Liferay deployments.&amp;rdquo;&lt;br /&gt;&lt;br /&gt;In its eighth year of development, the award-winning Liferay Portal is chosen by enterprises around the world for its enterprise feature set, flexible architecture, intuitive user interface, and built-in collaboration and content management capabilities. Liferay Portal boasts a large worldwide community with an average of 40,000 downloads per month.&lt;br /&gt;&lt;br /&gt;&amp;ldquo;We are choosing our partnerships very carefully,&amp;rdquo; said&amp;nbsp; Jeff Hartley, Terracotta&amp;rsquo;s vice president of product operations. &amp;ldquo;Liferay is the world&amp;rsquo;s most widely used portal framework,&amp;nbsp; and Terracotta is quickly growing in popularity as the Java scalability solution of choice. We look forward to further strengthening our relationship with Liferay as both communities continue their rapid growth.&amp;rdquo;&lt;br /&gt;&lt;br /&gt;Scalability is becoming a crucial consideration for portal frameworks because of the growing prominence of enterprise portals as a critical component of enterprise SOA initiatives, Liferay has earned industry recognition as a robust enterprise portal solution, and its integration with Terracotta further increases Liferay Portal&amp;rsquo;s value proposition to the enterprise market.&lt;br /&gt;&lt;br /&gt;For more information and an online demo, visit www.liferay.com.&lt;br /&gt;&lt;br /&gt;&lt;br style=\"font-weight: bold;\" /&gt;&lt;span style=\"font-weight: bold;\"&gt;About Liferay Inc.&lt;/span&gt;&lt;br /&gt;Liferay, Inc. is the provider of Liferay Portal, the world&amp;rsquo;s leading enterprise open source portal framework with Fortune 500 clients world-wide. Liferay Professional Services offers technical support, custom development and professional training to ensure successful deployment of its flagship product in the most demanding IT environments. The company is headquartered in Los Angeles.&amp;nbsp; For more information, please visit www.liferay.com&lt;br /&gt;&lt;br /&gt;&lt;span style=\"font-weight: bold;\"&gt;About Terracotta, Inc.&lt;/span&gt;&lt;br /&gt;Terracotta&amp;rsquo;s infrastructure software provides affordable and scalable high availability for Java applications. Companies use Terracotta to offload work from databases and application servers and to reduce their development efforts. Founded in 2003, Terracotta, Inc., is a private firm headquartered in San Francisco. More information is available at www.terracottatech.com. Terracotta&amp;rsquo;s open source community is available at www.terracotta.org.&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&lt;br /&gt;&lt;br /&gt;####&lt;br /&gt;&amp;nbsp;&lt;br /&gt;&lt;span style=\"font-weight: bold;\"&gt;PRESS CONTACTS:&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;br /&gt;Corey Gutwillig&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;br /&gt;S&amp;amp;S Public Relations, Inc.&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;br /&gt;847/955-0700 ext. 9333&lt;br /&gt;cgutwillig@sspr.com&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;br /&gt;&lt;br /&gt;Amy Burnis or John McElhenny, &lt;br /&gt;781-684-0770 &lt;br /&gt;Schwartz Communications&lt;br /&gt;terracotta@schwartz-pr.com&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20071001165400, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
