insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3508, 3509, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3272', 1.0, 'Downloads-Software Repository', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;h2 align=\"right\"&gt;&lt;font size=\"5\"&gt;&lt;em&gt;One click install&lt;/em&gt;&lt;/font&gt;&lt;/h2&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt; &lt;p&gt;&lt;strong&gt;What is the Liferay Software Repository?&lt;/strong&gt;&lt;/p&gt; &lt;p&gt;Our &lt;em&gt;Software Repository &lt;/em&gt;is an exclusive feature of Liferay Portal that allows administrators to install, update, and remove Liferay Plugins (portlets, themes and layouts), all online with one click.&lt;/p&gt; &lt;p&gt;&lt;strong&gt;What is a Plugin?&lt;/strong&gt;&lt;/p&gt; &lt;p&gt;Plugins are ready-made additions to your basic Liferay Portal. These can be extra portlets, new themes or layouts.&lt;/p&gt; &lt;p&gt;The repository keeps track of version numbers and compatibility issues (for example, a portlet may only be compatible on Liferay 4.3.1) to reduce administration headaches.&lt;/p&gt; &lt;p&gt;By communicating with remote servers, the Software Repository discovers new functionality and content available both from Liferay and from third party portlet, theme and layout developers, thereby creating an online community of offerings.&lt;/p&gt; &lt;p&gt;And with instant, &lt;em&gt;one click install,&lt;/em&gt; the process is effortless.&lt;/p&gt; &lt;p&gt;&lt;br /&gt; &lt;strong&gt;Why is the Liferay Software &lt;/strong&gt;&lt;strong&gt;Repository &lt;/strong&gt;&lt;strong&gt;Significant?&lt;br /&gt; &lt;/strong&gt;&lt;/p&gt; &lt;p&gt;The development cycle for software is getting shorter and shorter, and Web 2.0 properties in particular are in a state of &amp;quot;perpetual beta.&amp;quot; The ability to quickly deploy new features into a production environment is key to a company or website staying competitive and attracting a critical mass of community members, the key competitive advantage of any social network.&lt;/p&gt; &lt;p&gt;The Software Repository ensures that the latest developments are available to our clients\' platforms. More importantly, it keeps the management of those features as painless as possible.&lt;/p&gt; &lt;p&gt;We offer 2 categories of Plugins in the Software Repository:&lt;/p&gt; &lt;blockquote&gt; &lt;ul&gt; &lt;li&gt;&lt;strong&gt;&lt;a href=\"http://www.liferay.com/web/guest/downloads/official_plugins\"&gt;Official Plugins&lt;/a&gt;&lt;/strong&gt; - These are contributed by Liferay and authorized Liferay partners. They are verified by Liferay as trusted plugins.&lt;/li&gt; &lt;li&gt;&lt;strong&gt;&lt;a href=\"http://www.liferay.com/web/guest/downloads/community_plugins\"&gt;Community Plugins&lt;/a&gt;&lt;/strong&gt; - These are contributed by our community. Anyone can submit their own plugins. A 3-step guide will guide you through the process.&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;/blockquote&gt;&lt;p&gt;Bookmark the pages, check back for updates, or contribute your own!&lt;/p&gt;&lt;blockquote&gt; &lt;/blockquote&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20071009233600, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3510, 3511, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3512', 1.0, 'Liferay Expands to China', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"headline\" type=\"text\">\n		<dynamic-content><![CDATA[Liferay Expands to China]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"creator\" type=\"text\">\n		<dynamic-content><![CDATA[www.liferay.com]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"link\" type=\"text\">\n		<dynamic-content><![CDATA[http://www.liferay.com/web/guest/about_us/news/liferay_china]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"description\" type=\"text_box\">\n		<dynamic-content><![CDATA[Company Opens Dalian Office In Response to Rapid Growth\n\n\"By tapping into a rich local talent pool, the Dalian office not only\nserves to support worldwide product development, but also helps to foster a\nrich technology exchange,\" said Bryan Cheung, Liferay\'s CEO. \"The open\nsource software movement is growing at a phenomenal rate in China. Liferay\naims to help foster and expand it.\"]]></dynamic-content>\n	</dynamic-element>\n</root>', 'announcements', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20071010073100, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3513, 3514, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3215', 1.0, 'HEADER-Latest_Bloggers', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"header\" type=\"text\">\n    <dynamic-content><![CDATA[Latest Bloggers]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-HEADER', 'BASIC-HEADER', SPECIFIC_TIMESTAMP_20071012220100, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3515, 3516, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2682', 1.0, 'Partners-Technology Partners-JProfiler', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[JProfiler]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header-link\" type=\"text\">\n		<dynamic-content><![CDATA[http://www.ej-technologies.com/products/jprofiler/overview.html]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-header-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[true]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"date\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"type\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image\" type=\"image\">\n		<dynamic-content id=\"210201\">/image/journal/article?img_id=1941&amp;t=1201070605804</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image-link\" type=\"text\">\n		<dynamic-content><![CDATA[http://www.ej-technologies.com/products/jprofiler/overview.html]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-image-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[true]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"flow\" type=\"list\">\n		<dynamic-content><![CDATA[right]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p>ej-technologies is the creator of JProfiler, an award-winning Java profiler targeted at JSE and JEE applications. JProfiler\'s intuitive GUI helps you find performance bottlenecks, pin down memory leaks and resolve threading issues.</p> <p><br /> <br /> </p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-ARTICLE', 'IMAGE-FLOW', SPECIFIC_TIMESTAMP_20071019221100, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3517, 3518, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3278', 1.0, 'PR-Pentaho_Partnership', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;div align=\"center\"&gt;&lt;p&gt;&lt;font size=\"3\"&gt;&lt;strong&gt;Liferay and Pentaho Announce Technology Partnership&lt;/strong&gt;&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;/div&gt; &lt;p&gt;&lt;strong&gt; Orlando, FL &amp;ndash; Oct 23, 2007 &amp;ndash; &lt;/strong&gt;Liferay, provider of the world&amp;rsquo;s leading enterprise-class, open source portal and content management system and Pentaho Corp., creator of the world&amp;rsquo;s most popular open source business intelligence (BI) suite, today announced a technology partnership to enhance integration between their product lines. By working together, Liferay and Pentaho will leverage open standards to simplify deployment for enterprise organizations looking to leverage commercial open source software to securely enable access to BI content as part of an overall enterprise portal strategy.&lt;/p&gt; &lt;p&gt;Portals provide an ideal framework to seamlessly integrate business intelligence with other enterprise applications and content. An enterprise portal framework makes it easy to deliver the benefits of business intelligence to large numbers of users, while enhancing the value of BI with application context, integrated collaboration, and an intuitive multi-application user interface. Portals can also make it easy for users to create personalized views of information, including dashboards that integrate BI with other enterprise content.&lt;/p&gt; &lt;p&gt;&amp;ldquo;Integrating business intelligence with enterprise portal frameworks provides a great opportunity to deliver greater information value to larger user communities,&amp;rdquo; said Mark Smith, CEO and EVP of Research at Ventana Research. &amp;ldquo;Liferay and Pentaho are expanding the access and attractiveness of their products to the enterprise by providing simplified configuration and tighter integration.&amp;rdquo;&lt;/p&gt; &lt;p&gt;Pentaho and Liferay both support the JSR-168 portal integration standard, providing a durable, standards-based method for integration. The companies are planning to work together to make integrated deployment even easier with minimal customization and configuration effort. The next phase of integration is expected to be delivered in Q1 of 2008.&lt;/p&gt; &lt;p&gt;&lt;em&gt;&lt;strong&gt;About Liferay:&lt;/strong&gt;&lt;br /&gt;Liferay Portal is the world&amp;rsquo;s leading enterprise open source portal framework with Fortune 500 clients around the world. Liferay Professional Services offers technical support, custom development and professional training to ensure successful deployment of its flagship product in the most demanding IT environments. The company is headquartered in Los Angeles with regional headquarters in Germany and China. For more information, please visit www.liferay.com.&lt;/em&gt;&lt;/p&gt;&lt;p&gt;&lt;em&gt;&lt;strong&gt;About Pentaho Corporation:&lt;/strong&gt;&lt;br /&gt;Pentaho Corporation provides a full spectrum of open source Business Intelligence (BI) capabilities including reporting, analysis, dashboards, data mining, data integration, and a BI platform that have made it the world&amp;rsquo;s most popular open source BI suite. Formed by a highly experienced team of industry veterans, Pentaho&amp;rsquo;s mission is to bring innovative, high quality technology and professional support to the BI market. Pentaho uses a revolutionary approach to development, distribution and support made possible by a commercial open source business model. Pentaho is the primary sponsor and owner of popular open source projects including JFreeReport, Kettle, Mondrian, and Weka. Pentaho&amp;rsquo;s technologies support a wide range of business initiatives from sales and profitability analysis, customer analysis, HR reporting, Financial reporting, KPI dashboards, Supply Chain analytics, and operational reporting. For more information, visit www.pentaho.com&lt;/em&gt;&lt;/p&gt;&lt;div align=\"center\"&gt;&lt;em&gt;####&lt;/em&gt;&lt;/div&gt;&lt;p&gt;&lt;strong&gt;Press Contact: &lt;/strong&gt;&lt;br /&gt;Corey Gutwillig &lt;br /&gt;S&amp;amp;S Public Relations, Inc. &lt;br /&gt;847/955-0700 ext. 9333 &lt;br /&gt;cgutwillig@sspr.com&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20071022075100, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
