insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3608, 3609, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3282', 1.0, 'PR-Collabnet', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content>&lt;p align=\"center\"&gt;&lt;strong&gt;&lt;font size=\"2\"&gt;CollabNet Builds New Discussion Forums, &lt;br /&gt;Using Liferay Developer and Collaboration Tools&lt;br /&gt;&lt;/font&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;LOS ANGELES, CA&amp;ndash; (October 29, 2007)&lt;/strong&gt; &amp;ndash; Liferay, provider of the world&amp;rsquo;s leading enterprise-class open source portal, announced that CollabNet, the leading provider of &lt;em&gt;Subversion&amp;reg;&lt;/em&gt; and other globally distributed software development tools has leveraged Liferay technology for its new Discussion Services functionality that is part of the recently released CollabNet Enterprise Edition 5.0.&lt;/p&gt;&lt;p&gt;Based on Liferay&amp;rsquo;s Message Board portlet and extended with Liferay&amp;rsquo;s ServiceBuilder tool, CollabNet&amp;rsquo;s Discussion Services is an enhanced collaboration tool for CollabNet&amp;rsquo;s collaborative platform for distributed development. &amp;ldquo;We saw the value and the potential of using Liferay&amp;rsquo;s development platform,&amp;rdquo; said Richard Murray, vice president of engineering at CollabNet. &amp;ldquo;There was a wealth of pre-built functionality available that provided a foundation to rapidly address the full range of our customers&amp;rsquo; needs.&amp;rdquo;&lt;/p&gt;&lt;p&gt;Notably, CollabNet&amp;rsquo;s enhancements to functionality were made using ServiceBuilder, Liferay&amp;rsquo;s exclusive code generator tool. Prebundled as part of the portal platform, ServiceBuilder helps developers get code enabled for Web Services, Spring, AJAX, and EJBs so they can focus on writing the business logic.&lt;/p&gt;&lt;p&gt;&amp;ldquo;We are thrilled to see organizations leveraging the power of Liferay&amp;rsquo;s developer tools,&amp;rdquo; said Brian Chan, Chief Software Architect of Liferay. &amp;ldquo;They were created and pre-packaged with the basic portlets specifically so that enterprises can tap into the potential of our out-of-the-box offering.&amp;rdquo;&lt;/p&gt;&lt;p&gt;The Discussion Services combine mailing list and discussion forum features from CollabNet&amp;rsquo;s current CollabNet Enterprise Edition&amp;reg; product under their fine-grained project security system. This gives Discussion Services administrators full control over who can access what content using either interface approach. The basic Liferay Message Board portlet includes features that are most comparable with CollabNet&amp;rsquo;s current discussion forums tool such as discussions, threads, messages, attachments, RSS and email subscriptions. It operates under Liferay&amp;rsquo;s similar fine-grained permissions system that resulted from technology sharing with CollabNet early in the project&amp;rsquo;s development.&lt;/p&gt;&lt;p&gt;The Discussion Services extend the Message Board primarily in the areas of email access (posting, subscription, moderation and bounce handling), multiple discussion types (unmoderated, trusted and moderated), multiple subscription types (individual, digest and web) and administrative options designed to limit access and content.&lt;/p&gt;&lt;p&gt;&amp;ldquo;With Discussion Services it is now possible to move an existing mailing list archive under our project security umbrella and tighten its access controls,&amp;rdquo; shared Jeff Eastman, principal architect of the Discussion Services project. &amp;ldquo;Similarly, it is possible to take an existing discussion forum and open it up for less-secure email access as well. Technology sharing with Liferay has enabled us to produce a superior collaboration product&amp;rdquo;.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;em&gt;About Liferay:&lt;/em&gt;&lt;/strong&gt;&lt;br /&gt;&lt;em&gt;Liferay Portal is the world&amp;rsquo;s leading enterprise open source portal framework with Fortune 500 clients world-wide. Liferay Professional Services offers technical support, custom development and professional training to ensure successful deployment of its flagship product in the most demanding IT environments. The company is headquartered in Los Angeles. For more information, please visit www.liferay.com.&lt;/em&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;em&gt;About CollabNet:&lt;/em&gt;&lt;/strong&gt;&lt;br /&gt;&lt;em&gt;With 1.2 million users, CollabNet is the industry\'s most widely used collaborative platform for software development. The CollabNet platform supports globally distributed teams in a centralized, cost-effective way, yielding 20% to 30% annual savings through reduced development infrastructure costs, increased productivity, improved project visibility and shared access to software assets. Founded upon open source principles, CollabNet is the primary sponsor of the Subversion&amp;reg; project, the new standard for distributed development teams.&lt;/em&gt;&lt;/p&gt;&lt;p align=\"center\"&gt;&lt;em&gt;####&lt;/em&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;</dynamic-content>\n	</dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20071029040600, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
