insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3616, 3617, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3332', 1.0, 'Products-Portal_Intro', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p><span style=\"font-size: small;\"><b>Liferay Portal is the world\'s leading open source enterprise portal solution using the latest in Java and Web 2.0 technologies.</b></span></p><ul><li>Built in Content Management System (CMS) &amp; Collaboration Suite</li><li>Award winning user interface</li><li>Out-of-the-box usablility &mdash;choose from <a target=\"_blank\" href=\"http://www.liferay.com/c/document_library/get_file?p_l_id=136815&amp;folderId=167862&amp;name=DLFE-314.pdf\">over 60 portlets</a></li><li>Out-of-the-box development tools</li><li>Out-of-the-box localization to 22 languages!</li><li>Runs on all major application servers, databases, and operating systems (over 700 deployment combinations)</li><li>Benchmarked as among the most secure portal platforms</li><li>Business-friendly MIT License</li></ul>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070810175900, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
