insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3625, 3626, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2381', 1.0, 'Products-What is a portal', 'content for what is a portal.', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<p><font size=\"2\">A portal is a web-based gateway for users to locate relevant content and use the applications they commonly need to be productive.</font></p><p><font size=\"2\">It offers compelling benefits to today&rsquo;s enterprises: reduced operational costs, improved customer satisfaction, and streamlined business processes.</font></p><p><font size=\"2\">Liferay Portal provides a virtual space where you can centralize, share and collaborate.</font> <font size=\"2\">Read <a href=\"@root_path@/web/guest/products/portal/bus_benefits\">more</a></font> about how our product can help your business.</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070723214200, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
