insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3672, 3673, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2445', 1.0, 'Aboutus-Leadership', 'content for the leadership page', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content>&lt;p&gt;Liferay was formed by four partners from diverse technological and professional backgrounds. Our seasoned team endeavors to pioneer innovative enterprise solutions for companies worldwide.&lt;/p&gt; &lt;h2&gt;Brian Chan&lt;/h2&gt; &lt;p&gt;Brian Chan, Chief Software Architect, began development on Liferay Portal in 2000 to provide nonprofit organizations with an open source solution to facilitate collaboration on the Internet. He has since steered Liferay to become a leader in innovative open source enterprise solutions. With a strong foundation in software architecture and economics, Brian has solidified open source as a low-cost, high performance solution for the enterprise. His expertise in portal architecture and design has garnered him a seat on the JSR-286 portlet specification committee.&lt;/p&gt; &lt;p&gt;Brian holds a dual B.S. degree in Economics and Computer Science from the University of Chicago.&lt;/p&gt; &lt;h2&gt;Bryan Cheung&lt;/h2&gt; &lt;p&gt;Bryan Cheung, CEO and a founding partner of Liferay, Inc., has an extensive background in consulting with Fortune 500 companies such as Genentech, Universal Music Group, Sony Consumer Electronics, Accenture Consulting, and Gateway Computers. Backed by a solid mix of technical knowledge and managment skills, he has helped steer Liferay\'s partner relationships and business development efforts around the world.&amp;nbsp;&lt;/p&gt; &lt;p&gt;Bryan holds a bachelor\'s degree in Computer Science from the University of California, Berkeley.&lt;/p&gt; &lt;h2&gt;Brian Kim&lt;/h2&gt; &lt;p&gt;Brian Kim is one of the original founders of Liferay, Inc. and experienced technology consultant and operations manager. As COO, he has led the growth of the company\'s Professional Services Organization and orchestrates the ongoing operations of a fast-growing global organization. Prior to co-founding Liferay, Brian worked alongside upper management at HubbardOne on internal company operations. He is also an alumnus of Accenture where he worked with marquis clients in the financial services sector.&lt;/p&gt; &lt;p&gt;Brian holds a B.S. and M.S. in Computer Science from the University of Chicago.&lt;/p&gt; &lt;h2&gt;Michael Young&lt;/h2&gt; &lt;p&gt;Michael Young is Liferay\'s CTO and brings over 10 years of enterprise computing experience to the company. Michael has held positions at Sony Electronics, Syntricity, and the United States Navy. At Syntricity he led the overhaul of its dataConductor EP data management and reporting software platform, paving the company\'s way to profitability. At Liferay, Michael heads up integration efforts with key technology partners such as Alfresco and ICEsoft.&lt;/p&gt; &lt;p&gt;Michael holds a B.S. in Computer Science from the University of California, San Diego.&lt;/p&gt;</dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070724211300, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
