insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3700, 3701, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2924', 1.0, 'Community-How_to', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content>&lt;p&gt;&amp;nbsp;&lt;/p&gt; &lt;h2&gt;Installation Instructions&lt;/h2&gt;&lt;p&gt;If you cannot use the Plugin Portlet (because your Liferay installation is behind a firewall or has no access to the Internet), you can install a plugin manually by copying it to your hot deploy directory.&lt;/p&gt; &lt;p&gt;By default, this directory is located in your user directory, which is different for each operating system&lt;/p&gt; &lt;ul&gt; &lt;li&gt;Windows XP: C:\\Documents and Settings\\&amp;lt;user name&amp;gt;&lt;/li&gt; &lt;li&gt;Windows Vista: C:\\Users\\&amp;lt;user name&amp;gt;&lt;/li&gt; &lt;li&gt;Linux: /home/&amp;lt;user name&amp;gt;&lt;/li&gt;&lt;li&gt;Mac OS X: /Users/&amp;lt;user name&amp;gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt; &lt;p&gt;After you have started Liferay, you should find the hot deploy directory in &amp;lt;user directory&amp;gt;/liferay/deploy.&lt;br /&gt; Copy the plugin there, and watch the Liferay console.&lt;br /&gt; When you see the message &amp;quot;registered successfully,&amp;quot; the plugin has been deployed.&lt;/p&gt;</dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20071101172700, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3702, 3703, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2487', 1.0, 'Aboutus-Events-Meetup 2007', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[Alfresco + Liferay: Content Management and Portal Meetup ]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"date\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"type\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image\" type=\"image\">\n		<dynamic-content id=\"1556\">/image/journal/article?img_id=1550&amp;t=1207241689604</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<p>July 18th, 2007<strong> </strong><br /> Ontario, California&nbsp;</p> <p>Come one, come all to the Alfresco + Liferay: CMS and Portal meetup!</p> <p>We\'re hosting interactive round-table discussions on subjects such as best practices, integration, roadmap, case studies and much more!</p> <p>Unlike a typical conference where one person dictates to the crowd, we\'re hosting small round-tables where YOU are in control of the discussion. No sales or marketing hype here! Only grade A certified prime content for you portal and CMS lovers!</p> <p>This meetup is open to anyone and everyone who is interested in Alfresco, Liferay or content management and portals in general. There will be plenty opportunities to network, ask questions, and provide feedback that the Alfresco and Liferay communities will use to grow and improve.</p> <p>The meetup runs the whole day (9:00am - 4:30pm) and concludes with a FREE HAPPY HOUR at a local bar/restaurant.</p> <ul>     <li><a href=\"http://web.meetup.com/44/pages/MeetUp_Agenda/\" target=\"_blank\">Meeting Agenda</a></li> </ul>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header-link\" type=\"text\">\n		<dynamic-content><![CDATA[http://web.meetup.com/44/]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"flow\" type=\"list\">\n		<dynamic-content><![CDATA[default]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-header-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-image-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-ARTICLE', 'BASIC-IMAGE-TEXT', SPECIFIC_TIMESTAMP_20070731021800, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
