insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3714, 3715, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2510', 1.0, 'Aboutus-Awards-eWeek_Review_October05', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[eWeek Review (October 2005)]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"date\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"type\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image\" type=\"image\">\n		<dynamic-content id=\"1752\">/image/journal/article?img_id=1688&amp;t=1201071534338</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<p>eWeek reviewed Liferay 3.6.1.<br /><em><br />&nbsp;&ldquo;It\'s long been commonplace to find open-source portals that rival or better their commercial counterparts in features and capabilities. And for a few years now, open-source portals have featured intuitive and flexible browser-based administration interfaces.<br />All of the above is true for the open-source Liferay Portal, but its browser-based administration and content creation interface goes much further, providing in-depth interactivity and capabilities that one usually finds only in desktop applications&mdash;providing this breadth of capabilities across all current-generation Web browsers.&rdquo;</em></p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"flow\" type=\"list\">\n		<dynamic-content><![CDATA[default]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-header-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-image-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-ARTICLE', 'BASIC-IMAGE-TEXT', SPECIFIC_TIMESTAMP_20070801214400, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3716, 3717, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2516', 1.0, 'Aboutus-Careers', 'content for the careers page', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;p&gt;Liferay has been enjoying great deal of success this year as a project and company. Now we\'re looking to add a few more good people to our growing team. If you or someone you know fit into any of the following roles, send us a resume to &lt;a href=\"mailto:careers@liferay.com\"&gt;careers@liferay.com&lt;/a&gt;.&lt;/p&gt; &lt;h2&gt;Job Openings&lt;/h2&gt; &lt;ul&gt; &lt;li&gt;&lt;a href=\"#ajax\"&gt;AJAX Developer&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#frontend\"&gt;Front End Developer&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#consultant\"&gt;Consultant&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#seniorconsultant\"&gt;Senior Consultant&lt;/a&gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;&lt;a name=\"ajax\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;AJAX Developer&lt;/h2&gt; &lt;p&gt;You are amazing with AJAX development and have already built great web applications that feel like desktop applications. You are comfortable with writing homegrown code or using libraries like jQuery or prototype (but you placed your bets on jQuery six months ago). You are an expert in DOM scripting.&lt;/p&gt; &lt;p&gt;You are not uncomfortable working in the context of a complex Java web application even if you don\'t understand its intricacies.&lt;/p&gt; &lt;p&gt;You are a self starter who loves working with technology and you stay up late putting the finishing touches on your code. You don\'t need to be told what to do to see what needs to get done. You are excited about working with a leading open source software company with cutting-edge opportunities at Fortune 500 companies around the world.&lt;/p&gt; &lt;p&gt;&lt;a name=\"frontend\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Front End Developer&lt;/h2&gt; &lt;p&gt;You have been working with (X)HTML and CSS for anywhere from 1-4 years. You prefer front end development because you\'re creative and visual and have a good eye for design. You write standards-compliant / semantic (X)HTML and CSS without Googling the syntax or using WYSIWYG editors.&lt;/p&gt; &lt;p&gt;You understand the difference between standards mode and quirks mode. You know what the box model hack is and what versions of IE it works for. You may love Firebug because it saves your life or you are so good you don\'t need it. You can use Gimp, Fireworks or Photoshop to cut up images and are handy with designing an icon or two in a pinch.&lt;/p&gt; &lt;p&gt;You are not uncomfortable working in the context of a complex Java web application even if you don\'t understand its intricacies. You won\'t get scared off by scripting markup like Velocity or JQuery showing up in the code here and there.&lt;/p&gt; &lt;p&gt;You are a self starter who loves working with technology and you stay up late putting the finishing touches on your code. You don\'t need to be told what to do to see what needs to get done. You are excited about working with a leading open source software company with cutting-edge opportunities at Fortune 500 companies around the world.&lt;/p&gt; &lt;p&gt;&lt;a name=\"consultant\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Consultant&lt;/h2&gt; &lt;p&gt;You have 3-4 significant years (not just filling a billable seat) of experience working in deeply technical projects on client sites. You have at least a year of experience working as a technical team lead or in other client-facing situations. You have worked with business stakeholders to translate their deep understanding of their business into ones and zeroes. Clients like you because you are comfortable to work with and they trust your judgment on technology. You are familiar with different development methodologies, including agile.&lt;/p&gt; &lt;p&gt;Technically speaking you have very good Java fundamentals and J2EE experience. You are starting to really understand how Struts, Spring and Hibernate fit together in a well-designed Java application and you have been exposed to design patterns. You are curious about the Ruby rage because you realize that a language is just a language but development is about strong fundamentals like algorithms and OOP.&lt;/p&gt; &lt;p&gt;You are a fan of open source because of the amount of quality software available from the FLOSS community. You have begun to work with Liferay in your free time or on a project. You know what ant build-service means.&lt;/p&gt; &lt;p&gt;You still enjoy traveling up to 90% of the time and are excited to work with a leading open source software company on great project opportunities at Fortune 500 companies around the world.&lt;/p&gt; &lt;p&gt;&lt;a name=\"seniorconsultant\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Senior Consultant&lt;/h2&gt; &lt;p&gt;You have 5-6 years of experience working in client-facing situations in deeply technical projects. As an ideal candidate you have experience with the full project lifecycle for technical implementations, including business requirements gathering, technical specifications, development, QA, and deployment to production. You prefer agile development methodologies but are equally comfortable walking with clients through more traditional processes. You are more than competent in being a trusted advisor to both business stakeholders and senior architects in Fortune 500 companies.&lt;/p&gt; &lt;p&gt;Technically speaking you have strong Java fundamentals and J2EE experience, are most comfortable using Java 1.4, understand why Liferay stopped using EJBs in our latest version, and have opinions about JSF, Struts, Hibernate and Spring. You read The Server Side or Java Lobby for fun. You know who Martin Fowler is.&lt;/p&gt; &lt;p&gt;You also have a strong commitment to the philosophy of open source and are particularly passionate about Liferay. You understand why it\'s significant that Liferay is still offered under an MIT license. You have been a contributor or user to the Liferay project for several years, you know who Brian, Mike, Ray or Jorge by first name, and you remember when our logo used to be an alien.&lt;/p&gt; &lt;p&gt;On top of all this, you love to travel up to 90% of the time and you believe in the power of community to build portals and the power of portals to build community.&lt;/p&gt; &lt;h2&gt;PLUSES:&lt;/h2&gt; &lt;ul&gt; &lt;li&gt;You know the peculiarities of specific app servers, Tomcat, or specific databases&lt;/li&gt; &lt;li&gt;You have integrated Liferay with other open source products or proprietary products like Exchange or Lotus&lt;/li&gt; &lt;/ul&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070724212300, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
