insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3819, 3820, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2699', 1.0, 'Services-Training-On-Site Training', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;p&gt;To maximize knowledge transfer to your staff, Liferay offers hands-on training on-site. Both curriculum content and length are tailored to fit your organization\'s unique needs.&lt;/p&gt; &lt;p&gt;Prior to the training sessions, you and a Liferay representative will discuss your training needs and the appropriate training days required.&lt;br /&gt; &lt;br /&gt; &lt;strong&gt;About the Instructors:&lt;/strong&gt;&lt;br /&gt; &lt;br /&gt; Training with Liferay, Inc. means that you gain access to the core developers of Liferay Portal. Our instructors are full time technical employees behind the product and offer unparalleled knowledge of our platform in addition to their expertise across a number of other technologies and architectures.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Onsite Training Advantages:&lt;/strong&gt;&lt;/p&gt; &lt;ul&gt; &lt;li&gt;Cost effective&lt;/li&gt; &lt;li&gt;Fully customized topics&lt;/li&gt; &lt;li&gt;No traveling cost for your staff&lt;/li&gt; &lt;li&gt;Your staff receives maximum knowledge transfer because topics and Q&amp;amp;A time are entirely focused on your company\'s needs&lt;/li&gt; &lt;li&gt;Courses can be tailored to cover introductory to advanced levels&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070726074600, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
