insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3823, 3824, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2705', 1.0, 'Services-Training-LA_Main', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"sidebar-location\" type=\"list\">\n		<dynamic-content><![CDATA[right]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"sidebar-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Syllabus</h2><p><a href=\"@root_path@/web/guest/services/training/course_topics\">Course Topics</a></p><h2>Date</h2><p>June 3 - 6<br />9am-5pm daily</p><h2>Location</h2><p><a target=\"_blank\" href=\"http://www.ayreshotels.com/diamondbar/index.asp\">Ayres Suites</a></p><div>21951 Golden Springs Drive</div><div>Diamond Bar, CA 91765</div><h2>Additional Information</h2><p>Please note that a laptop is required for this course.</p><h2>Register</h2><p>Please click <a href=\"@root_path@/web/guest/services/training/LA/registration\">here</a> to register.</p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"main-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Overview</h2><p>Liferay will host a public training seminar&nbsp;in the greater LA area. The seminar will be comprehensive, covering architecture, installation, portlet development, themes, and CMS.</p><p>This course is designed to help you come up to speed on the functionality and development of Liferay Portal. It will help you to increase your productivity as well as introduce you to valuable new features.</p><h2>Latest&nbsp;Release</h2><p>Come and learn about the new features Liferay Portal&nbsp;5.0 has to offer!</p><h2>Hands-On Training</h2><p>We will take you through the entire process of portlet development and teach you industry best practices.</p><h2>Subject Matter Experts</h2><p>The course will be led by the Liferay experts who designed the software.</p><h2>Development Environment</h2><p>For development we will setup Tomcat on Eclipse and use MySQL for the database. Although Liferay can be used with any application server and database, the above setup is the fastest in terms of development. We may also touch upon installation with other application servers and databases as well.</p><h2>Prerequisites</h2><p>Basic knowledge of Java and Servlets is required. The course will be intensive, but we will make sure to answer any questions you have to ensure that you have a solid grasp of the technology by the end of the course.</p><p>Please note that a laptop is required for this course.</p><ul><li>Minimum System Requirements: 1.4 GHz 512MB RAM</li><li>Recommended System Requirements: 2.0 GHz 1GB RAM</li><li>Wireless Card Required</li></ul>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT-SIDEBAR', 'BASIC-CONTENT-SIDEBAR', SPECIFIC_TIMESTAMP_20070726183400, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3825, 3826, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2708', 1.0, 'Services - Training - Santa Clara - Page Header', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"page-name\" type=\"text\">\n		<dynamic-content><![CDATA[Santa Clara, CA]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"page-description\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'PAGE-HEADER', 'PAGE-HEADER', SPECIFIC_TIMESTAMP_20070726083100, TRUE, 2, 'Joe Bloggs', FALSE, FALSE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3827, 3828, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2711', 1.0, 'Services-Training-SantaClara_Main', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"sidebar-location\" type=\"list\">\n		<dynamic-content><![CDATA[right]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"sidebar-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Training Schedule</h2><p><a href=\"http://www.cignex.com/site/services/training/liferay/course_details\">Course Topics</a></p><h2>Training Time</h2><p>April&nbsp;7 - 10<br />9am-5pm daily</p><h2>Additional Information</h2><p>Please note that a laptop is required for this course.</p><h2>How to Register</h2><p>Please click <a href=\"http://www.cignex.com/site/services/training/liferay/registration_info\">here</a> to register.</p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"main-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Overview</h2><p>CIGNEX, a training partner of Liferay is conducting Liferay Portal Training at CIGNEX office, Santa Clara, California. The training covers the architecture, installation, portlet development, themes, and CMS.</p><p><a href=\"http://www.cignex.com/site/services/training/liferay/registration_info\">Registration and Payment Information</a> (Online registration available NOW)</p><p>This course is designed to help you come up to speed on the functionality and development of Liferay Portal. It will help you to increase your productivity as well as introduce you to valuable new features, such as the runtime-portlet tag and hot-deployable themes. We will also teach you how to migrate your existing Struts applications to a portlet environment.</p><h2>Latest&nbsp;Release</h2><p>Come and learn about the new features Liferay Portal 4.4 has to offer!</p><h2>Hands-On Training</h2><p>We will take you through the entire process of portlet development and teach you industry best practices. You will learn how to code the action class, auto-generate business components, implement server-side validation, and perform database operations.</p><h2>Subject Matter Experts</h2><p>The course will be led by CIGNEX, a training partner of Liferay.</p><h2>Development Environment</h2><p>For development we will setup Tomcat on Eclipse and use MySQL for the database. Although Liferay can be used with any application server and database, the above setup is the fastest in terms of development. We may also touch upon installation with other application servers and databases as well.</p><h2>Prerequisites</h2><p>Basic knowledge of Java and Servlets is required. The course will be intensive, but we will make sure to answer any questions you have to ensure that you have a solid grasp of the technology by the end of the course.</p><p>Please note that a laptop is required for this course.</p><ul><li>Minimum System Requirements: 1.4 GHz 512MB RAM</li><li>Recommended System Requirements: 2.0 GHz 1GB RAM</li><li>Wireless Card Required</li></ul><p>&nbsp;</p><h2>Training Location</h2><p>Training will be conducted at the CIGNEX office, Santa Clara, CA, USA.</p><p>For more details please contact Munwar Shariff at 408.327.9900 x303 or send email to training@cignex.com.</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT-SIDEBAR', 'BASIC-CONTENT-SIDEBAR', SPECIFIC_TIMESTAMP_20070726183600, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
