insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3867, 3868, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2783', 1.0, 'Aboutus-Events-Brainshare_2007 (full page)', 'full page details for Brainshare 2007', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;div align=\"justify\"&gt;March 18 - 23, 2007&lt;br /&gt;Salt Lake City, Utah&lt;br /&gt;Salt Palace Convention Center&lt;br /&gt;&lt;a href=\"https://www.novellbrainshare.com/slc2007/exhibitor/custom/BS07_ERC_Exhbt_Hall_Scale.pdf\"&gt;Booth 230&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;With the sunsetting of the exteNd Portal platform (support ending 6/9/09), many Novell customers needing a portal solution are looking for compelling alternatives with long-term viability. Liferay Portal is a mature open source portal platform, and its growing community of Novell users and compatibility with Novell products make it a viable replacement and alternative to exteNd. &lt;br /&gt;&lt;br /&gt;At Brainshare 2007, Liferay\'s chief software architect Brian Chan and CTO Mike Young (&lt;a href=\"http://www.novellbrainshare.com/slc2007/exhibitor/custom/BS07_ERC_Exhbt_Hall_Scale.pdf\"&gt;Booth 230&lt;/a&gt;) will demonstrate how to deploy a full open source stack&amp;nbsp; using Novell SUSE Linux 10, Apache Geronimo Application Server 1.1, and Liferay Portal 4.2 (or most current version). They will also demonstrate how to use Liferay\'s LDAP administration GUI to integrate with Novell eDirectory, as well as how to extend the default Liferay user profile with additional attributes found in an LDAP directory.&lt;br /&gt;&lt;br /&gt;Key features of the portal and a high level objective comparison with other offerings will be highlighted so that attendees who provide Novell professional services can make informed recommendations to clients. Case studies of companies using Liferay on SUSE and/or with eDirectory will also be presented.&lt;/div&gt;&lt;div align=\"center\"&gt;---------------------&lt;/div&gt;&lt;div align=\"justify\"&gt;Liferay will also be collaborating with a new technology partner, &lt;a href=\"http://www.trivir.com\"&gt;TriVir&lt;/a&gt; (&lt;a href=\"http://www.novellbrainshare.com/slc2007/exhibitor/custom/BS07_ERC_Exhbt_Hall_Scale.pdf\"&gt;Booth 530&lt;/a&gt;), to preview new features and functionality enhancements:&lt;br /&gt;&lt;blockquote&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;&lt;em&gt;Liferay running behind Access Manager 3.0&lt;/em&gt;&lt;/strong&gt; &amp;ndash; TriVir has created code that will allow Liferay to consume an HTTP Basic Authentication Header. The Access Manager sends a basic auth header to Liferay when the user logs in, providing an elegant and maintainable single sign on solution. As Novell\'s Access Manager 3.0 also comes with a J2EE security agent for a number of popular application servers, many Novell users will find integrating this type of security directly into applications to be straightforward and flexible.&lt;/li&gt;&lt;/ul&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;&lt;em&gt;PasswordSmith Portlet&lt;/em&gt;&lt;/strong&gt; &amp;ndash; TriVir has also developed the first eDirectory Universal Password-enabled JSR-168 Portlet. With Liferay\'s new Plug-In Manager, this portlet can easily be deployed live into an existing running portal environment. But the real power of this portlet is that it is written to leverage password policies and Universal Password from eDirectory.&lt;/li&gt;&lt;/ul&gt;&lt;ul&gt;&lt;li&gt;&lt;em&gt;&lt;strong&gt;LDAP Dredging Feature&lt;/strong&gt; of Liferay Portal 4.2.1&lt;/em&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;&lt;em&gt;Novell IDM integration&lt;/em&gt; &lt;/strong&gt;with Liferay via the Liferay web service interface&lt;/li&gt;&lt;/ul&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;&lt;em&gt;Subscription portlet &lt;/em&gt;&lt;/strong&gt;&amp;ndash; This custom portlet from TriVir allows users to subscribe to newsletters based upon the business unit, or LDAP organizational unit, ready to go. A demonstration will show:&lt;/li&gt;&lt;/ul&gt;&lt;blockquote&gt;a. How to deploy a portlet or war file&lt;br /&gt;&lt;br /&gt;b. How to configure portlet preferences&lt;br /&gt;&lt;br /&gt;c. How to place a portlet instance on a page&lt;br /&gt;&lt;br /&gt;d. The custom code of a working &amp;quot;JSF style&amp;quot; portlet&lt;br /&gt;&lt;/blockquote&gt;&lt;blockquote&gt;&amp;nbsp;&lt;/blockquote&gt;&lt;/blockquote&gt;Liferay welcomes you to join them at&amp;nbsp; Booth 230 in the Salt Palace Convention Center to see some of the interesting new technologies being developed through this new partnership. &lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;div align=\"center\"&gt;&lt;img alt=\"\" src=\"http://www.trivir.com/trivirlogo.gif\" /&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;img width=\"152\" height=\"36\" alt=\"\" src=\"http://www.liferay.com/html/themes/brochure/images/custom/liferay-logo.png\" /&gt;&lt;/div&gt;&lt;/div&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070726213500, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
