insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3875, 3876, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2796', 1.0, 'Aboutus-Events-JAX_India_2007 (full page)', 'Full page details for JAX India', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;p&gt;&lt;a target=\"_blank\" href=\"http://www.sda-india.com/conferences/jax-india/\"&gt;JAX India 2007&lt;/a&gt;&lt;br /&gt;May 28-31, 2007&lt;br /&gt;J N Tata Auditorium, IISc&lt;br /&gt;Bangalore India &lt;br /&gt;&lt;br /&gt;Liferay is a &lt;a target=\"_blank\" href=\"http://www.sda-india.com/conferences/jax-india/sponsor_overview.php\"&gt;Gold Sponsor&lt;/a&gt; for this year\'s JAX India, one of the largest and most important events for enterprise technologies in Europe and Asia.&lt;br /&gt;&lt;br /&gt;Join us in Bangalore as CEO, Bryan Cheung and Chief Software Architect, Brian Chan speak at three separate &lt;a target=\"_blank\" href=\"http://www.sda-india.com/conferences/jax-india/speakers.php\"&gt;sessions &lt;/a&gt;on portal technology and the market: &lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Session: &lt;/strong&gt;Leveraging SOA and ESB for an Enterprise Architecture&lt;br /&gt;&lt;strong&gt;Speaker: &lt;/strong&gt;Bryan Cheung, CEO&lt;br /&gt;&lt;br /&gt;The Gartner Group predicts that by 2008, 80% of development projects will be related to service oriented architectures. BEA, IBM, and others are pouring millions into marketing around their software\'s readiness to take on the crucial task of providing a SOA. But are there open alternatives? This session will showcase the Liferay architecture, including the exposure of services via EJBs, Spring, JSON, and SOAPWeb Services to facilitate the rapid deployment of composite applications in an SOA context. Also highlighted will be integration with the Apache ServiceMix enterprise service bus (chosen for its compatible license), and Liferay\'s leveraging of the JBPM Workflow engine.&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Session: &lt;/strong&gt;JSR 286: The New Portlet API&lt;br /&gt;&lt;strong&gt;Speaker: &lt;/strong&gt;Brian Chan, Chief Software Architect&lt;br /&gt;&lt;br /&gt;JSR 168, the first version of the Portlet API, defined standards for the expected behavior of portlets and portlet containers. However, the fledgling specification lacked clear definitions in key areas of portlet and portlet container behavior. As a result, developing real world applications in JSR 168 almost always requires going beyond the specification to fulfill business requirements. JSR 286 improves on the JSR 168 spec by providing for inter-portlet communication, eventing, portlet filters, and more. This session will discuss some of the topics related to the JSR286 (except confidential aspects) and cover real world examples where the new definitions might make development easier than with JSR168.&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Session: &lt;/strong&gt;Why Java Kicks the Crap Out of PHP and Ruby&lt;br /&gt;&lt;strong&gt;Speaker: &lt;/strong&gt;Brian Chan, Chief Software Architect&lt;br /&gt;&lt;br /&gt;The Server Side, JavaLobby, and other developer communities are notorious battlegrounds for the perpetual wars over which programming framework reigns supreme. Yet at the end of the day, technology is just a tool to be used to get the job done, and different problems are best addressed with different tools. The success or failure of a project more often depends not on the tools used, but whose hands they\'re in. This session will discuss the various strengths and weaknesses of these three popular platforms. We will also see how Liferay, an open source Java portal, can be an aggregator of portlets using different technologies, including Java, Ruby, and PHP.&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070726214200, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3877, 3878, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2799', 1.0, 'HEADER-Meetup_2007', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"header\" type=\"text\">\n    <dynamic-content><![CDATA[Alfresco + Liferay: Content Management & Portal Meetup!]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-HEADER', 'BASIC-HEADER', SPECIFIC_TIMESTAMP_20070726201400, TRUE, 2, 'Joe Bloggs', FALSE, FALSE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3879, 3880, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2803', 1.0, 'Stories-CAP_GmbH (Happy Digits)', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"company-image\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"company-link\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"company-link-name\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"quote\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;p&gt;&lt;em&gt;&lt;font size=\"3\"&gt;&amp;quot;With CAPassist, Liferay Portal and Ancud IT Services have been leveraged to deliver a workflow portal that structures and accelerates the internal processes of CAP GmbH. Based on open source components, the portal empowers CAP GmbH&amp;rsquo;s various divisions and their external service providers to collaborate effectively&amp;quot; &lt;/font&gt;&lt;/em&gt;&lt;/p&gt;&lt;p align=\"right\"&gt;- Christoph Kalka, Customer Service, CAP GmbH&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;p&gt;&lt;strong&gt;Company Profile&lt;/strong&gt;&lt;br /&gt; &lt;br /&gt; CAP GmbH is the operating company of &lt;a href=\"https://www.happydigits.de/servlet/hdhtml/index\" target=\"_blank\"&gt;HappyDigits&lt;/a&gt; founded by Deutsche Telekom, German parent of T-Mobile, and KarstadtQuelle, Europe\'s largest department store and mail order group. With over 26 million customer cards, HappyDigits is one of the most popular customer incentive programs in Germany. &lt;br /&gt; &lt;br /&gt; CAP GmbH develops and markets the bonus system, is responsible for winning partners, and manages the reward system. The company specializes in the areas of customer loyalty and incentive programs supporting large and medium size companies with customer relationship management. Its competencies range from technology and back-office, letter shop, bonus management and web presence to market analysis, reporting and others.&lt;br /&gt; &lt;strong&gt;&lt;br /&gt; Challenge&lt;/strong&gt;&lt;br /&gt; &lt;br /&gt; CAP GmbH coordinates complex marketing campaigns that reach several million addressees and works with external suppliers such as marketing agencies and copy shops. The result is a complex production process with a multitude of single steps, components, actors and dependencies. &lt;br /&gt; &lt;br /&gt; Therefore, CAP GmbH was looking for:&lt;/p&gt; &lt;ul&gt; &lt;li&gt;An application accessible to the involved internal departments as well as to the external suppliers.&lt;/li&gt; &lt;li&gt;An additional budgeting tool to record the costs for balancing and evaluation. Campaign processes take place in an extremely time dependent environment. Clear process mapping was of major importance.&lt;/li&gt; &lt;li&gt;A user-friendly interface to ensure users would be more productive using the application.&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;&lt;strong&gt;&lt;br /&gt; Resolution&lt;/strong&gt;&lt;br /&gt; &lt;br /&gt; CAP GmbH was at first open to both open source and proprietary solutions for its portal framework. After evaluating both options, however, CAP GmbH and system integrator, Ancud IT, chose Liferay Portal for its wide range of functionalities, technical flexibility and high ROI. &lt;br /&gt; &lt;br /&gt; Because Liferay Portal is JSR-168 compliant, Ancud IT was able to quickly integrate different applications within the new portal. Liferay&amp;rsquo;s standards-compliance also significantly facilitates the replacement of applications within the portal infrastructure. Outdated or obsolete software can be easily removed and replaced. &lt;br /&gt; &lt;br /&gt; By leveraging Liferay&amp;rsquo;s capabilities, Ancud IT was able to deliver a workflow tool, CAPassist, and a budgeting tool, CAPbudget:&lt;em&gt;&lt;br /&gt; &lt;br /&gt; Workflow&lt;/em&gt;&lt;br /&gt; CAPassist gives users personalized task management and context-sensitive navigation. Automatically scheduled e-mails help users to continuously monitor production progress. In addition to versioning, Liferay Portal&amp;rsquo;s automatic time scheduling and document management features are invaluable to the CAPassist portal. &lt;br /&gt; &lt;br /&gt; Liferay Portal allowed Ancud IT to structure and automate working steps and accelerate them, thus making processes independent of place and time. It also greatly reduced fault probability.&lt;br /&gt; &lt;em&gt;&lt;br /&gt; Budgeting&lt;/em&gt;&lt;br /&gt; To address budgeting, Ancud IT integrated a budgeting and reporting tool, CAPbudget. The ability to compare estimated and real costs at a glance greatly facilitates standardized and precise budget planning. The tool also features important accounting functions like reversals and transfers.&lt;br /&gt; &lt;br /&gt; Through CAPbudget, third parties such as internal departments or parent companies can be directly charged for services provided by CAP GmbH. Calendar management makes sure that no budgets &amp;ldquo;get lost&amp;rdquo; after monthly or project balances. Reporting functionalities with an Excel interface makes direct analysis and evaluation of budgets in Excel possible.&lt;br /&gt; &lt;br /&gt; Due to the success of CAPassist and its high degree of acceptance, CAP GmbH is now planning to introduce Liferay Portal to the entire company. Several additional integrations will be built, including:&lt;/p&gt; &lt;ul&gt; &lt;li&gt;Call center operations&lt;/li&gt; &lt;li&gt;Microsoft Outlook integration for improved calendar management&lt;/li&gt; &lt;li&gt;Integration of a unit cost tool based on Hyperion S-Base&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;&lt;br /&gt; In keeping with the open source character of the project, CAP GmbH is planning to publish parts of the source code developed on the Internet to make it available to the community to serve administrators and developers as a best-practice example.&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"full-image1\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"thumb-image1\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"full-image2\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"thumb-image2\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"full-image3\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"thumb-image3\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"full-image4\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"thumb-image4\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"full-image5\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"thumb-image5\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"full-image6\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"thumb-image6\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"full-image7\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"thumb-image7\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"full-image8\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"thumb-image8\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"full-image9\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"thumb-image9\" type=\"image\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'SHOWCASE', 'SHOWCASE', SPECIFIC_TIMESTAMP_20070801193400, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
