insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3927, 3928, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2880', 1.0, 'Stories-RCM Managing Authority', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"company-image\" type=\"image\">\n		<dynamic-content id=\"1722\">/image/journal/article?img_id=1665&amp;t=1201072566033</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"company-link\" type=\"text\">\n		<dynamic-content><![CDATA[http://www.pepkm.gr]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"company-link-name\" type=\"text\">\n		<dynamic-content><![CDATA[www.pepkm.gr]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"quote\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<p><em><font size=\"3\">&quot;Liferay gives you the ability to build standalone web applications and to combine them to build a portal. This results to easiness of extending functionality.&quot; </font></em></p><p align=\"right\">- Kostas Karkaletsis, Gnomon Informatics S.A.d</p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Company Profile</h2> <p>The <a href=\"http://www.pepkm.gr/\">Managing Authority</a> of the Regional Operational Program of Central Macedonia 2000 - 2006 in Thessaloniki, Greece, is the&nbsp;authority that manages the activation, efficiency, rationality, and transparency of Operational Programme of Central Macedonia. The total budget of the Operational Programme for 2000-2006 is 1.5 billion euro. The projects funded by the Operational Programme are selected by the Managing Authority following the criteria described in the Program Complement and approved by the Monitoring Committee.</p> <h2>Challenge</h2> <p>The Managing Authority needed an enterprise portal system with content management capabilities that would allow the staff of the organization to update&nbsp;the site content on a daily basis and manage the large number of citizens and organizations accessing the portal for updated information:</p><ul> <li>The exploitation possibilities offered by the location of the Region</li> <li>News regarding the implementation progress of Regional Operating Program activities</li> <li>Update on the calls for proposal launched or terminated</li> <li>Press releases</li> </ul> <p>&nbsp;</p> <h2>Resolution</h2> <p><a href=\"http://www.gnomon.com.gr/\">Gnomon Informatics S.A.</a>, an IT consulting company located in Thessaloniki, Greece, selected Liferay Enterprise Portal as the framework for building the portal. Significant effort has been given towards applying multilingual behavior in all new portlets in order to create multilingual portals in both the user interface and the data. By leveraging Liferay\'s capabilities, Gnomon was able to:</p><ul> <li>Build a lot of new portlets: Web Content Manager (creation of dynamic html pages with table of contents), Helpdesk issue recording and tracking, Frequently Asked Questions, Banner Management, Datagrid portlets, Multilingual Wiki, and Newsletters</li> <li>Implement multilingual mechanism to data layer and supporting translations to any number of languages of the data. The portal now can be multilingual in both the user interface and data presentation.</li> <li>Develop specific tools: JavaScript menus, search engines</li> </ul><p>&nbsp;</p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image1\" type=\"image\">\n		<dynamic-content id=\"1723\">/image/journal/article?img_id=1723&amp;t=1201072566046</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image1\" type=\"image\">\n		<dynamic-content id=\"1724\">/image/journal/article?img_id=1666&amp;t=1201072566048</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image2\" type=\"image\">\n		<dynamic-content id=\"1725\">/image/journal/article?img_id=1725&amp;t=1201072566051</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image2\" type=\"image\">\n		<dynamic-content id=\"1726\">/image/journal/article?img_id=1667&amp;t=1201072566054</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image3\" type=\"image\">\n		<dynamic-content id=\"1727\">/image/journal/article?img_id=1727&amp;t=1201072566057</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image3\" type=\"image\">\n		<dynamic-content id=\"1728\">/image/journal/article?img_id=1668&amp;t=1201072566060</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image4\" type=\"image\">\n		<dynamic-content id=\"1729\">/image/journal/article?img_id=1729&amp;t=1201072566063</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image4\" type=\"image\">\n		<dynamic-content id=\"1730\">/image/journal/article?img_id=1669&amp;t=1201072566066</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image5\" type=\"image\">\n		<dynamic-content id=\"1731\">/image/journal/article?img_id=1731&amp;t=1201072566070</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image5\" type=\"image\">\n		<dynamic-content id=\"1732\">/image/journal/article?img_id=1670&amp;t=1201072566074</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image6\" type=\"image\">\n		<dynamic-content id=\"1733\">/image/journal/article?img_id=1733&amp;t=1201072566077</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image6\" type=\"image\">\n		<dynamic-content id=\"1734\">/image/journal/article?img_id=1671&amp;t=1201072566081</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image7\" type=\"image\">\n		<dynamic-content id=\"1735\">/image/journal/article?img_id=1735&amp;t=1201072566085</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image7\" type=\"image\">\n		<dynamic-content id=\"1736\">/image/journal/article?img_id=1672&amp;t=1201072566090</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image8\" type=\"image\">\n		<dynamic-content id=\"1737\">/image/journal/article?img_id=1673&amp;t=1201072566094</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image8\" type=\"image\">\n		<dynamic-content id=\"1738\">/image/journal/article?img_id=1674&amp;t=1201072566098</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image9\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image9\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'SHOWCASE', 'SHOWCASE', SPECIFIC_TIMESTAMP_20070801202400, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3929, 3930, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2888', 1.0, 'Stories-Telecentros', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"company-image\" type=\"image\">\n		<dynamic-content id=\"1877\">/image/journal/article?img_id=1775&amp;t=1201072598802</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"company-link\" type=\"text\">\n		<dynamic-content><![CDATA[http://www.telecentros.es]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"company-link-name\" type=\"text\">\n		<dynamic-content><![CDATA[www.telecentros.es]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"quote\" type=\"text_area\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<h2>Company Profile</h2> <p>Telecentros is a network of public offices that belongs to a government program called <em>Internet Rural</em> that promotes the use of new technologies in rural areas. Telecentros is part of Red.es -- a public company run by the Ministry of Industry, Tourism, and Commerce.</p> <h2>Challenge</h2> <p>For <em>Internet Rural</em>, Red.es opened public offices throughout many rural towns that provided free Internet access. In one of these Telecentros offices, residents in the area could surf the web and begin to utilize the new technologies.</p> <p>Red.es needed a web portal that displayed all Telecentros office locations in Spain and provided a description of the services they offered. Having over 3,000,000 potential users, the portal needed to&nbsp;connect all of the Telecentros offices. Achieving this was important in providing communication and collaboration tools for its users. Another requirement was that all the tools needed to be easy to use as many of residents had littleto no experience with a computer, let alone the World Wide Web.</p> <h2>Resolution</h2> <p>Germinus developed the Telecentros portal using Liferay Portal. They found that Liferay Portal had a user-friendly design. It was powerful in connecting users yet simple enough for those who had little or no experience with the Internet. Liferay Portal exceeded the requirements of the client.</p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image1\" type=\"image\">\n		<dynamic-content id=\"1878\">/image/journal/article?img_id=1878&amp;t=1201072598813</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image1\" type=\"image\">\n		<dynamic-content id=\"1879\">/image/journal/article?img_id=1776&amp;t=1201072598816</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image2\" type=\"image\">\n		<dynamic-content id=\"1880\">/image/journal/article?img_id=1880&amp;t=1201072598819</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image2\" type=\"image\">\n		<dynamic-content id=\"1881\">/image/journal/article?img_id=1777&amp;t=1201072598822</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image3\" type=\"image\">\n		<dynamic-content id=\"1882\">/image/journal/article?img_id=1882&amp;t=1201072598825</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image3\" type=\"image\">\n		<dynamic-content id=\"1883\">/image/journal/article?img_id=1778&amp;t=1201072598829</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image4\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image4\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image5\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image5\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image6\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image6\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image7\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image7\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image8\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image8\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"full-image9\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"thumb-image9\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'SHOWCASE', 'SHOWCASE', SPECIFIC_TIMESTAMP_20070801213400, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
