insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3933, 3934, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3289', 1.0, 'PR-EContent100_2007', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content>&lt;div align=\"center\"&gt;&lt;strong&gt;Liferay Portal Named to EContent 100 &lt;/strong&gt; &lt;br /&gt; &lt;strong&gt;As It Reaches 1 Million Downloads&lt;/strong&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;/div&gt; &lt;p&gt;&lt;strong&gt; LOS ANGELES &amp;ndash; (November 15, 2007) &amp;ndash;&lt;/strong&gt; Liferay, provider of the world&amp;rsquo;s leading enterprise-class, open source portal and content management system , was today named to the &amp;ldquo;EContent 100&amp;rdquo;, EContent magazine&amp;rsquo;s list of the 100 most influential and innovative companies in the digital content industry. Averaging 40,000 downloads a month, Liferay Portal recently passed the one million download mark.&lt;/p&gt; &lt;p&gt;The EContent 100 ranks companies by their impact on the digital content industry by a panel consisting of editors and specialists from EContent, as well as other Information Today, Inc. editors. The award recognizes Liferay Portal&amp;rsquo;s solutions for the enterprise, which include a broad spectrum of out of the box tools for content management, collaboration and web-publishing and that can be customized and extended with bundled development tools.&lt;/p&gt; &lt;p&gt;&amp;ldquo;We are honored to be recognized by EContent for the second consecutive year,&amp;rdquo; Said Bryan Cheung, Chief Executive Officer at Liferay. &amp;ldquo;This recognition and the reaching of the one millionth download mark reflect Liferay Portal&amp;rsquo;s solid following and market adoption. Enterprises know that we bring innovation and business value beyond just cost savings.&amp;rdquo;&lt;/p&gt; &lt;p&gt;Current users include the United Nations, AutoZone, Discovery Education, The French Ministry of Defense and a number of universities and marquis financial institutions around the world. For more information, visit www.liferay.com.&lt;/p&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt; &lt;p&gt;&lt;em&gt;&lt;strong&gt;About Liferay: &lt;/strong&gt;&lt;/em&gt;&lt;/p&gt; &lt;p&gt;&lt;em&gt;Liferay Portal is the world&amp;rsquo;s leading enterprise open source portal framework with Fortune 500 clients in the U.S. and Europe. Liferay Professional Services offers technical support, custom development and professional training to ensure successful deployment of its flagship product in the most demanding IT environments. Liferay clients include Discovery Education, Goodwill Industries, and the Christian Science Monitor. The company is headquartered in Los Angeles. For more information, please visit www.liferay.com.&lt;/em&gt;&lt;/p&gt; &lt;p align=\"center\"&gt;# # # #&lt;/p&gt; &lt;p&gt;&lt;strong&gt;Press Contact:&lt;/strong&gt;&lt;/p&gt; &lt;p&gt;S&amp;amp;S Public Relations, Inc.&lt;br /&gt; Tony Keller&lt;br /&gt; tkeller@sspr.com&lt;/p&gt;</dynamic-content>\n	</dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20071115200500, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
