insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3950, 3951, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2916', 1.0, 'Partners-Partnership Program', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;p style=\"line-height: 1.8em;\"&gt;In an environment where technology infrastructures are complex, Liferay\'s International Partner Program provides you with extensive training and support around our award winning portal.&lt;/p&gt; &lt;p style=\"line-height: 1.8em;\"&gt;While other industry programs segment functions that you can participate in and charges for participation in additional revenue streams, the Liferay International Partner Program enables you to immediately participate in many key revenue streams.&amp;nbsp;&lt;/p&gt; &lt;p style=\"line-height: 1.8em;\"&gt;Backed with Liferay\'s support, you have an enormous amount of flexibility to capture these opportunities. Choose from the following partnership categories and levels to best leverage your company\'s strengths.&lt;/p&gt; &lt;div&gt; &lt;ul&gt; &lt;li style=\"padding-bottom:5px\"&gt;&lt;strong&gt;Services Partners&lt;/strong&gt; engage in sales, consulting, and development services for Liferay products in their respective territories.&lt;/li&gt; &lt;li style=\"padding-bottom:5px\"&gt;&lt;strong&gt;Premier Partners&lt;/strong&gt; are carefully selected by Liferay to stand as the premier providers of sales, strategic marketing, consulting, development, training, and Level 1 support for Liferay products in their respective territories.&amp;nbsp; Only one Premier Partner for each territory will be appointed by Liferay.&amp;nbsp; Premier Partners have a regional presence and have made significant commitment to Liferay products and training.&lt;/li&gt; &lt;li style=\"padding-bottom:5px\"&gt;&lt;strong&gt;Preferred Partners&lt;/strong&gt; engage in sales, consulting, and development services for Liferay products in their respective territories.&amp;nbsp; With the full support of Liferay and Liferay\'s appointed Premier Partner, the Preferred Partner Program is ideal for vendors that have extensive technical expertise but could benefit from the marketing, training, and support services offered by Liferay and Liferay Premier Partners.&lt;/li&gt; &lt;/ul&gt; &lt;/div&gt; &lt;p style=\"line-height: 1.8em;\"&gt;Successful Liferay Partners share Liferay\'s vision and effectively execute business objectives.&amp;nbsp; Liferay\'s International Partners:&lt;/p&gt; &lt;div style=\"line-height: 1.8em;\"&gt; &lt;ul&gt; &lt;li&gt;Have extensive technological expertise&lt;/li&gt; &lt;li&gt;Share Liferay\'s commitment to providing innovative solutions for clients&lt;/li&gt; &lt;li&gt;Holds recognizable and trusted presence in their territories&lt;/li&gt; &lt;li&gt;Are motivated to continually refining Liferay product skills&lt;/li&gt; &lt;/ul&gt; &lt;/div&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070730200400, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
