insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3971, 3972, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3973', 1.0, 'Downloads-Liferay_Samples', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;style&gt; .sample-table td { background: #1C2633; border: 2px solid #141B24; } .sample-table .header { background: #304256; } &lt;/style&gt; &lt;h1&gt;Liferay Samples&lt;/h1&gt; &lt;p&gt;The sample portlets and layouts were developed by Liferay to teach the community how to create portlets and layouts. Contact us at staff@liferay.com if you would like us to promote components you have developed.&lt;/p&gt; &lt;p&gt;The sample portlets are JSR-168 compliant and can be hot-deployed. Portlet WARs are componentized applications and cleanly separated from the portal. Our goal is to provide the community with enough sample portlets so that everyone can go out and create JSR-168 compliant portlets to meet real-world needs.&lt;/p&gt; &lt;p&gt;Layout templates are used when adding content so that users can easily arrange portlets inside a custom design template. See the documentation for more information on Layout Design Templates.&lt;/p&gt; &lt;table class=\"sample-table\"&gt; &lt;tr&gt; &lt;td class=\"header\"&gt; &lt;h2&gt;Sample JSR-168 Portlets&lt;/h2&gt; &lt;/td&gt; &lt;/tr&gt; &lt;tr&gt; &lt;td&gt; &lt;p&gt;&lt;a href=\"LINK\"&gt;Sample ICEfaces JSF Sun Portlet&lt;/a&gt; (New For Use with Liferay 4.3.0rc1)&lt;br /&gt; This portlet shows how to use ICEfaces and the Sun implementation of JSF as a portlet. It demonstrates Ajax-powered field validation, field conversion, and file upload.&lt;/p&gt; &lt;p&gt;&lt;a href=\"LINK\"&gt;Sample DAO Portlet&lt;/a&gt;&lt;br /&gt; This portlet has one data access object that allows users to add, update, and delete entries. This portlet does not utilize a MVC or persistence framework in order showcase the minimum steps of writing a portlet that communicates with a database.&lt;/p&gt; &lt;p&gt;&lt;a href=\"LINK\"&gt;Sample JSP Portlet&lt;/a&gt;&lt;br /&gt; This portlet shows how to include JSPs from a portlet.&lt;/p&gt; &lt;p&gt;&lt;a href=\"LINK\"&gt;Sample JSF Sun Portlet&lt;/a&gt;&lt;br /&gt; This portlet shows how to use the Sun implementation of JSF as a portlet.&lt;/p&gt; &lt;p&gt;&lt;a href=\"LINK\"&gt;Sample JSF MyFaces Portlet&lt;/a&gt;&lt;br /&gt; This portlet shows how to use the MyFaces implementation of JSF as a portlet.&lt;/p&gt; &lt;p&gt;&lt;a href=\"LINK\"&gt;Sample JSON Portlet&lt;/a&gt;&lt;br /&gt; This portlet shows how to use JSON and AJAX to pull an application through a portlet.&lt;/p&gt; &lt;p&gt;&lt;a href=\"LINK\"&gt;Sample Struts Portlet&lt;/a&gt;&lt;br /&gt; This portlet uses Struts as a MVC framework to cleanly separate business logic from display logic. Developers who are familiar with Struts can use this portlet as an example of how to migrate existing Struts applications from a servlet environment to a portlet environment.&lt;/p&gt; &lt;/td&gt; &lt;/tr&gt; &lt;tr&gt; &lt;td class=\"header\"&gt; &lt;h2&gt;Sample Layout Templates&lt;/h2&gt; &lt;/td&gt; &lt;/tr&gt; &lt;tr&gt; &lt;td&gt; &lt;p&gt;&lt;a href=\"LINK\"&gt;Sample Pack 01 Layout Templates&lt;/a&gt;&lt;br /&gt; This sample pack includes the 2-1-2 layout template. The first row is made up of two columns of portlets. The second row is made up of one column. The third row is made up of another two columns.&lt;/p&gt; &lt;/td&gt; &lt;/tr&gt; &lt;/table&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070801232300, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3974, 3975, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2952', 1.0, 'Partners-Partnership_Program-FAQ', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;p&gt;&lt;style type=\"text/css\"&gt; html body .faq-question { padding-bottom: 0; margin-bottom: 0; color: #ffffff; } body .faq-chart { width: 100%; } body .faq-chart td { border: 2px solid #141B24; background: #1C2633; } html body .faq-chdr { color: #FFFFFF; background: #304256; } html body .faq-chnp { background: #141B24; } &lt;/style&gt;&lt;/p&gt; &lt;h1&gt;Sections&lt;/h1&gt; &lt;ul&gt; &lt;li&gt;&lt;a href=\"#liferay-partner-fee\"&gt;Liferay Partner Fee&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#liferay-international-partners\"&gt;Liferay International Partners&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#liferay-annual-certification-requirement\"&gt;Liferay Annual Certification Requirements&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#liferay-training\"&gt;Liferay Training&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#revenue-benefits-for-consulting-and-developing-services\"&gt;Revenue Benefits for Consulting and Developing Services&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#revenue-benefits-for-training-services\"&gt;Revenue Benefits for Training Services&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#revenue-benefits-for-liferay-support-contracts\"&gt;Revenue Benefits for Support Contracts&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#revenue-benifits-for-client-validation-service\"&gt;Revenue Benefits for Client Validation Service&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#becoming-a-liferay-international-partner\"&gt;Becoming A Liferay Partner&lt;/a&gt;&lt;/li&gt; &lt;li&gt;&lt;a href=\"#contacting-liferay\"&gt;Contacting Liferay&lt;/a&gt;&lt;/li&gt; &lt;/ul&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt; &lt;p&gt;&lt;em&gt;Note: All program terms are subject to change without notice. &lt;/em&gt; &lt;a name=\"liferay-partner-fee\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Liferay Partner Fee&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: How much does it cost to become a Liferay International Partner?&lt;/p&gt; &lt;p&gt;A: Partnership fees vary with the partnership level. Please &lt;a href=\"mailto:sales@liferay.com?subject=Partnership%20Program%20Details\"&gt;email us&lt;/a&gt; for current details.&lt;/p&gt; &lt;p&gt;&lt;a name=\"liferay-international-partners\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Liferay International Partners&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: Can there be more than one Premier Partner per region?&lt;/p&gt; &lt;p&gt;A: Liferay selects only one Premier Partner per region.&lt;/p&gt; &lt;p class=\"faq-question\"&gt;Q: How many Preferred and Services Partners are selected per region?&lt;/p&gt; &lt;p&gt;A: While there is no predetermined number, Liferay and their Premier Partners constantly monitor the respective regions to create an optimum business environment for all our partners to grow.&lt;/p&gt; &lt;p class=\"faq-question\"&gt;Q: What is the difference between Premier, Preferred, and Services Partners?&lt;/p&gt; &lt;p&gt;A: All partners work together with Liferay to deliver value and innovation to our clients.&lt;/p&gt; &lt;p&gt;The &lt;strong&gt;Services Partner&lt;/strong&gt; level is the basic partner level for professional services providers who want to signal to the marketplace that they have committed resources and experience using the Liferay Portal platform.&lt;/p&gt; &lt;p&gt;&lt;strong&gt;Preferred Partners&lt;/strong&gt; take that commitment a level further by working with Liferay to approach the market together in sales, marketing, and business development activities.&lt;/p&gt; &lt;p&gt;&lt;strong&gt;Premier Partners&lt;/strong&gt; have the highest commitment to and strongest relationship with Liferay and advise Liferay on strategic activity in specific regions.&lt;/p&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt; &lt;p class=\"faq-question\"&gt;Q: What is required to become a Premier Partne&lt;/p&gt; &lt;p&gt;A: Premier Partners must have been a Preferred Partner for at least one year and shown their investment in the Liferay platform during that time. Premier Partnership will be a natural result of a strong working relationship between a Preferred Partner and Liferay in a specific region.&lt;/p&gt; &lt;p&gt;&lt;a name=\"liferay-annual-certification-requirement\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Liferay Annual Certification Requirement&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: Once I pass Liferay\'s Skill Certification exam will I need to take another certification exam when it is time for me to enroll in the Annual Recertification Program?&lt;/p&gt; &lt;p&gt;A: Once you have passed the Liferay Skill Certification exam you will not need to retake it provided your certification is current.&amp;nbsp; Enrollment in the Annual Certification Program is required to keep your certification current.&lt;/p&gt; &lt;p class=\"faq-question\"&gt;Q: How many Liferay Certified developers do I need?&lt;/p&gt; &lt;p&gt;A: Certification commitments vary by Partner level. Please &lt;a href=\"mailto:sales@liferay.com?subject=Partnership%20Program%20Details\"&gt;email us&lt;/a&gt; for current details.&lt;/p&gt; &lt;p&gt;&lt;a name=\"liferay-training\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Liferay Training&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: Who provides the training?&lt;/p&gt; &lt;p&gt;A: Premier, Preferred Partners, and Liferay provide training for clients in their respective territory.&amp;nbsp; Premier Partners along with Liferay provide training support for Preferred Partners. Liferay provides certification training for Premier and Preferred Partners.&lt;/p&gt; &lt;p&gt;&lt;a name=\"revenue-benefits-for-consulting-and-developing-services\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Revenue Benefits for Consulting and Development Services&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: What are the revenue benefits for consulting and development services?&lt;/p&gt; &lt;p&gt;A: For work that is referred to Liferay by the partner, the partner is entitled to 10% of the first three calendar months of scoped work. For work referred to the partner by Liferay, Liferay is entitled to 10% of the first three calendar months of scoped work.&lt;/p&gt; &lt;p&gt;&lt;a name=\"revenue-benefits-for-training-services\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Revenue Benefits for Training Services&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: What are the revenue benefits for training services?&lt;/p&gt; &lt;p&gt;A: Only Preferred and Premier partners are entitled to provide training services. Partners will receive 70-80% of training fees for all students.&lt;/p&gt; &lt;p&gt;&lt;a name=\"revenue-benefits-for-liferay-support-contracts\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Revenue Benefits for Liferay Support Contracts&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: What are the revenue benefits for Liferay Support Contracts?&lt;/p&gt; &lt;p&gt;A: The revenue benefit for Liferay Support Contracts are determined by the level of involvement.&lt;/p&gt; &lt;p&gt;&lt;strong&gt;Support Contract Revenue Benefits Highlight&lt;/strong&gt;&lt;/p&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt; &lt;table class=\"faq-chart\"&gt; &lt;tbody&gt; &lt;tr valign=\"top\"&gt; &lt;td width=\"40%\" class=\"faq-chnp\"&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;/td&gt; &lt;td width=\"20%\" class=\"faq-chdr\"&gt;&lt;strong&gt;Services Partner&lt;/strong&gt;&lt;/td&gt; &lt;td width=\"20%\" class=\"faq-chdr\"&gt;&lt;strong&gt;Preferred Partner&lt;/strong&gt;&lt;/td&gt; &lt;td width=\"20%\" class=\"faq-chdr\"&gt;&lt;strong&gt;Premier Partner&lt;/strong&gt;&lt;/td&gt; &lt;/tr&gt; &lt;tr valign=\"top\"&gt; &lt;td width=\"40%\" class=\"faq-chdr\"&gt;Partner sells Liferay Support Contract and provides Level 1 support&lt;/td&gt; &lt;td width=\"20%\"&gt;N/A&lt;/td&gt; &lt;td width=\"20%\" valign=\"middle\" align=\"left\"&gt;45% first year of contract, 35% thereafter&lt;/td&gt; &lt;td width=\"20%\" valign=\"middle\" align=\"left\"&gt;60% first year of contract, 50% thereafter&lt;/td&gt; &lt;/tr&gt; &lt;tr valign=\"top\"&gt; &lt;td width=\"40%\" class=\"faq-chdr\"&gt;Partner sells Liferay Support Contract but does not provide Level 1 support&lt;/td&gt; &lt;td width=\"20%\"&gt;30% first year of contract, 20% thereafter&lt;/td&gt; &lt;td width=\"20%\"&gt;30% first year of contract, 20% thereafter&lt;/td&gt; &lt;td width=\"20%\"&gt;40% first year of contract, 30% thereafter&lt;/td&gt; &lt;/tr&gt; &lt;/tbody&gt; &lt;/table&gt; &lt;p&gt;*Please contact Liferay for revenue benefit details. All submissions must adhere to Liferay\'s revenue benefit descriptions and are subject to Liferay approval.&lt;/p&gt; &lt;p class=\"faq-question\"&gt;Q: Who provides support services?&lt;/p&gt; &lt;p&gt;A: Preferred and Premier Partners provide Level 1 support and Liferay provides Level 2 and Level 3 support.&lt;/p&gt; &lt;p&gt;&lt;a name=\"revenue-benifits-for-client-validation-service\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Revenues Benefit for Client Validation Service&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: Who can sell Client Validation Service?&lt;/p&gt; &lt;p&gt;A: Premier and Preferred Partners are encouraged to offer Client Validation Service to enhance their services.&lt;/p&gt; &lt;p class=\"faq-question\"&gt;Q: What are the revenue benefits for Client Validation Service?&lt;/p&gt; &lt;p&gt;A: See chart below. Client Validation Service Benefit Highlight&lt;/p&gt; &lt;p&gt;&lt;strong&gt;Client Validation Service Benefits Highlight&lt;/strong&gt;&lt;/p&gt; &lt;p&gt;&amp;nbsp;&lt;/p&gt; &lt;table class=\"faq-chart\"&gt; &lt;tbody&gt; &lt;tr valign=\"top\"&gt; &lt;td width=\"50%\" class=\"faq-chnp\"&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;/td&gt; &lt;td width=\"25%\" class=\"faq-chdr\"&gt;&lt;strong&gt;Preferred Partner&lt;/strong&gt;&lt;/td&gt; &lt;td width=\"25%\" class=\"faq-chdr\"&gt;&lt;strong&gt;Premier Partner&lt;/strong&gt;&lt;/td&gt; &lt;/tr&gt; &lt;tr valign=\"top\"&gt; &lt;td width=\"50%\" class=\"faq-chdr\"&gt;&lt;p&gt;Premier Partner sells Client Validation Service&lt;/p&gt;&lt;/td&gt; &lt;td width=\"25%\"&gt;&lt;p&gt;10%&lt;/p&gt;&lt;/td&gt; &lt;td width=\"25%\"&gt;&lt;p&gt;10%&lt;/p&gt;&lt;/td&gt; &lt;/tr&gt; &lt;tr valign=\"top\"&gt; &lt;td width=\"50%\" class=\"faq-chdr\"&gt;&lt;p&gt;Preferred Partner sells Client Validation Service&lt;/p&gt;&lt;/td&gt; &lt;td width=\"25%\"&gt;&lt;p&gt;10%&lt;/p&gt;&lt;/td&gt; &lt;td width=\"25%\"&gt;&lt;p&gt;10%&lt;/p&gt;&lt;/td&gt; &lt;/tr&gt; &lt;/tbody&gt; &lt;/table&gt; &lt;p&gt;*Please contact Liferay for revenue benefit details. All submissions must adhere to Liferay\'s revenue benefit descriptions and are subject to Liferay approval.&lt;/p&gt; &lt;p&gt;&lt;a name=\"becoming-a-liferay-international-partner\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Becoming a Liferay International Partner&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: How do I become a Liferay International Partner?&lt;/p&gt; &lt;p&gt;A: To learn more about becoming a Liferay Partner visit Becoming a Liferay Partner and Requirements and Benefits Description.&amp;nbsp; Please contact Liferay at &lt;a href=\"mailto:sales@liferay.com\"&gt;sales@liferay.com&lt;/a&gt; for additional inquiries.&lt;/p&gt; &lt;p&gt;&lt;a name=\"contacting-liferay\"&gt;&lt;/a&gt;&lt;/p&gt; &lt;h2&gt;Contacting Liferay&lt;/h2&gt; &lt;p class=\"faq-question\"&gt;Q: How do I contact Liferay?&lt;/p&gt; &lt;p&gt;A: Liferay can be contacted at &lt;a href=\"mailto:sales@liferay.com\"&gt;sales@liferay.com&lt;/a&gt;.&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070730221800, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3976, 3977, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2958', 1.0, 'Partners-Partnership Program-Requirements_Benefits', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<p><em>Note: All program terms are subject to change without notice.</em></p> <h2>Program Fee</h2> <p>Continuing Liferay\'s reputation of being a high value, cost-effective industry leader, our International Partner Program carries modest annual partnership fees to ensure the quality and commitment of our partner team.</p> <h2>Liferay Partner Training Program</h2> <p>The Liferay Partner Training Program is designed to equip our Partners with the necessary expertise to effectively implement Liferay products. Liferay\'s courses are delivered from the people who designed our innovative products.</p> <p>Vendors seeking to become Premier and Preferred Partners must enroll a minimum of five developers into the Liferay Partner Training Program.</p> <h2>Liferay Skill Certification</h2> <p>Upon completion of Liferay Partner Training Program, enrolled developers must successfully pass a Liferay certification examination.&nbsp; Successful completion of Liferay Skill Certification will provide your developers with the skills to implement Liferay products in mission critical projects.&nbsp; No further certification exam is necessary after successful completion of Liferay Skill Certification.&nbsp; Partners must maintain a minimum of five Liferay certified developers on staff.</p> <h2>Annual Recertification Requirements</h2> <p>At Liferay we are constantly developing new technologies so that you and your clients can stay ahead of the competition.&nbsp; Annual recertification is designed to keep your company at the forefront in Liferay product knowledge.</p> <p>Annual recertification is designed for developers who have already passed the Liferay Skill Certification exam to maintain extensive interaction with Liferay products. No further certification exam is necessary after successful completion of Liferay Skill Certification.&nbsp; Partners must maintain a minimum number of Liferay certified developers on staff depending on their partnership level.</p> <h2>Consultation and Development Services</h2> <p>Liferay Premier and Preferred Partners participate in promoting and providing consultation and development services. As Liferay Partners, your company will play a critical role in developing innovative and cost-effective solutions for clients. As an added benefit, Liferay Portal is issued under the MIT license, which means that you are free to rebundle Liferay products and resell it.&nbsp; In addition to traditional consulting and development services, you now have an additional revenue source by providing the same level of quality solutions that Liferay is known for.</p> <h2>Liferay Support Service</h2> <p>Clients demand fast and effective service when mission critical projects require additional support.&nbsp; For years, Liferay Support Services has provided the support that companies can count on.&nbsp; Liferay Premier and Preferred Partners actively engage in promoting support services.&nbsp; Premier Partners, selected by Liferay to oversee a specific territory, provide Level 1 support. Liferay stands ready to support our partners by providing Level 2 and Level 3 support services.</p> <h2>Liferay Training Service</h2> <p>Today\'s complex technology infrastructures mean that companies need their technical staff to effectively integrate different products.&nbsp; For years, Liferay Training Service has been providing the training that companies need.&nbsp; Liferay Premier and Preferred Partners actively engage in promoting and providing training services to respective clients.&nbsp; Premier Partners, selected by Liferay to oversee a specific territory, along with Liferay are the exclusive providers of Liferay Training Service for Liferay Preferred Partners in their respective regions.</p> <h2>Client Validation Service</h2> <p>Liferay\'s Client Validation Service creates value for our partners.&nbsp; Liferay Premier and Preferred Partners actively engage in promoting Client Validation Service.&nbsp; Liferay\'s Client Validation Service offers assurance to our clients that the technologies will be successfully integrated.&nbsp; Liferay\'s consultants fully evaluate each project submitted through the Client Validation Service and provide a complete assessment of our partners\' solutions.&nbsp; Clients will be issued a Liferay Client Validation Certificate to verify that the solutions are optimally configured.&nbsp; Clients will recognize the value that the Client Validation Service offers because they will save through reduced support issues.</p> <h2>Territory Assignment</h2> <p>Liferay International Partner Program is structured to enhance your company\'s ability to penetrate your market.</p> <ul> <li>Premier Partners are carefully selected by Liferay to stand as the lead provider of sales, strategic marketing, consulting, training, and Level 1 support for Liferay products in their respective territories.&nbsp; Only one Premier Partner for each territory will be appointed by Liferay.&nbsp;Premier Partners have a regional presence and have made significant commitment to Liferay products and training.</li> <li>Preferred Partners engage in sales and consulting services for Liferay products in their respective territories.&nbsp; Fully supported by Liferay and Liferay\'s appointed Premier Partner, the Preferred Partner Program is ideal for vendors that have extensive technical expertise but could benefit from the marketing, training, and support services offered by Liferay and Liferay Premier Partners.</li> </ul> <h2>Joint Business Plan and Marketing Development</h2> <p>Liferay Premier Partners, with the support of Liferay, develops the marketing and business plan for Liferay products in their respective territories.&nbsp; Creating a proper business plan and marketing strategy means that your company will be able to maximize your resources.&nbsp; Liferay stands ready to partner with you and lend our experience so that market opportunities are fully exploited.</p> <h2>Referrals</h2> <p>To further your success, lead referrals are an exclusive benefit that Liferay provides for Liferay Premier Partners.</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070731070600, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3978, 3979, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2966', 1.0, 'Community-Architecture-PortletAPI', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<p><strong>Portal API</strong></p><p>Liferay is designed to deploy portlets that adhere to the Portlet API (JSR-168). Many useful portlets are bundled with the portal (Mail, Document Library, Calendar, Message Boards, to name a few) and can be used as examples for adding your own custom portlets.</p> <p>Liferay also holds a seat on the JSR-286 committee with plans to fully move&nbsp; to this standard with version 5.0.</p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"date\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"type\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image\" type=\"image\">\n		<dynamic-content id=\"1855\">/image/journal/article?img_id=1757&amp;t=1199718541667</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"flow\" type=\"list\">\n		<dynamic-content><![CDATA[left]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-header-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-image-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-ARTICLE', 'IMAGE-FLOW', SPECIFIC_TIMESTAMP_20070730225500, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3980, 3981, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2971', 1.0, 'Community-Architecture-Struts & Tiles', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<div align=\"justify\"><p><strong>Struts &amp; Tiles</strong></p><p>All HTTP and WAP requests go through <a href=\"http://www.liferay.com/c/portal/layout?p_l_id=PUB.1.63\">MainServlet</a> which extends the basic Struts ActionServlet. <a href=\"http://jakarta.apache.org/struts/api/org/apache/struts/action/ActionServlet.html\">MainServlet </a>processes all requests and ensures that each request is routed to the proper <a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portal/struts/PortletAction.html\">PortletAction</a>. Refer to <a href=\"http://jakarta.apache.org/struts\">Struts</a> for a better understanding of how the portal\'s web framework functions.<br /><br />Layout information for the portal is managed with customizable templates. Refer to articles about <a href=\"http://www.javaworld.com/javaworld/jw-01-2002/jw-0104-tilestrut.html\">Tiles</a> to get a better understanding of how Tiles works as a layout manager.</p></div>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"date\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"type\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image\" type=\"image\">\n		<dynamic-content id=\"1856\">/image/journal/article?img_id=1758&amp;t=1200939964417</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"flow\" type=\"list\">\n		<dynamic-content><![CDATA[left]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-header-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-image-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-ARTICLE', 'IMAGE-FLOW', SPECIFIC_TIMESTAMP_20070731001600, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3982, 3983, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2974', 1.0, 'Community-Architecture-Spring&Hibernate', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<div><p><strong>EJB Spring Hibernate</strong></p><p>Liferay is no longer dependent on EJBs and can be deployed on a standalone servlet container. All business logic is concentrated inside POJO implementations that are looked up and instantiated by Spring. These implementations can be modified or enhanced via Spring\'s AOP and IOC capabilities.<br /><br />The enterprise release of the portal wraps the POJO implementations with Session EJBs to provide heavy scaling and transaction support required by large sites. The professional release of the portal calls the POJO implementations directly to provide a light weight facade.<br /><br />All data is persisted using Hibernate and is called through the POJO implementations. Liferay used to rely on CMP technology to achieve persistence, but switched over to Hibernate because of its raw speed and flexibility. Liferay is database agnostic and can run on a variety of popular&nbsp; <a href=\"@friendly_url_public@/guest/devzone/documentation\">databases</a>.<br /><br />Liferay uses JAAS Web security so that when a user logs in, their principal is propogated to the Servlet and EJB tiers. Remote Session EJBs can take advantage of this by checking security and permissions at the EJB level so it does not have be duplicated else where. Local Session EJBs exposes business logic to other Session EJBs and does not specifically check for security since they cannot be called remotely. Principals are also propagated to POJO implementations that are the base classes for Remote Session EJBs.<br /><br />The enterprise release uses Session EJBs which allows the deployer to separate the Web server, EJB server, and database server to achieve clustering at three levels. This is true n-tier deploying because no one is forced to cluster at any single layer and allows the most flexibility for large companies.<br /><br />Most of our EJBs, HBMs, and Models are generated through the ant task build-service which reads the file service.xml in /portal-ejb. Each portlet that persist data has its own service.xml (do a search in /portal-ejb and you will get a list back). We copy this file to /portal-ejb when we want to generate the persistence classes for that portlet. This is an internal tool that is built on top of the XDoclet engine.<br /><br />For example, upon reading service.xml found in the Bookmarks portlet, the following model classes are generated. Each model class reflects a table in the database. Never edit BookmarksEntryModel. Do edit BookmarksEntry to add hand massaged code. BookmarksEntry is generated once and extends BookmarksEntryModel. This allows us the ease of generated code and flexibility of hand massaged code.<br /><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/model/BookmarksEntry.html\">com.liferay.portlet.bookmarks.model.BookmarksEntry</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/model/BookmarksEntryModel.html\">com.liferay.portlet.bookmarks.model.BookmarksEntryModel</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/model/BookmarksFolder.html\">com.liferay.portlet.bookmarks.model.BookmarksFolder</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/model/BookmarksFolderModel.html\">com.liferay.portlet.bookmarks.model.BookmarksFolderModel</a><br /><br />Hibernate classes are generated that map to the model classes. This allows for an n-tier architecture for cases where your model classes are marshalled across the wire and your Hibernate classes are not.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/persistence/BookmarksEntryHBM.html\">com.liferay.portlet.bookmarks.service.persistence.BookmarksEntryHBM</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/persistence/BookmarksFolderHBM.html\">com.liferay.portlet.bookmarks.service.persistence.BookmarksFolderHBM</a><br /><br />Persistence methods to add, update, delete, find, remove, and count the Hibernate entries are generated as the default persistence mechaninsm.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/persistence/BookmarksEntryPersistence.html\">com.liferay.portlet.bookmarks.service.persistence.BookmarksEntryPersistence</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/persistence/BookmarksFolderPersistence.html\">com.liferay.portlet.bookmarks.service.persistence.BookmarksFolderPersistence</a><br /><br />Helper classes are generated that call the persistence methods. By default, the helper classes call the Hibernate persistence methods to update the database. You can override this in <a href=\"@friendly_url_public@/guest/devzone/documentation\">portal.properties</a> and set your own persistence class as long as it extends the default persistence class. This means you can customize where you store your data. It can be a traditional database, a LDAP server, or even something else.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/persistence/BookmarksEntryUtil.html\">com.liferay.portlet.bookmarks.service.persistence.BookmarksEntryUtil</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/persistence/BookmarksFolderUtil.html\">com.liferay.portlet.bookmarks.service.persistence.BookmarksFolderUtil</a><br /><br />Pooling classes are also created to minimize object creation. Behavior can be modified in <a href=\"@friendly_url_public@/guest/devzone/documentation\">portal.properties</a>.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/persistence/BookmarksEntryPool.html\">com.liferay.portlet.bookmarks.service.persistence.BookmarksEntryPool</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/persistence/BookmarksFolderPool.html\">com.liferay.portlet.bookmarks.service.persistence.BookmarksFolderPool</a><br /><br />POJO implementations that extend <a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portal/service/impl/PrincipalBean.html\">PrincipalBean</a> are generated to hold business logic that check the caller principal and can be called <strong>remotely</strong>. Calling getUserId() returns the user id of the current user. Calling getUser() returns the <a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portal/model/User.html\">User</a> model that represents the current user. The Session EJB that extends the POJO implementation implements <a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portal/service/impl/PrincipalSessionBean.html\">PrincipalSessionBean</a>.<br /><br />For example, these classes allow you to delete a bookmark entry or folder if and only if you are the creator of that entry or folder.<br /><br />These classes are only generated once if they do not already exist.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/impl/BookmarksEntryServiceImpl.html\">com.liferay.portlet.bookmarks.service.impl.BookmarksEntryServiceImpl</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/impl/BookmarksFolderServiceImpl.html\">com.liferay.portlet.bookmarks.service.impl.BookmarksFolderServiceImpl</a><br /><br />Helper classes are generated based on the POJO implementations. They help save developer time and prevent polluted code. Instead of writing many lines of code just to look up the appropriate Session EJB wrapper or POJO implementation, you simply call BookmarksEntryServiceUtil.addEntry to call the equivalent method in BookmarksEntryServiceImpl.addEntry.<br /><br />BookmarksEntryServiceUtil calls BookmarksFolderServiceFactory to look up the class that implements BookmarksEntryService. BookmarksFolderServiceFactory defers to Spring and settings in <a href=\"@friendly_url_public@/guest/devzone/documentation\">portal.properties</a> on whether to load the Session EJB wrapper or the plain POJO implementation. The Session EJB extends the POJO implementation.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksEntryServiceEJB.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksEntryServiceEJB</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksEntryServiceEJBImpl.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksEntryServiceEJBImpl</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksEntryServiceHome.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksEntryServiceHome</a><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksEntryService.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksEntryService</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksEntryServiceFactory.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksEntryServiceFactory</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksEntryServiceUtil.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksEntryServiceUtil</a><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksFolderServiceEJB.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksFolderServiceEJB</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksFolderServiceEJBImpl.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksFolderServiceEJBImpl</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksFolderServiceHome.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksFolderServiceHome</a><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksFolderService.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksFolderService</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksFolderServiceFactory.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksFolderServiceFactory</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksFolderServiceUtil.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksFolderServiceUtil</a><br /><br />Tunneling classes are generated so that developers can call the POJO implementations over port 80. An example of this given in the section V of this document.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/http/BookmarksEntryServiceHttp.html\">com.liferay.portlet.bookmarks.service.http.BookmarksEntryServiceHttp</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/http/BookmarksFolderServiceHttp.html\">com.liferay.portlet.bookmarks.service.http.BookmarksFolderServiceHttp</a><br /><br />Soap classes are generated so that developers can call the POJO implementations over port 80. Soap is slower than tunneling because tunneling streams requests in binary format. Soap is more flexible than tunneling because the client classes are not limited to Java.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/http/BookmarksEntryServiceSoap.html\">com.liferay.portlet.bookmarks.service.http.BookmarksEntryServiceSoap</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/http/BookmarksFolderServiceSoap.html\">com.liferay.portlet.bookmarks.service.http.BookmarksFolderServiceSoap</a><br /><br />POJO implementations classes that do <strong>not</strong> extend <a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portal/service/impl/PrincipalBean.html\">PrincipalBean</a> are generated to hold business logic that do <strong>not</strong> check the caller principal and can be called <strong>locally</strong>. These classes exist so that business logic can be easily integrated with other projects.<br /><br />These classes are only generated once if they do not already exist.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/impl/BookmarksEntryLocalServiceImpl.html\">com.liferay.portlet.bookmarks.service.impl.BookmarksEntryLocalServiceImpl</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/impl/BookmarksFolderLocalServiceImpl.html\">com.liferay.portlet.bookmarks.service.impl.BookmarksFolderLocalServiceImpl</a><br /><br />Helper classes are also generated.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksEntryLocalServiceEJB.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksEntryLocalServiceEJB</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksEntryLocalServiceEJBImpl.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksEntryLocalServiceEJBImpl</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksEntryLocalServiceHome.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksEntryLocalServiceHome</a><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksEntryLocalService.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksEntryLocalService</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksEntryLocalServiceFactory.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksEntryLocalServiceFactory</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksEntryLocalServiceUtil.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksEntryLocalServiceUtil</a><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksFolderLocalServiceEJB.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksFolderLocalServiceEJB</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksFolderLocalServiceEJBImpl.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksFolderLocalServiceEJBImpl</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksFolderLocalServiceHome.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksFolderLocalServiceHome</a><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksFolderLocalService.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksFolderLocalService</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksFolderLocalServiceFactory.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksFolderLocalServiceFactory</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksFolderLocalServiceUtil.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksFolderLocalServiceUtil</a><br /><br />Some of our users needed to call the Local Service classes remotely, so Remote Service classes that parallel their Local counterparts are also generated.<br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksEntryRemoteServiceEJB.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksEntryRemoteServiceEJB</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksEntryRemoteServiceEJBImpl.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksEntryRemoteServiceEJBImpl</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksEntryRemoteServiceHome.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksEntryRemoteServiceHome</a><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksEntryRemoteService.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksEntryRemoteService</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksEntryRemoteServiceFactory.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksEntryRemoteServiceFactory</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksEntryRemoteServiceUtil.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksEntryRemoteServiceUtil</a><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksFolderRemoteServiceEJB.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksFolderRemoteServiceEJB</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksFolderRemoteServiceEJBImpl.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksFolderRemoteServiceEJBImpl</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/ejb/BookmarksFolderRemoteServiceHome.html\">com.liferay.portlet.bookmarks.service.ejb.BookmarksFolderRemoteServiceHome</a><br /><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksFolderRemoteService.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksFolderRemoteService</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksFolderRemoteServiceFactory.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksFolderRemoteServiceFactory</a><br /><a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/bookmarks/service/spring/BookmarksFolderRemoteServiceUtil.html\">com.liferay.portlet.bookmarks.service.spring.BookmarksFolderRemoteServiceUtil</a><br /><br />A lot of people stay away from Session EJBs because they are heavy and require a lot of coding. Our build scripts show that you can leverage the advantages of Session EJBs while minimizing repetitive labor so that you can strike a good balance between effort and results.<br /><br />Spring gives Liferay additional flexibility. Developers can test their POJO implementations with Liferay Portal Professional in a servlet container and deploy to production with Liferay Portal Enterprise in an application server.</p></div>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"date\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"type\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image\" type=\"image\">\n		<dynamic-content id=\"1857\">/image/journal/article?img_id=1759&amp;t=1200643104199</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"flow\" type=\"list\">\n		<dynamic-content><![CDATA[left]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-header-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-image-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-ARTICLE', 'IMAGE-FLOW', SPECIFIC_TIMESTAMP_20070731003500, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3984, 3985, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2979', 1.0, 'Community-Architecture-SOAP, RMI & Tunneling', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;h2&gt;SOAP, RMI &amp;amp; Tunneling&lt;/h2&gt; &lt;p&gt;All of our remote POJO implementations are exposed to the external world via SOAP, RMI, and our custom tunneling classes.&lt;/p&gt; &lt;p&gt;We do not do this simply because Web services is a buzz word, but because we find it extremely useful for integration. The following is an example of a company that leverages these resources.&lt;/p&gt; &lt;p&gt;&lt;a href=\"http://www.3sixteen.com/\"&gt;3sixteen&lt;/a&gt; is a t-shirt company that needed to get up and running fast. In the fashion industry that relies on cutting-edge appearance and presentation, 3sixteen needed to keep its Flash MX front-end &amp;quot;brochure&amp;quot; website&amp;nbsp;that would not be used to sell clothing but to offer an experience through the music, graphics, and articles. Due to the static nature of Flash, the brochure site&amp;nbsp;was not an e-commerce solution because products would need to be added, removed, and updated on a constant basis. To solve this problem, they decided to separate their web presence into two sites: a brochure site and shopping site.&lt;/p&gt; &lt;p&gt;&lt;a href=\"http://www.3sixteen.com/\"&gt;www.3sixteen.com&lt;/a&gt; became the&amp;nbsp;graphic oriented&amp;nbsp;site built in Flash and &lt;a href=\"http://my.3sixteen.com/c/extranet/home\"&gt;my.3sixteen.com&lt;/a&gt; became the shopping site using the vanilla Liferay distribution. These two sites are hosted on two different Linux machines, and for all intents and purposes could have resided on different continents.&lt;/p&gt; &lt;p&gt;They also needed to build a mailing list to collect email addresses for all their interested customers. To accomplish this, they added a JSP pop up box on their Flash site that would tell the portal server to add the email address to a contact in the Address Book portlet.&lt;/p&gt; &lt;p&gt;The following is a JSP snippet that shows how the guys at 3sixteen leveraged &lt;a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/addressbook/service/http/ABContactServiceHttp.html\"&gt;ABContactServiceHttp&lt;/a&gt; to add a contact.&lt;/p&gt; &lt;p&gt;String URL = &amp;quot;http://my.3sixteen.com&amp;quot;; &lt;br /&gt; &lt;br /&gt; HttpPrincipal httpPrincipal = new HttpPrincipal(URL, &amp;quot;joe_bloggs&amp;quot;, &amp;quot;password&amp;quot;);&lt;br /&gt; &lt;br /&gt; ABContactServiceHttp.addContact(httpPrincipal, firstName, lastName, emailAddress); &lt;br /&gt; &lt;a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/addressbook/service/http/ABContactServiceHttp.html\"&gt;ABContactServiceHttp&lt;/a&gt; invoked &lt;em&gt;addContact&lt;/em&gt; in &lt;a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/addressbook/service/spring/ABContactServiceUtil.html\"&gt;ABContactServiceUtil&lt;/a&gt;. The invocation was sent over port 80 and received by http://my.3sixteen.com/tunnel/servlet/AuthTunnelServlet. The application server made sure the authentication matched and then processed ABContactServiceUtil as if the user with the id &lt;strong&gt;joe_bloggs&lt;/strong&gt; was calling &lt;em&gt;addContact&lt;/em&gt;. ABContactServiceUtil then called &lt;a href=\"http://content.liferay.com/4.0.0/docs/api/portal-ejb/com/liferay/portlet/addressbook/service/impl/ABContactServiceImpl.html\"&gt;ABContactServiceImpl&lt;/a&gt; to do the actual work. You can trace the logic of this by viewing the source included in the generated JavaDocs.&lt;br /&gt; &lt;br /&gt; Now Joe Bloggs can log into the portal and look in his Address Book portlet to see that he has a new contact. All of the included portlets have this capability because these helper classses are generated. This means you can write applets or any other Java application to access the Session EJBs that contain your business logic. This can be a security hazard if someone had your password, so you can configure to limit the Tunnel Servlet to only listen on certain ports by editing &lt;a href=\"@friendly_url_public@/guest/devzone/documentation\"&gt;portal.properties&lt;/a&gt;.&lt;br /&gt; &lt;br /&gt; You can also access the Session EJBs over SOAP and RMI. We will post more examples of that shortly.&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"header\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"date\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"type\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"image\" type=\"image\">\n    <dynamic-content id=\"1858\">/image/journal/article?img_id=1760</dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"header-link\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"flow\" type=\"list\">\n    <dynamic-content><![CDATA[left]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"open-header-link-in-new-window\" type=\"boolean\">\n    <dynamic-content><![CDATA[false]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"image-link\" type=\"text\">\n    <dynamic-content><![CDATA[]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"open-image-link-in-new-window\" type=\"boolean\">\n    <dynamic-content><![CDATA[false]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-ARTICLE', 'IMAGE-FLOW', SPECIFIC_TIMESTAMP_20070731005000, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3986, 3987, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2985', 1.0, 'PR-Liferay  4.0', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;div align=\"center\"&gt;&lt;font size=\"3\"&gt;&lt;strong&gt;New Liferay Portal 4.0 Extends&lt;/strong&gt;&lt;/font&gt;&lt;br /&gt;&lt;font size=\"3\"&gt;&lt;strong&gt;Greater Control To Administrators, Users&lt;/strong&gt;&lt;/font&gt;&lt;br /&gt;&lt;em&gt;Open Source Portal Development Tool Now Lets Admins Set Permissions At Portlet Level;&lt;/em&gt;&lt;br /&gt;&lt;em&gt;Private/Public Pages, Page-Level Theming and More Now Available&lt;br /&gt;&lt;strong&gt;&lt;br /&gt;&lt;br /&gt;&lt;/strong&gt;&lt;/em&gt;&lt;/div&gt;&lt;div align=\"justify\"&gt;&lt;strong&gt;LOS ANGELES, CA &amp;ndash; April 12, 2006&amp;ndash; &lt;/strong&gt;Liferay, a leading provider of enterprise-class open source software products, today unveiled Liferay Portal 4.0, a major new release of the leading open source Web portal that makes portal development and operation more versatile, secure and attractive than ever. Liferay Portal 4.0 has improvements that benefit administrators, portal creators, and end-users alike, from an improved security model to a new library of themes that can be implemented even at the page level.&lt;br /&gt;&lt;br /&gt;As an open source product, Liferay Portal is freely available under an MIT license. Suitable for even the most sophisticated Web portals, Liferay Portal supports virtually all types of middleware and database platforms including JBoss JEMS, BEA Platform, WebSphere, Oracle and others; it also conforms with the industry standard JSR-168 portal API for easy integration with existing portal elements.&lt;br /&gt;&lt;br /&gt;With Liferay Portal 4.0, Liferay has listened to its users to create enhancements that are practical, convenient and powerful. Among the improvements are:&lt;br /&gt;&lt;ul&gt;&lt;li&gt;&lt;strong&gt;Improved Security&lt;/strong&gt;&amp;mdash;Liferay Portal 4.0 supports user permissioning at the portlet level. Administrators can set or restrict access to portlets and portlet objects; they can also delegate permissioning authority to others. Access can be set at the individual, community or object levels, and temporary &amp;ldquo;guest&amp;rdquo; permissions can also be given;&lt;/li&gt;&lt;li&gt;&lt;strong&gt;Enterprise Taxonomy&lt;/strong&gt;&amp;mdash;Version 4.0 gives enterprises the ability to create separate portals or sub-portals according to corporate structure. For example, a division or branch office can have a portal within the corporate portal;&lt;/li&gt;&lt;li&gt;&lt;strong&gt;Velocity Template Support&lt;/strong&gt;&amp;mdash;Portal developers can enjoy the newly added convenience of support for templates generated by Velocity, the Java-based template engine, when creating or updating portals and constituent portlets/pages;&lt;/li&gt;&lt;li&gt;&lt;strong&gt;Private/Public Pages&lt;/strong&gt;&amp;mdash;Portal users can now post pages and objects with public and private viewing properties. A photography club, for example, can enable members to post pictures viewable by the general public, or on private pages viewable only by club members. Private pages require password/login;&lt;/li&gt;&lt;li&gt;&lt;strong&gt;Theme Enhancements&lt;/strong&gt;&amp;mdash;In addition to making a number of new graphical themes available, Liferay has made it possible for users to specify themes for individual pages or groups of pages;&lt;/li&gt;&lt;li&gt;&lt;strong&gt;JSR-170 Compliance&lt;/strong&gt;&amp;mdash;Version 4.0 introduces JSR-170 support to Liferay Portal via Jackrabbit integration, with the Document Library currently conforming to the standard.&lt;/li&gt;&lt;li&gt;&lt;strong&gt;More Documentation&lt;/strong&gt;&amp;mdash;Over 100 additional pages of instructions and other material are now available to make using Liferay Portal easier than ever.&lt;/li&gt;&lt;/ul&gt;&lt;br /&gt;In addition to these new features and tools, Liferay anticipates adding support for a workflow portlet that integrates with the jBPM workflow engine. That addition will be available in the very near future in Liferay Portal 4.0.1.&lt;br /&gt;&lt;br /&gt;&amp;ldquo;Convenience and flexibility have always been the hallmarks of Liferay Portal, and with Version 4.0 we&amp;rsquo;ve continued that tradition with the kinds of enhancements our users want,&amp;rdquo; said Brian Chan, founder and chief software architect of Liferay. &amp;ldquo;Whether you&amp;rsquo;re creating, using, or managing an enterprise portal, Liferay Portal 4.0 is an important and valuable update that is truly worth adopting.&amp;rdquo;&lt;br /&gt;&lt;br /&gt;Version 4.0 is available in both current editions of Liferay Portal&amp;mdash;Liferay Portal Professional, the standard product; and Liferay Portal Enterprise, which includes all Professional features plus the ability to cluster portlet transactions across multiple servers. The product is available for immediate download in both editions.&lt;br /&gt;&lt;br /&gt;To download either product, or for more information about Liferay and Liferay Portal 4.0, go to www.liferay.com.&lt;/div&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;div align=\"justify\"&gt;&lt;em&gt;&lt;font size=\"1\"&gt;&lt;strong&gt;About Liferay:&lt;/strong&gt;&lt;/font&gt;&lt;/em&gt;&lt;br /&gt;&lt;em&gt;&lt;font size=\"1\"&gt;Liferay is a leading provider of high-value, open source software products that help firms optimize their business, increase productivity, and differentiate themselves from the competition. The company&amp;rsquo;s flagship product, Liferay Portal, is an enterprise-class portal development tool designed to handle the most demanding applications while ensuring scalability and robust performance for firms across all industries. Liferay monetizes its business by offering expert support, training, and professional services for its no-cost products; its corporate philosophy emphasizes making a difference in the world by&lt;/font&gt;&lt;/em&gt;&lt;br /&gt;&lt;em&gt;&lt;font size=\"1\"&gt;supporting respected non-profit organizations such as Samaritan&amp;rsquo;s Purse and World Vision through regular financial support. The privately-held company is headquartered in Los Angeles. For more information about Liferay, please visit www.Liferay.com.&lt;/font&gt;&lt;/em&gt;&lt;/div&gt;&lt;p&gt;# # # #&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070731195200, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3988, 3989, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2989', 1.0, 'PR-Liferay 4.2', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;div align=\"center\"&gt;&lt;font size=\"2\"&gt;&lt;strong&gt;Liferay Unveils Liferay Portal 4.2&lt;/strong&gt;&lt;/font&gt;&lt;br /&gt;&lt;em&gt;New Features Simplify Enterprise Integration, Enhance Content Management and User Experience&lt;/em&gt;&lt;/div&gt;&lt;p&gt;&lt;strong&gt;&lt;br /&gt;&lt;/strong&gt;&lt;/p&gt;&lt;div align=\"justify\"&gt;&lt;strong&gt;LOS ANGELES, CA &lt;/strong&gt;&amp;ndash; December 13, 2006 &amp;ndash; Liferay, provider of the world&amp;rsquo;s leading enterprise-class, open source portal framework, today announced the release of Liferay Portal 4.2. The latest version offers added back-end enhancements to workflow and content management via integration with ServiceMix JBI, jBPM workflow engine, and Alfresco, as well as new features such as chat and other user-interface enhancements that further improve end-user experience and collaboration.&lt;br /&gt;&lt;br /&gt;New upgrades in Version 4.2 include integration with the ServiceMix JBI (Java Business Integration) engine, which acts as a single point of connection for disparate enterprise applications. ServiceMix simplifies the integration, upgrade and substitution of siloed applications such as CRM, ERP, and ECM, reducing development time and lowering maintenance costs.&lt;br /&gt;&lt;br /&gt;Other new features of Liferay Portal 4.2 include:&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;li&gt;jBPM workflow engine &amp;ndash; allows developers to quickly change application workflow to reflect evolving business processes;&lt;/li&gt;&lt;li&gt;Liferay Chat &amp;ndash; a secure, portal-based instant messaging solution with an intuitive AJAX/Web 2.0 interface;&lt;/li&gt;&lt;li&gt;Alfresco integration &amp;ndash; allows users to manage enterprise content directly through the Liferay Portal environment;&lt;/li&gt;&lt;li&gt;A new parallel portlet rendering engine &amp;ndash; reduces end-user wait time by allowing portlets to load independently.&lt;/li&gt;&lt;/ul&gt;&lt;br /&gt;&amp;ldquo;Version 4.2&amp;rsquo;s improvements essentially allow an organization to be more nimble,&amp;rdquo; said Brian Chan, founder and Chief Software Architect of Liferay. &amp;ldquo;The back-end enhancements help an organization to connect enterprise resources and to react faster to a changing business environment. Meanwhile, strong usability on the front-end maximizes the productivity gains of portal users&amp;mdash;a good user experience is key to capturing the highest return on an enterprise\'s portal investment.&amp;rdquo;&lt;br /&gt;&lt;br /&gt;Liferay Portal, available at no cost under the MIT license, supports all major middleware and database platforms including JBoss JEMS, BEA Platform, WebSphere, Tomcat, Oracle, mySQL and SQLServer. It also complies with major standards such as JSR-168 and JSR-170.&lt;br /&gt;&lt;br /&gt;Version 4.2 is available in both current editions of Liferay Portal&amp;mdash;Liferay Portal Professional, the standard product; and Liferay Portal Enterprise, which includes all Professional features plus the ability to cluster portlet transactions across multiple servers. The product is available for immediate download in both editions at www.liferay.com/web/guest/downloads. A full list of features is available at www.liferay.com.&lt;br /&gt;&lt;br /&gt;&lt;em&gt;&lt;strong&gt;About Liferay:&lt;/strong&gt;&lt;/em&gt;&lt;br /&gt;&lt;em&gt;Liferay Portal is the world&amp;rsquo;s leading enterprise open source portal framework with Fortune 500 clients in the U.S. and Europe. Liferay Professional Services offers technical support, custom development and professional training to ensure successful deployment of its flagship product in the most demanding IT environments. The company is headquartered in Los Angeles. For more information, please visit www.liferay.com.&lt;/em&gt;&lt;/div&gt;&lt;p&gt;&lt;em&gt;&lt;br /&gt;&lt;/em&gt;&lt;/p&gt;&lt;div align=\"center\"&gt;###&lt;/div&gt;&lt;p&gt;&lt;br /&gt;&lt;strong&gt;Contact:&lt;/strong&gt;&lt;br /&gt;S&amp;amp;S Public Relations, Inc.&lt;br /&gt;Corey Gutwillig, 847-955-0700 ext. 9333&lt;br /&gt;cgutwillig@sspr.com&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070731202000, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (3990, 3991, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2993', 1.0, 'PR-Alfresco', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content language-id=\"en_US\">&lt;div align=\"center\"&gt;&lt;font size=\"3\"&gt;&lt;strong&gt;Liferay Teams With Alfresco And Virtuas To Provide&lt;br /&gt;Performance, Value, Choice in Enterprise Software&lt;/strong&gt;&lt;/font&gt;&lt;br /&gt;&lt;em&gt;Companies Work to Integrate Liferay Portal with Alfresco Enterprise Content Management&lt;br /&gt;and with Geronimo Application Server; Results to be Showcased at JavaOne&lt;br /&gt;&lt;strong&gt;&lt;br /&gt;&lt;br /&gt;&lt;/strong&gt;&lt;/em&gt;&lt;/div&gt;&lt;div align=\"justify\"&gt;&lt;strong&gt;MOSCONE CENTER / BOOTH 935, SAN FRANCISCO &amp;ndash; MAY 16, 2006 &amp;ndash; &lt;/strong&gt;Liferay, provider of the leading enterprise-class open source portal platform, today announced the pending release of a bundled version of Liferay Portal with Alfresco Enterprise Content Management Suite, as well as a new partnership with Colorado-based Virtuas Open Source Solutions, LLC, to certify Liferay Portal on the Geronimo open-source application server. With these two initiatives, enterprises will have more choices&lt;br /&gt;and derive even greater performance and value when using Liferay for their portal installations.&lt;br /&gt;&lt;br /&gt;The announcement, made at Sun Microsystems&amp;rsquo; 2006 JavaOne Conference in San Francisco, comes on the heels of the release of Liferay Portal 4.0, which will be in final release by the end of May.&lt;/div&gt;&lt;p&gt;&lt;br /&gt;&lt;strong&gt;&lt;br /&gt;&lt;/strong&gt;&lt;/p&gt;&lt;div align=\"justify\"&gt;&lt;strong&gt;An Excellent Portal-Based ECM Solution&lt;/strong&gt;&lt;br /&gt;Alfresco is the leading open source alternative for Enterprise Content Management (ECM) available today. Started by Documentum co-founder John Newton and former COO of Business Objects John Powell, Alfresco is built with a full SOA (Service Oriented Architecture) using many of the same technologies and standards as Liferay Portal, including Spring, Hibernate, Lucene, MyFaces, JSR 168, JSR 170, and Web Services. Features include a sophisticated administration system, CIFS and WebDAV support, drag and drop content folders, taxonomy, library services, automatic content transformation (filetype&lt;br /&gt;conversion), and customizable workflow while delivering unparalleled performance and scalability with support for clustering and failover.&lt;br /&gt;&lt;br /&gt;&amp;ldquo;The Liferay Portal&amp;ndash;Alfresco bundle delivers a tightly integrated Portal + ECM solution that leverages the synergy between the two products,&amp;rdquo; said Brian Chan, founder and chief software architect of Liferay. &amp;ldquo;Liferay Portal and Alfresco share important features that provide for close cooperation between the two: J2EE, SOA, high availability and scalability, security and SSO, and fine-grained permissions. And Liferay\'s web publishing, delegable administration, themes, bundled portlets, and 700+&lt;br /&gt;deployment configurations provide Alfresco&amp;rsquo;s excellent ECM feature set with a portal platform that amplifies its value.&amp;rdquo;&lt;br /&gt;&lt;br /&gt;&amp;ldquo;We are committed to providing customers with a choice when deploying their Alfresco solutions in a portal environment, &amp;ldquo; said John Newton, founder and CTO of Alfresco. &amp;ldquo;Both Alfresco and Liferay are dedicated to open source and open standards allowing customers maximum flexibility. An Alfresco-Liferay bundle is an out-of-the-box solution alleviating the need for customers to have to integrate the two solutions on their own.&amp;rdquo;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Another Application Server Alternative&lt;/strong&gt;&lt;br /&gt;In addition, Liferay has teamed with Virtuas to optimize Liferay&amp;rsquo;s compatibility with the Apache Geronimo open-source application server. Virtuas, founded in 1999, is led by a team of experienced opensource leaders that have committed code to various Apache Software Foundation projects, authored books on open-source technologies, and worked in leading open source roles for organizations such as VA Linux and HP. As the premier provider of professional services and support surrounding Apache Geronimo and other leading open-source technologies, Virtuas&amp;rsquo; expertise is enabling seamless integration of Liferay and Geronimo. Liferay has also joined Virtuas, IBM, AMD, MySQL, Alfresco and others in co-sponsoring Geronimo Live!, an event at JavaOne showcasing the support and synergy forming around Geronimo.&lt;br /&gt;&lt;br /&gt;&amp;ldquo;We see a lot of potential for Geronimo and want to be sure that Liferay stays ahead of the curve with the best of the new technologies out there,&amp;rdquo; Chan said. &amp;ldquo;Virtuas&amp;rsquo; team has an intimate knowledge of Geronimo and they strongly believe Liferay is the best open source portal available, so we naturally partnered with them to get the two products working together. The fact that Liferay and Alfresco are supporting Virtuas&amp;rsquo; Geronimo Live! event shows the synergy is just going to get stronger among these products.&amp;rdquo;&lt;br /&gt;&lt;br /&gt;&amp;ldquo;As customers move towards open source &amp;lsquo;up the stack&amp;rsquo; at the application level, the need for &amp;lsquo;best-of-breed&amp;rsquo; partnerships to integrate these applications with open source middleware will continue to grow,&amp;rdquo; said Matt Filios, Founder and President of Virtuas. &amp;ldquo;Virtuas is pleased to work with Liferay and Alfresco to offer enterprise customers with such a trusted end-to-end open source solution.&amp;rdquo;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;New Capabilities&lt;/strong&gt;&lt;br /&gt;Liferay Portal continues to offer the best combination of performance and value in the portal market. On top of its enterprise-class service-oriented architecture (SOA) with high availability, clustering and failover capabilities, Liferay Portal offers compatibility with JBoss JEMS, BEA WebLogic Platform, IBM WebSphere, Oracle and more to help protect organizations&amp;rsquo; IT investments. An integrated web publishing CMS, large number of bundled portlets, EJB or servlet flexibility, liberal open source license, and standards compliance round out the package to deliver tremendous value.&lt;br /&gt;&lt;br /&gt;Version 4.0 of Liferay Portal adds enhanced security including portlet-level permissioning, enterprise-level taxonomies, public/private pages, full-page caching for static content, and real-time style editing. Further enhancements across the board include performance improvements, improved user experience and a refactored portlet suite. Version 4.0 also supports JSR-170 via Jackrabbit integration.&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Now On Display&lt;/strong&gt;&lt;br /&gt;Liferay&amp;rsquo;s new Liferay Portal&amp;ndash;Alfresco bundle will be available shortly in both the Professional and Enterprise editions of Liferay Portal. The Liferay Apache Geronimo certification is also expected to be completed by the end of May.&lt;br /&gt;&lt;br /&gt;Prototypes of the Liferay Portal&amp;ndash;Alfresco integration, as well as an early release of Liferay running on Geronimo, will be demonstrated at JavaOne at the Liferay exhibit, Booth 935 at Moscone Center. For more information, including download options, go to &lt;a href=\"http://www.liferay.com\"&gt;www.liferay.com&lt;/a&gt;.&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;font size=\"1\"&gt;&lt;em&gt;&lt;strong&gt;About Virtuas Open Source Solutions, LLC&lt;/strong&gt;&lt;/em&gt;&lt;/font&gt;&lt;br /&gt;&lt;font size=\"1\"&gt;&lt;em&gt;Virtuas Open Source Solutions is the premier provider of enterprise-level support services for widelyadopted open source software such as Apache Geronimo, Tomcat, Spring and Hibernate. Since 1999, Virtuas has provided the Global 2000 with world-class enterprise software solutions. For more information about Virtuas, please visit www.virtuas.com.&lt;/em&gt;&lt;/font&gt;&lt;br /&gt;&lt;br /&gt;&lt;font size=\"1\"&gt;&lt;em&gt;&lt;strong&gt;About Alfresco&lt;/strong&gt;&lt;/em&gt;&lt;/font&gt;&lt;br /&gt;&lt;font size=\"1\"&gt;&lt;em&gt;Alfresco Software Inc. is the leading open source alternative for enterprise content management. It is the first company to bring the power of open source to the enterprise content management market, enabling unprecedented scale and a much lower total cost of ownership than proprietary systems. Founded by a team of content management veterans that includes the co-founder of Documentum, John Newton, and former COO of Business Objects, John Powell, Alfresco is based in London. For more information, visit www.alfresco.com.&lt;/em&gt;&lt;/font&gt;&lt;br /&gt;&lt;br /&gt;&lt;font size=\"1\"&gt;&lt;em&gt;&lt;strong&gt;About Liferay&lt;/strong&gt;&lt;/em&gt;&lt;/font&gt;&lt;br /&gt;&lt;font size=\"1\"&gt;&lt;em&gt;Liferay is leading innovation, performance and value in the portal market with Version 4.0 of Liferay Portal, which features an Enterprise Service Oriented Architecture, liberal open source license, and integration with the best of today&amp;rsquo;s technologies. To complement this offering Liferay provides a full suite of 24/7 support, product training, and professional consulting services. Liferay&amp;rsquo;s corporate philosophy emphasizes a philanthropic spirit through work with and financial support of respected non-profit organizations such as Goodwill Industries and World Vision. The privately-held company is headquartered in Los Angeles. For more information about Liferay, please visit www.liferay.com.&lt;/em&gt;&lt;/font&gt;&lt;/div&gt;&lt;div align=\"center\"&gt;&lt;font size=\"1\"&gt;&lt;em&gt;# # # #&lt;/em&gt;&lt;/font&gt;&lt;/div&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070731202300, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
