insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4056, 4057, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3099', 1.0, 'Products-Collaboration_Main', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"date\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"type\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<h1>Features</h1> <p>Take advantage of a virtualized work environment with Liferay&rsquo;s built in Collaboration Suite. This collection of collaboration tools comes prebundled with Liferay Portal.</p><ul><li><strong>Mail </strong>&ndash; Send mail directly from your portal. A full AJAX-based webmail client can be configured to interface with many popular IMAP email servers. <br /><ul><li><strong>Microformat Support</strong> &ndash; Transfer your calendar and user information via Web 2.0 standards. Liferay portlets offer data in microformats (hCard, hCalendar, etc) to be easily used by and integrated with third-party applications (Yahoo, Technorati, etc.).</li></ul></li><li><strong>Shared Calendar</strong> &ndash; Community-based calendars with task list can be used to keep track of group-based events.<ul><li><strong>Calendaring Support </strong>&ndash;&nbsp; Import and export&nbsp; events from&nbsp; the Calendar Portlet. Liferay supports iCal and Exchange.</li></ul></li><li><strong>Blogs </strong>&ndash; Features include RSS support, threaded user and guest comments, email notifications of blog replies, tags and labels, an entry rating system, and social bookmarking links, . <ul><li>A <b>Blog Aggregator </b>portlet groups together all blog entries in the entire portal or in a particular organization.</li><li>Check out Liferay\'s own <a target=\"_blank\" href=\"http://www.liferay.com/web/guest/community/blog\">Blogs</a>!</li></ul></li><li><strong>Message Boards</strong> &ndash;&nbsp; Includes support for sticky posts, statistics, recent posts, RSS, email-based subscriptions, avatars, threads management (move to another category), and full fine-grained permissions.<ul><li><a href=\"http://www.liferay.com/web/guest/community/forums\" target=\"_blank\">See our Message Boards in action!</a></li></ul></li><li><strong>Enterprise Instant Messenger </strong>&ndash; AJAX enabled Jabber client allows users to add &ldquo;buddies&rdquo; based on existing portal user accounts. Chat windows persist across portal pages.</li><li><strong>RSS </strong>&ndash; Subscribe to frequently read RSS feeds from message boards and blogs within the portal.</li><li><strong>Wikis </strong>&ndash; Collaborate on collective knowledge with Liferay&rsquo;s built-in Wiki, with versioning and reversion capabilities.</li><li><strong>Tagging </strong>&ndash; Use tagging to dynamically share important or interesting content with other portal users.</li></ul><p>&nbsp;</p><p style=\"text-align: right;\"><b>See </b><a href=\"http://www.liferay.com/c/document_library/get_file?p_l_id=136815&amp;folderId=167862&amp;name=DLFE-314.pdf\" target=\"_blank\"><b>Full Portlet List</b></a></p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"flow\" type=\"list\">\n		