insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4098, 4099, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3143', 1.0, 'Products-Technical_Specs-Supported_Tech-ICEfaces', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[ICEfaces]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"date\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"type\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image\" type=\"image\">\n		<dynamic-content id=\"1866\">/image/journal/article?img_id=1767&amp;t=1201497897613</dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<p><span id=\"10097_13343_1.0\" class=\"journal-content-article\">ICEfaces extends JavaServer&trade; Faces (JSF), providing transparent Ajax capabilities while preserving the server-side Java EE programming model, and eliminating the need for low-level JavaScript development. Integration of ICEfaces with Liferay Portal allows enterprise developers to draw on the power of Ajax technology to provide visually rich, highly responsive experiences for users.</span></p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"header-link\" type=\"text\">\n		<dynamic-content><![CDATA[http://www.icefaces.org/main/home/index.jsp]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"flow\" type=\"list\">\n		<dynamic-content><![CDATA[default]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-header-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"image-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"open-image-link-in-new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-ARTICLE', 'BASIC-IMAGE-TEXT', SPECIFIC_TIMESTAMP_20070731204900, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4100, 4101, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3147', 1.0, 'Products-Portal_Main', 'Content for the top products page.', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root default-locale=\"en_US\" available-locales=\"en_US\">\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content language-id=\"en_US\"><![CDATA[<h2>Key Business Benefits:</h2> <ul>     <li><a href=\"#Provide%20an%20intuitive%20and%20collaborative%20user%20experience.\">Provide an intuitive and collaborative user experience.</a></li>     <li><a href=\"#Consolidate,%20organize%20and%20access%20all%20your%20data%20and%20applications%20via%20a%20single%20point%20of%20access.\">Consolidate, organize and access all your data and applications via a single point of access.</a></li>     <li><a href=\"#Optimize%20existing%20IT%20investments.\">Optimize existing IT investments.</a></li>     <li><a href=\"#Adapt%20to%20the%20demands%20of%20a%20changing%20market.\">Adapt to the demands of a changing market.</a></li>     <li><a href=\"#Scale%20your%20business%20without%20worrying%20about%20your%20IT.\">Scale your business without worrying about your IT.</a></li>     <li><a href=\"#Gain%20the%20lowest%20TCO\">Gain the lowest TCO.</a></li> </ul>  <p>&nbsp;</p><p><a href=\"@root_path@/web/guest/downloads\"><b>Download Liferay Portal</b></a></p><p><a href=\"http://www.liferay.com/web/guest/products/use_cases\"><b>How do people use Liferay Portal?</b></a></p> <h2><a name=\"Provide an intuitive and collaborative user experience.\"></a>Provide an intuitive and collaborative user experience.</h2> <p>Maximize the productivity gains of portal users&mdash;a good user experience is key to capturing the highest return on an enterprise\'s portal investment</p> <ul type=\"disc\">     <li>Customize your user experience.</li>     <li>Use one of the 20+ out-of-the-box themes to change the look and feel of the portal without dealing with complex code.</li>     <li>All users are given their own pages with a user-defined friendly URL. This option gives users a better sense of ownership over the technology, thus enhancing their experience and generating user loyalty.</li>     <li>Collaborative tools such as Instant Messaging, Message Boards, Blogs &amp; Wikis allow you to create true communities of users.</li> </ul> <h2><a name=\"Consolidate, organize and access all your data and applications via a single point of access.\"></a>Consolidate, organize and access all your data and applications via a single point of access.</h2> <p>Liferay portal provides you a single point of access to all your organization&rsquo;s data, content, and information from both existing in-house applications (i.e., HR, CRM) and external sources. Single sign-on lets users log in once to access all their information needs.</p> <p>After signing in, our fine-grained permissioning system allows you to customize and control who can access sensitive information and functionality.</p> <p>Users get an intuitive front-end while our support of ServiceMix technology simplifies the integration, upgrade, and substitution of disparate applications for the developer behind the scenes .</p> <h2><a name=\"Optimize existing IT investments.\"></a>Optimize existing IT investments.</h2> <p>Leverage your existing IT environment.</p> <p>Liferay Portal is the only open source portal that works with any application server, database server, or operating system with over <a href=\"@root_path@/web/guest/products/tech_specs/deployment_matrix\">700 deployment configurations!</a></p> <p>Be assured that existing technology investments are not jettisoned and that future changes will not require an overhaul of an existing Liferay Portal installation.</p> <h2><a name=\"Adapt to the demands of a changing market. \"></a>Adapt to the demands of a changing market.</h2> <p>Liferay&rsquo;s workflow engine allows organizations of all sizes to be more nimble. Flexible IT makes business processes more dynamic, modular, and adaptable to the demands of fast-changing markets.</p> <p>No matter how your processes change, Liferay was benchmarked as among the most secure portal platforms on the market using LogicLibrary\'s Logiscan suite, so you can<span style=\"font-weight: normal; font-family: Arial;\"> be always confident in the security of your data.</span></p> <h2><a name=\"Scale your business without worrying about your IT.\"></a>Scale your business without worrying about your IT.</h2> <p>Liferay grows with your organization.</p> <p>Because our technology is among the few that allows for clustering (the addition of hardware to meet growing usage demands) Liferay Portal&rsquo;s capacity for content and applications is limitless!</p> <p>Liferay Portal also accommodates today&rsquo;s global business environment with out of the box support for 22 languages.</p> <p>Add Liferay&rsquo;s language portlet to any page and allow end-users to quickly select a different localization with one click.</p> <h2><a name=\"Gain the lowest TCO\"></a>Gain the lowest TCO.</h2> <p>Liferay Portal provides the highest value and lowest costs at every level.</p> <p>Open source products promise more freedom and flexibility, but not all open source licenses are the same.</p> <p>Liferay Portal is offered under the MIT License, the most liberal and business friendly license available with absolutely no license fees or reciprocity clauses.</p> <blockquote> <u>Hardware and Software</u>&ndash; Liferay Portal can be deployed on all major application servers, databases and operating systems, reaping these additional value benefits: <ul type=\"disc\" style=\"margin-top: 0in;\">     <li>Avoid &ldquo;vendor lock-in&rdquo; or dependence on a single third-party.</li>     <li>Eliminate the incidental cost of new database or application server licenses a proprietary portal solution might require.</li>     <li>Leverage existing in-house expertise with no need to hire or train IT staff on new hardware / software.</li> </ul> <p><u>Implementation </u>&ndash; Liferay Portal&rsquo;s included <a href=\"@root_path@/web/guest/products/portal/portlets\">portlet suite (over 60)</a> and built-in web publishing and content management system (CMS) reduce or eliminate the initial cost of custom development.</p> <p>Should development be needed, the MIT license allows in-house development teams to customize the portal instead of using third party developers.</p> <p>Training &amp; support are available when needed. Because Liferay Portal is standards-based, developer familiarity with the key technologies in Liferay&rsquo;s architecture shortens the development cycle.</p> <p><u>Maintenance</u>&ndash; Liferay Portal is supported by a wide range of 24/7 business critical support offerings. Choose the level of support that meets your needs.</p> </blockquote> <p>&nbsp;</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070723190500, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
