insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4104, 4105, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3152', 1.0, 'Services - Training - Kuala Lumpur - Page Header', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"page-name\" type=\"text\">\n		<dynamic-content><![CDATA[Kuala Lumpur, Malaysia]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"page-description\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'PAGE-HEADER', 'PAGE-HEADER', SPECIFIC_TIMESTAMP_20070809170400, TRUE, 2, 'Joe Bloggs', FALSE, FALSE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4106, 4107, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3154', 1.0, 'Services-Training-Kuala_Lumpur', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"sidebar-location\" type=\"list\">\n		<dynamic-content><![CDATA[right]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"sidebar-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Date</h2><p><!--March 3-6 (Mon-Thu)-->TBD<br />9am-5pm daily</p><h2>Cost</h2><p>USD $2700</p><h2>Location</h2><p>TBA<!--<a href=\"#link_here\">iTrain Training Center</a><br />\nWisma MCIS Annexe, Jalan Barat<br />\n46200 Petaling Jaya, Selangor Darul Ehsan, Malaysia<br />\n(Next to Hilton Hotel Petaling Jaya)--></p><h2>Additional Information</h2><p>A laptop is required for this course.</p><h2>Topics</h2><p><a href=\"@root_path@/web/guest/services/training/course_topics\">Course Topics</a></p><h2>How to Register</h2><p>Email <a href=\"mailto:sales@liferay.com\">sales@liferay.com</a> to register.</p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"main-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Overview</h2><p>Liferay will host a public training seminar in Kuala Lumpur, Malaysia. Core topics that will be covered include architecture, portlet development, content management and themes.</p><p>You will learn how to develop using the extension environment, the patterns you need to know to debug through the source code, auto-generation of the service and persistence layer and more.</p><p>The training seminar will be for 4 days to allow for more in-depth coverage of topics such as core development using Struts and modular development using portlet WARs.</p><h2>Latest&nbsp;Release</h2><p>Come and learn about the new features Liferay Portal 4.4 has to offer!</p><h2>Hands-On Training</h2><p>We will take you through the entire process of portlet development and teach you industry best practices. You will learn how to code the action class, auto-generate business components, implement server-side validation, and perform database operations.</p><h2>Subject Matter Experts</h2><p>The course will be led by the Liferay experts who designed the software.</p><h2>Development Environment</h2><p>For development we will setup Tomcat on Eclipse and use MySQL for the database. Although Liferay can be used with any application server and database, the above setup is the fastest in terms of development. We may also touch upon installation with other application servers and databases as well.</p><h2>Prerequisites</h2><p>Basic knowledge of Java and Servlets is required. The course will be intensive, but we will make sure to answer any questions you have to ensure that you have a solid grasp of the technology by the end of the course.</p><p>Please note that a laptop is required for this course.</p><ul><li>Minimum System Requirements: 1.4 GHz 512MB RAM</li><li>Recommended System Requirements: 2.0 GHz 1GB RAM</li><li>Wireless Card Required</li></ul><p>&nbsp;</p><h2>Contact Us</h2><p>Email us at <a href=\"mailto:sales@liferay.com\">sales@liferay.com</a> to register or if you have any questions.</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT-SIDEBAR', 'BASIC-CONTENT-SIDEBAR', SPECIFIC_TIMESTAMP_20070809191100, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4108, 4109, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '4110', 1.0, 'Liferay Powers SpringSource Website', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"headline\" type=\"text\">\n		<dynamic-content><![CDATA[Liferay Powers SpringSource Website]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"creator\" type=\"text\">\n		<dynamic-content><![CDATA[www.liferay.com]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"link\" type=\"text\">\n		<dynamic-content><![CDATA[@root_path@/web/guest/about_us/news/springsource]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[false]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"description\" type=\"text_box\">\n		<dynamic-content><![CDATA[Liferay Portal is powering the new website for SpringSource, provider of Spring Framework, the leading Java™ and Java EE™ application framework.]]></dynamic-content>\n	</dynamic-element>\n</root>', 'announcements', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20071127184100, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4111, 4112, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3300', 1.0, 'PR-SpringSource', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content>&lt;div align=\"center\"&gt;&lt;p&gt;&lt;strong&gt;LIFERAY POWERS&lt;/strong&gt;&lt;br /&gt; &lt;strong&gt;NEW SPRINGSOURCE WEBSITE &lt;/strong&gt;&lt;br /&gt;&lt;em&gt; Makers of Spring Framework &lt;br /&gt; Leverage Portal Features&lt;/em&gt;&lt;/p&gt;&lt;/div&gt; &lt;p&gt;&lt;strong&gt;LOS ANGELES &amp;ndash; (November 27, 2007) &amp;ndash;&lt;/strong&gt; Liferay, Inc., provider of the world&amp;rsquo;s leading enterprise-class open source portal, announced that it is powering the new website for SpringSource, provider of Spring Framework, the leading Java&amp;trade; and Java EE&amp;trade; application framework. Formerly called Interface21, SpringSource launched www.springsource.com on the latest version of Liferay Portal as part of its company rebranding efforts.&lt;/p&gt; &lt;p&gt;&amp;ldquo;A strong online presence is essential to an open source business,&amp;rdquo; said Greg Schott, VP of Marketing at SpringSource. &amp;ldquo;The new website is a strategic extension of our product offering that will help us to better serve and interact with our community of users.&amp;rdquo;&lt;/p&gt; &lt;p&gt;SpringSource has helped hundreds of organizations, including some of the world&amp;rsquo;s largest financial institutions and public sector clients, build and deploy mission-critical applications that boost developer productivity and increase enterprise ROI. Spring Framework&amp;rsquo;s wide user community can now access a nimble, content friendly site powered by Liferay Portal with variable layers of functionality and levels of access.&lt;/p&gt; &lt;p&gt;In particular, Liferay Portal&amp;rsquo;s permissioning system gives SpringSource&amp;rsquo;s visitors varying levels of access to the site. For example, user level access to whitepapers (and other content) can be dynamically granted through webforms based on a user&amp;rsquo;s entries.&lt;/p&gt; &lt;p&gt;SpringSource is also using Liferay&amp;rsquo;s dynamic virtual hosting feature to power multiple domains on the same instance of Liferay. Each domain is able to have a unique look and set of capabilities, giving a company great flexibility in extending its community and business. &amp;ldquo;Liferay Portal&amp;rsquo;s features allow us to be very creative in the way that we maximize our online presence,&amp;rdquo; said Schott.&lt;/p&gt; &lt;p&gt;&amp;ldquo;We are very excited that SpringSource is leveraging Liferay not just to power a site but to help build the developer and user community around its product,&amp;rdquo; said Brian Chan, Liferay&amp;rsquo;s Chief Software Architect. &amp;ldquo;Spring Framework is a key component of Liferay Portal, and we certainly consider ourselves a member of that community.&amp;rdquo;&lt;/p&gt; &lt;p&gt;Deployed at enterprises worldwide in a variety of use cases, Liferay Portal addresses business needs that far exceed those of a typical public facing website. For more information and an online demo, visit www.liferay.com/web/guest/products.&lt;/p&gt; &lt;p&gt;&lt;em&gt;&lt;strong&gt; About Liferay Inc. &lt;/strong&gt;&lt;/em&gt;&lt;br /&gt; &lt;em&gt;Liferay, Inc. is the provider of Liferay Portal, the world&amp;rsquo;s leading enterprise open source portal framework with Fortune 500 clients world-wide. Liferay Professional Services offers technical support, custom development and professional training to ensure successful deployment of its flagship product in the most demanding IT environments. The company is headquartered in Los Angeles. For more information, please visit www.liferay.com.&lt;/em&gt;&lt;/p&gt; &lt;p&gt;&lt;em&gt;&lt;strong&gt; About SpringSource. &lt;/strong&gt;&lt;br /&gt; SpringSource is a leading provider of trusted open source software for mission-critical enterprise applications. SpringSource created and sustains the development of the Spring Framework, the leading Java&amp;trade; and Java EE&amp;trade; application framework. Through the Spring family of products, SpringSource has helped hundreds of organizations, including some of the world&amp;rsquo;s largest financial institutions and public sector clients, build and deploy mission-critical applications that boost developer productivity and increase enterprise ROI. For more information, please visit www.springsource.com.&lt;/em&gt;&lt;/p&gt; &lt;p align=\"center\"&gt;####&lt;/p&gt; &lt;p&gt;PRESS CONTACT: &lt;br /&gt; Tony Keller &lt;br /&gt; S&amp;amp;S Public Relations, Inc.&lt;/p&gt;</dynamic-content>\n	</dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20071127184700, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4113, 4114, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3157', 1.0, 'Products-Portal-Scenarios-Enterprise_Intranet', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;h1&gt;&lt;a name=\"company_intranet\"&gt;&lt;/a&gt;Company Intranet&lt;/h1&gt; &lt;p&gt;Liferay Portal provides the tools needed to give your people secure access to senstive company information and applications. Liferay not only helps you manage groups within your organization (ie, executives, marketing, human resources), it also ensures that individuals within those groups only see what they\'re allowed to see. Meanwhile, a Liferay administrator can get applications and content to the whole portal community quickly and easily without having to maintain several different sites.&lt;/p&gt; &lt;p&gt;&lt;strong&gt;Intranet Story: &lt;/strong&gt;&lt;a href=\"@root_path@/web/guest/stories/goodwill\"&gt;Goodwill Industries&lt;/a&gt; used Liferay to build its intranet site, MyGoodwill, to create a landmark organizational tool that enabled thousands of staff worldwide to collaborate, share best practices, and engage in real-time learning.&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070813201000, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4115, 4116, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3162', 1.0, 'Products-Portal-Scenarios-Infrastructure_Portal', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;h1&gt;&lt;a name=\"infrastructure_portal\"&gt;&lt;/a&gt;Infrastructure Portal&lt;/h1&gt; &lt;p&gt;One of the key uses of a portal is to serve as a framework for aggregating content and applications. The right tools and software will make it easy for developers to make new and existing enterprise applications available&amp;nbsp; in the&amp;nbsp; secure, manageable framework that a portal provides.&lt;/p&gt; &lt;p&gt;Liferay Portal offers comprehensive tools to help developers integrate enterprise assets with an integrated enterprise service bus (ESB), workflow engine, web services support (ie, SOAP, JSON, REST), and developer tools such as Liferay\'s Eclipse plug-in manager and Liferay Service Builder.&lt;/p&gt; &lt;p&gt;Liferay\'s Content Interaction API is designed to create a unified interface for working with content in Liferay Portal regardless of source. Users can manage and publish content from third-party systems like Alfresco, Magnolia, Microsoft Sharepoint, or Vignette with the same interface used for Liferay\'s built-in content managment system (CMS).&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070811012800, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4117, 4118, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3166', 1.0, 'Products-Portal-Scenarios-Corporate_Extranet', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content>&lt;h1&gt;&lt;a name=\"corporate_extranet\"&gt;&lt;/a&gt;Corporate Extranet&lt;/h1&gt;&lt;p&gt;Liferay Portal helps organizations create secure client- or partner-facing websites (&amp;quot;extranets&amp;quot;).&lt;/p&gt;&lt;p&gt;For example, a supplier of aftermarket automobile parts may work with several retail resellers that need to access the company\'s inventory to order parts and check shipment status. Liferay makes it easy to manage each reseller\'s access rights, content, and functionality, and quickly create branded extranets for new resellers.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Extranet Story: &lt;/strong&gt;&lt;a href=\"@root_path@/web/guest/stories/educamadrid\"&gt;EducaMadrid&lt;/a&gt;, the Madrid school system (500,000+ students across 1,600 schools), implemented Liferay as its framework application for an extranet of educational websites.&lt;/p&gt;</dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070813200700, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4119, 4120, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3168', 1.0, 'Products-Portal-Scenarios-Web_Publishing_CMS', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;h1&gt;&lt;a name=\"content_management_web_publishing\"&gt;&lt;/a&gt;Content Management &amp;amp; Web Publishing&lt;/h1&gt; &lt;p&gt;The ever increasing volume of digital content we deal with on a daily basis makes effective content management systems (CMS) a vital part of any organization. In fact, content management is a natural fit for a portal because of the need for secure access and permissions (ie, who should be able to delete a document from the system?), distinct roles (writer, editor, designer, adminstrator), and workflow (edit, approve, publish).&lt;/p&gt; &lt;p&gt;Document management is also more effective when repositories can be assigned to different departments and groups within the company.&lt;/p&gt; &lt;p&gt;&lt;strong&gt;CMS/Web Publishing Story: &lt;/strong&gt;The &lt;a href=\"@root_path@/web/guest/stories/csmonitor\"&gt;Christian Science Monitor&lt;/a&gt; used Liferay to deliver a more dynamic and interactive user experience for its online news site, streamline its behind-the-scenes publishing process, and better control work flow.&lt;/p&gt; &lt;p&gt;&lt;strong&gt;CMS/Web Publishing &lt;/strong&gt;&lt;strong&gt;Story: &lt;/strong&gt;&lt;a href=\"@root_path@/web/guest/stories/pantech\"&gt;Pantech&lt;/a&gt;, the seventh largest handset manufacturer in the world, chose Liferay to build its US &amp;amp; Canadian websites to quickly adapt to the existing look &amp;amp; feel of its branding and provide language localization into Spanish and French.&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070811013200, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4121, 4122, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3172', 1.0, 'Products - How Do People Use LR? - Page Header', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"page-name\" type=\"text\">\n		<dynamic-content><![CDATA[How do people use Liferay?]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"page-description\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'PAGE-HEADER', 'PAGE-HEADER', SPECIFIC_TIMESTAMP_20070811012700, TRUE, 2, 'Joe Bloggs', FALSE, FALSE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4123, 4124, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3174', 1.0, 'Products-Portal-Scenarios-Collaboration_Platform', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;h1&gt;&lt;a name=\"collaboration\"&gt;&lt;/a&gt;Collaboration&lt;/h1&gt; &lt;p&gt;The internet enables individuals to collaborate in unprecedented ways, and Web 2.0-style websites have pushed the possibilities even further. But in an enterprise environment, the boundless capacity to share and publish information must be governed by the roles, permissions, and security considerations unique to each user and department.&lt;/p&gt; &lt;p&gt;Liferay Portal helps organize the potential chaos of an unfettered Web 2.0 environment by empowering users with tools such as tagging, blogs, instant email, message boards, and instant messaging within a secure, administrated framework. Liferay gives administrators ultimate control over what people can do, through tools like tags administration, fine-grained permissions, delegable administrator privileges, enterprise taxonomy, and ad-hoc user groups.&amp;nbsp;&lt;/p&gt; &lt;p&gt;&lt;strong&gt;Collaboration Story: &lt;/strong&gt;The &lt;a target=\"_blank\" href=\"http://www.ixarm.com/Platform-hub\"&gt;French Armaments Procurement Agency (DGA)&lt;/a&gt; leverages Liferay Portal\'s framework&amp;nbsp; and collaboration suite to run its &amp;quot;Espace Partenaire&amp;rdquo; collaborative Platform (EPNG) for the French defense community...&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070814034900, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4125, 4126, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3182', 1.0, 'PR-ICEfaces', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;div align=\"center\"&gt;&lt;strong&gt;Liferay and ICEfaces Announce Ajax Portal Technology Partnership&lt;/strong&gt;&lt;br /&gt;&lt;em&gt;Product Integration Provides Rich User Experience and Developer Simplicity&lt;/em&gt;&lt;/div&gt;&lt;p&gt;&amp;nbsp;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;LOS ANGELES, CA &lt;/strong&gt;&amp;ndash; (July 23, 2007) &amp;ndash; Liferay, provider of the world&amp;rsquo;s leading enterprise-class, open source portal and content management system, announced a new technology partnership with ICEsoft Technologies. ICEsoft is the creator and provider of ICEfaces, the leading open source framework for simplified development of Ajax-enabled Java EE applications that are rich, robust, secure, and scalable. The integration of ICEfaces with Liferay Portal now allows enterprise developers to draw on the power of Ajax technology to provide visually rich, highly responsive experiences for users.&amp;nbsp;&amp;nbsp; &lt;br /&gt;&lt;br /&gt;&amp;ldquo;ICEfaces technology raises the bar for creating intuitive and usable portal user interfaces,&amp;rdquo; said Liferay&amp;rsquo;s Chief Software Architect, Brian Chan, &amp;ldquo;We specifically integrated with ICEfaces because they are the best at what they do and will have the highest impact on both the user and the developer experience.&amp;rdquo;&lt;br /&gt;&lt;br /&gt;ICEfaces extends JavaServer&amp;trade; Faces (JSF), providing transparent Ajax capabilities while preserving the server-side Java EE programming model, and eliminating the need for low-level JavaScript development. With this partnership, developers can now leverage Ajax Push within Liferay Portal, which allows presentation changes to be asynchronously pushed from the application server to individual portlets. Furthermore, the Liferay Portal and ICEfaces integration bears significance for the portal market as a whole. Due to limited integration between Ajax and JSF in a portal environment in the past, the benefits of JSF have not been widely leveraged by portal developers. This relationship addresses the complexities of Ajax development in a portal environment.&lt;br /&gt;&lt;br /&gt;&amp;ldquo;Without a doubt, Liferay Portal now provides the richest feature set in the industry,&amp;rdquo; said Stephen Maryka, CTO at ICEsoft. &amp;ldquo;The collaborative efforts of the Liferay and ICEfaces development teams have addressed the complexities of incorporating JSF and Ajax in Portals, allowing the developer to focus on the creative aspects of rich portal development. I expect the partnership and integration between Liferay Portal and ICEfaces to be a major catalyst in boosting JSF adoption in portal environments.&amp;rdquo;&lt;/p&gt;&lt;p&gt;Most recently, Kofax, the market leader and de facto standard in capture application software, leveraged the benefits of the Liferay Portal and ICEfaces relationship. &amp;ldquo;We believe Liferay and ICEfaces provide the best way to build content-rich portals with an SOA architecture. Kofax selected these technologies to build powerful solutions that will fit into any of our customer\'s enterprises,&amp;quot; said VP of Engineering, Sameer Samat.&lt;br /&gt;&lt;br /&gt;Liferay Portal 4.3.0 and ICEfaces 1.6.0 are both available for immediate download and mark the first of many synergistic technology releases for both companies. &amp;ldquo;The Liferay team is very happy with our partnership with ICEsoft and its impact on the developer landscape,&amp;rdquo; said Chan, &amp;ldquo;We look forward to seeing even more fruits of this relationship forwarding in the future.&amp;rdquo; Upcoming releases will see a number of Liferay&amp;rsquo;s over 60 out-of-the-box portlets rebuilt to leverage the ICEfaces Ajax framework and component suite.&amp;nbsp;&lt;br /&gt;&lt;br /&gt;For more about Liferay Portal, visit www.liferay.com. Additional information about ICEfaces can be found at www.icefaces.org. Detailed information on using ICEfaces with Liferay Portal is available in Liferay Portal 4.3.0 documentation.&lt;br /&gt;&amp;nbsp;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;About Liferay:&lt;/strong&gt;&lt;br /&gt;Liferay Portal is the world&amp;rsquo;s leading enterprise open source portal framework with Fortune 500 clients in the U.S. and Europe. Liferay Professional Services offers technical support, custom development and professional training to ensure successful deployment of its flagship product in the most demanding IT environments. Liferay clients include Discovery Education, Goodwill Industries, and the Christian Science Monitor. The company is headquartered in Los Angeles. For more information, please visit www.liferay.com.&lt;br /&gt;&lt;strong&gt;&lt;br /&gt;About ICEsoft Technologies Inc.&lt;/strong&gt;&lt;br /&gt;ICEsoft Technologies, Inc., is a leading provider of standards-compliant, Ajax-based solutions for developing and deploying Java EE, rich Internet applications. The company\'s portfolio of enterprise level Java products includes ICEfaces, an Ajax application framework that enables Java EE application developers to easily create and deploy thin-client rich Web applications in pure Java. Visit www.icesoft.com.&lt;br /&gt;&lt;br /&gt;# # # #&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'press-release', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070814221100, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4127, 4128, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3187', 1.0, 'Products-Technical_Specs-Architecture-SOA_Step_3', 'SOA Step 3', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p>Like any other J2EE application, the Remote implementation class should do any security and permission checks that you may need; the Local implementation class should contain the business logic that you allow trusted sources to invoke.&nbsp; After you are done, compile your code and you now have a SOA!</p><p>Liferay\'s ServiceBuilder has created all the necessary files you need for a Service-Oriented Architecture.&nbsp; Rather than hours and hours of tedious copy and pasting, the ServiceBuilder automates all of this in a matter of seconds.&nbsp; You can now spend all that time saved on the place where your expertise should be focused: on the business logic.</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070815041800, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4129, 4130, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3189', 1.0, 'Products-Technical_Specs-Architecture-SOA_Introduction', 'Introduction for SOA', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n  <dynamic-element name=\"content\" type=\"text_area\">\n    <dynamic-content>&lt;p&gt;The Liferay portal comes packaged with a powerful tool known as ServiceBuilder to helps developers quickly implement a Service-Oriented Architecture (SOA).&amp;nbsp; Most technologies needed for SOA (Web Services, Spring, Hibernate, etc.) require a tremendous amount of files to be written and configured.&amp;nbsp; For any developer, this can quickly become tedious and nurture an environment of copy-and-paste purgatory.&lt;/p&gt; &lt;p&gt;Liferay\'s ServiceBuilder automates most of this so that you, the developer, can focus your energy on your project\'s business logic.&amp;nbsp; It only takes three steps to implement a powerful SOA in Liferay.&lt;/p&gt;</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070815035000, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4131, 4132, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3191', 1.0, 'Products-Technical_Specs-Architecture-SOA_Step_1', 'SOA Step 1', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p>All you need to do is write an XML file called <font face=\"Courier New\">service.xml</font>, based on a DTD provided by Liferay.&nbsp; The file should specify all necessary fields, keys and finder methods.&nbsp; Run ServiceBuilder using Ant, and you are done!<br /> <br /> Over a dozen files were generated for you for each table you specified.&nbsp; This includes the following types of files:</p> <ul> <li>Spring configuration files and Stubbed Remote/Local service classes</li> <li>SOAP and Tunnel classes; WSDL files and Javascript Web Services methods</li> <li>Data Model classes</li> <li>Hibernate classes and configuration files</li> <li>SQL create and index scripts</li> </ul>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070815035300, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4133, 4134, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3193', 1.0, 'Products-Technical_Specs-Architecture-SOA_Step_2', 'SOA Step 2', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p>Empty Remote and Local service classes have been stubbed out for your by ServiceBuilder.&nbsp; All you have to do is declare the methods you want and run the ServiceBuilder task in Ant again, and you are done!</p> <p>Any business methods declared in the Remote service classes will be propagated into the SOAP and Tunnel classes, WSDL files and added into the list of Javascript Web Services methods.&nbsp; Like the definition of the data model, any subsequent changes should be followed by a run of the ServiceBuilder and the methods will be appropriately propagated.</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20070815041700, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
