insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4346, 4347, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '4348', 1.0, 'Kito Mann to present JSF on top of Liferay at TheServerSide Symposium', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"headline\" type=\"text\">\n		<dynamic-content><![CDATA[Kito Mann to present JSF on top of Liferay at TheServerSide Symposium]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"creator\" type=\"text\">\n		<dynamic-content><![CDATA[javasymposium.techtarget.com]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"link\" type=\"text\">\n		<dynamic-content><![CDATA[http://javasymposium.techtarget.com/html/det_descriptions.htm#KMannJSF]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"new-window\" type=\"boolean\">\n		<dynamic-content><![CDATA[true]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"description\" type=\"text_box\">\n		<dynamic-content><![CDATA[Component-oriented user interface frameworks such as JavaServer Faces (JSF) are growing in popularity, and organizations are also beginning to recognize the power of building application components with the Portlet API. What many don\'t realize, however, is the fact that JSF has integrated support for the Portlet API, making it a natural fit for building portlets. This session starts with a brief overview of JSF, portlets, and portals. It then explains how JSF portlet support works, and examines the process of developing portlets with JSF. Next, it walks through the development of a simple JSF application and deployment of that application as a portlet inside of Liferay Enterprise Portal, an open-source portal server.]]></dynamic-content>\n	</dynamic-element>\n</root>', 'news', 'BASIC-RSS-ITEM', 'BASIC-RSS-ITEM', SPECIFIC_TIMESTAMP_20060322230000, TRUE, 2, 'Joe Bloggs', FALSE, FALSE);
