insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4497, 4498, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2422', 1.0, 'Products - Licensing - Vs. Closed-Source Portals', 'A brief over view of Licensing benefits of Liferay vs. closed source portals.', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[Liferay vs. Closed Source Portals]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"readmore-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"bold-description\" type=\"text_box\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"description\" type=\"text_area\">\n		<dynamic-content>&lt;ul&gt; &lt;li&gt;&lt;strong&gt;Innovation &lt;/strong&gt;- In its 8th year of development, Liferay boasts a history of innovation among both commercial and open source products.&lt;/li&gt; &lt;li&gt;&lt;strong&gt;Low TCO &lt;/strong&gt;- Liferay provides the lowest total cost of ownership with ZERO license fees under the &lt;a target=\"_blank\" href=\"http://www.opensource.org/licenses/mit-license.php\"&gt;MIT license&lt;/a&gt;.&lt;/li&gt; &lt;li&gt;&lt;strong&gt;High ROI &lt;/strong&gt;- Use your existing IT investment! There is no application server agenda and no pressure to pay for toolsDevelop portlets with a full featured IDE like Eclipse, Netbeans, and JBuilder or with lightweight editor like EditPlus, VI, and Emacs.&lt;/li&gt; &lt;li&gt;&lt;strong&gt;Freedom &amp;amp; Flexibility&lt;/strong&gt; - Open source means the code is in your hands. If you want to customize it, you &lt;em&gt;can&lt;/em&gt;. If there are security flaws, the community can participate in fixing it quickly.&lt;/li&gt; &lt;li&gt;&lt;strong&gt;Security &lt;/strong&gt;- There is actually &lt;em&gt;less&lt;/em&gt; risk in going with a sustained open source product than in going with a commercial vendor. Vendors can go out of business. Vendors can merge and render your product dead in the water. A sustained open source product like Liferay Portal has an active community around it that works together.&lt;/li&gt; &lt;/ul&gt;</dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'READ-MORE', 'READ-MORE', SPECIFIC_TIMESTAMP_20080103215100, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4499, 4500, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2420', 1.0, 'Products - Licensing - Vs. Open-Source Portals', 'A brief over view of Licensing benefits of Liferay vs. open source portals.', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[Liferay vs. Open Source Portals]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"readmore-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"bold-description\" type=\"text_box\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"description\" type=\"text_area\">\n		<dynamic-content><![CDATA[<ul> <li><strong>Market Leadership </strong>- Liferay is the leader in downloads <a target=\"_blank\" href=\"http://sourceforge.net/project/stats/detail.php?group_id=49260&amp;ugn=lportal&amp;mode=alltime&amp;&amp;type=prdownload\">(over 60,000 per month)</a> among ALL open source Java portals.</li> <li><strong>Principle </strong>- Not all open source licenses are created equal! Some require modifications to be contributed if you choose to sell or distribute your product. Some still charge a license fee for commercial use.<br /> Liferay is open source because we believe it is the next step in the evolution of software development and chose the liberal MIT license accordingly.</li> <li><strong>Maturity </strong>- Liferay has one of the most mature and active open source communities as evidenced by our active <a href=\"http://forums.liferay.com/\">forums</a>.</li> <li><strong>Stability </strong>- Aside from our impressive community, there is a real company behind the product driving product development.</li> </ul>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'READ-MORE', 'READ-MORE', SPECIFIC_TIMESTAMP_20080103215600, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4501, 4502, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2947', 1.0, 'Downloads - Additional Files - WARs', 'A list of extra wars for Liferay.', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[WARs]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"readmore-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"bold-description\" type=\"text_box\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"description\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-4.4.2.war\">Liferay Portal 4.4.2 WAR</a><br />\n(Servlet 2.4)</p>\n<p><a href=\"    http://downloads.sourceforge.net/lportal/liferay-portal-4.4.2-servlet23.war\">Liferay Portal 4.4.2 WAR</a><br />\n(Servlet 2.3)</p>\n<div class=\"hr\"><hr />\n</div>\n<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-tunnel-web-4.4.2.war\">tunnel-web.war</a>: To add support for accessing Liferay\'s services remotely and to access the document library using WebDAV.</p>\n<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-cms-web-4.4.2.war\">cms-web.war</a>: To use the legacy Liferay CMS.</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'READ-MORE', 'READ-MORE', SPECIFIC_TIMESTAMP_20080103220200, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4503, 4504, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2949', 1.0, 'Downloads - Additional Files - Additional Files', 'A list of additional files for Liferay.', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[Additional Files]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"readmore-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"bold-description\" type=\"text_box\">\n		<dynamic-content><![CDATA[Installation: Read the \"Detailed installation\" section in \"Liferay Portal 4 - Installation Guide\".]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"description\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Additional Files</h2>\n<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-dependencies-4.4.2.zip\">Liferay Portal 4.4.2 Dependencies</a><br />\nUnzip and put them in the application server global classpath.<br />\nAlso see the files to integrate with external systems below for Workflow, CAS, Chat, etc.</p>\n<div class=\"hr\"><hr />\n</div>\n<h2>Application Server Plugins</h2>\n<p>Geronimo: <a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-geronimo-tomcat-4.4.2.car\">liferay-portal-geronimo-tomcat-4.4.2.car</a><br />\nBe sure you install liferay-portal-geronimo-derby-pool-4.4.2.car first.</p>\n<div class=\"hr\"><hr />\n</div>\n<h2>Database Scripts</h2>\n<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-sql-4.4.2.zip\">Liferay Portal 4.4.2 SQL Scripts</a><br />\nContains scripts for all supported databases. Use:</p>\n<ul>\n    <li>create-minimal-*.sql: deletes any existing data, creates the database, creates all necessary tables and indexes and loads the minimal necessary data.</li>\n    <li>create-*.sql:: does the same and additionally loads example data.</li>\n</ul>\n<p>&nbsp;</p>\n<p>Alternatives:</p>\n<ul>\n    <li><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-sql-oracle-9-4.4.2.dmp\">Liferay Portal 4.4.2 SQL Oracle 9 Dump</a></li>\n    <li><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-sql-oracle-10-4.4.2.dmp\">Liferay Portal 4.4.2 SQL Oracle 10 Dump</a></li>\n    <li><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-geronimo-derby-pool-4.4.2.car\">Geronimo Derby Pool 4.4.2 CAR</a></li>\n</ul>\n<p>&nbsp;</p>\n<div class=\"hr\"><hr />\n</div>\n<h2>Integration with External Applications</h2>\n<p><strong>Workflow:</strong> <a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-mule-web-4.4.2.war\">liferay-portal-mule-4.4.2.war</a> and <a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-jbpm-web-4.4.2.war\">liferay-portal-jbpm-4.4.2.war</a><br />\nRename as mule-web.war and jbpm-web.war and deploy as a regular web application.</p>\n<p><strong>Workflow:</strong> <a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-servicemix-web-4.4.2.war\">liferay-portal-servicemix-4.4.2.war</a> and <a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-jbpm-web-4.4.2.war\">liferay-portal-jbpm-4.4.2.war</a><br />\nRename as servicemix-web.war and jbpm-web.war and deploy as a regular web application.</p>\n<p><strong>CAS:</strong> <a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-cas-web-4.4.2.war\">liferay-portal-cas-4.4.2.war</a><br />\nrename as cas-web.war and deploy as a regular web application. <a href=\"http://wiki.liferay.com/index.php/Single_SignOn_-_Integrating_Liferay_With_CAS_Server\">More information...</a></p>\n<p><strong>Chat:</strong> Follow the Chat <a href=\"http://wiki.liferay.com/index.php/Chat_Portlet\">installation instructions</a>.</p>\n<div class=\"hr\"><hr />\n</div>\n<h2>Files for Developers<a onclick=\"window.location=\'/project/downloading.php?group_id=49260&amp;use_mirror=superb-east&amp;filename=liferay-portal-ext-4.4.2.zip&amp;\'+Math.floor(Math.random()*100000000); return false;\" href=\"http://downloads.sourceforge.net/lportal/liferay-portal-ext-4.4.2.zip?\"><br />\n</a></h2>\n<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-ext-4.4.2.zip\">Liferay EXT SDK 4.4.2</a></p>\n<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-plugins-sdk-4.4.2.zip\">Liferay Plugins SDK 4.4.2</a><br />\nDevelopment environment for Portlets, Themes and Layout Templates.</p>\n<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-client-4.4.2.zip\">Liferay Portal 4.4.2 Web Services Client</a><br />\nJava Stubs for Liferay Services (SOAP, Spring Remoting).</p>\n<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-doc-4.4.2.zip\">Liferay Portal 4.4.2 JavaDocs</a></p>\n<p><a href=\"http://support.liferay.com/secure/ReleaseNote.jspa?projectId=10014&amp;styleName=Html&amp;version=10291\">Liferay Portal 4.4.2 Release Notes</a></p>\n<p><a href=\"http://downloads.sourceforge.net/lportal/liferay-portal-src-4.4.2.zip\">Liferay Portal 4.4.2 Source</a></p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'READ-MORE', 'READ-MORE', SPECIFIC_TIMESTAMP_20080103220600, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
