insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4543, 4544, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2362', 1.0, 'Products - Liferay Portal - LiferayPedia Explanation', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Built in Developer Tools</h2> <p><b>Liferay Plugins Software Development Kit</b></p> <p>Liferay\'s exclusive tool enables developers to easily create their own portlets and themes.</p><p><b>Liferay Installation Wizard</b></p> <p>An easy to use windows setup installer with automatic Java SDK detection.</p><p><b>Liferay ServiceBuilder</b></p> <p>Liferay\'s exclusive tool helps developers get their code enabled for Web Services, Spring and AJAX so they can focus on writing the business logic. <a href=\"http://wiki.liferay.com/index.php/Service_Builder\">LiferayPedia Explanation</a></p><p><a target=\"_blank\" href=\"../../../../../web/guest/community/tech_specs\"><b>More Technical Information...</b></a></p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT', 'BASIC-CONTENT', SPECIFIC_TIMESTAMP_20080118065400, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4545, 4546, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3335', 1.0, 'Products - Liferay Portal - End User In Mind', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[Built with the User in Mind]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"readmore-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"bold-description\" type=\"text_box\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"description\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h3>Just In Time Portlet Rendering</h3> <p>Portlets load independently as they become ready so users don&rsquo;t have to wait to start using the portal.</p> <h3>One-Click Look and Feel Change</h3> <p>Graphical user interfaces can be selected by end-users without modifying core code. New themes can be added instantly using Liferay&rsquo;s Plugin Installer.</p> <h3>WebDAV Support</h3> <p>Users will be more productive managing portal content with:<br /> - Familiar operating system conventions for documents and folders.<br /> - Full support for Windows and Linux</p> <h3>Freeform Portlets / WebOS</h3> <p>An optional free-floating layout that mimics the look and feel of a desktop environment..</p> <h3>Easy AJAX UI</h3> <p>Liferay Portal features a rich user interface that includes  - Drag-and-drop portlet re-positioning - Dynamic portlet loader - Portlet stylizer that allows end users to customize colors, fonts, and links without editing stylesheets or HTML</p> <h3>Context-Sensitive Help</h3> <p>Portal users can access information specific to the tool they are using at any given time by clicking on a &quot;question mark&quot; icon.</p> <h3>Liferay Plugins Catalog</h3> <p>This exclusive feature connects to remote portlet and theme repositories and keeps track of new versions and updates. It allows portlets to be instantly updated or rolled back without restarting the server. Portlet providers can leverage this new channel to deliver powerful extensions to Liferay&rsquo;s capabilities.</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'READ-MORE', 'READ-MORE', SPECIFIC_TIMESTAMP_20080118073800, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4547, 4548, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3330', 1.0, 'Products - Liferay Portal - Tech to Help Developer', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[Technology to Help Developers]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"readmore-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"bold-description\" type=\"text_box\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"description\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h3>Standards Compliant</h3> <p>Liferay complies with the JSR-168 and JSR-286 portlet standards, and can therefore consume any portlets written to these standards.</p> <h3>Ready Integration</h3> <p>Partnerships with organizations such as Alfresco, ICEsoft, SpringSource, and Pentaho ensure unparalleled integration and support for these widely-used technologies. Bridges are provided to allow developers to use many popular frameworks, such as JSF, Spring Framework, or Struts to write portlets.</p> <h3>Fine-Grained Permissioning System</h3> <p>Get the right information and application functions to the right people.</p> <p>A reusable, extensible authorization architecture is used throughout the portal and is applicable to individual portlet elements such as buttons, messages, portlets and users.</p> <p><a href=\"http://wiki.liferay.com/index.php/Permissioning_Explained\">LiferayPedia Explanation</a></p> <p>&nbsp;</p> <h3>Workflow Capability</h3> <p>Reduce the time it takes to update applications to reflect how your business changes.</p> <p>Easily update processes such as e-commerce checkout, user registration, content publishing, and document approval.</p> <p>Default installation of jBPM can be replaced with Intalio or other workflow engines.</p> <h3>CSS Compliance</h3> <p>Liferay Portal is coded to CSS standards for simple theme development.</p> <h3>AJAX Interface Tools</h3> <p>Liferay-tailored Javascript client libraries based on jQuery with support for JSON Web Services to help create rich user interfaces.</p> <h3>Themes/Layout Enhancements</h3> <p>- Role-based theme management<br /> - Standardization on jQuery JavaScript Framework<br /> - Simplified theme development process<br /> - Semantic and non-obtrusive markup<br /> - Standards compliance<br /> - LAR Import / Export for Theme</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'READ-MORE', 'READ-MORE', SPECIFIC_TIMESTAMP_20080118074300, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
