insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4589, 4590, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3326', 1.0, 'Dalian Liferay User Group Meeting Registration', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root available-locales=\",en_US\" default-locale=\"\">\n	<static-content language-id=\"en_US\"><![CDATA[<p style=\"margin: 0cm 0cm 0pt; line-height: 150%;\" class=\"MsoNormal\"><b style=\"\"><span style=\"line-height: 150%; font-family: 宋体;\">尊敬的先生</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">/</span></b><b style=\"\"><span style=\"line-height: 150%; font-family: 宋体;\">女士：您好！</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\"><o:p></o:p></span></b></p><p style=\"margin: 0cm 0cm 0pt; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\"><o:p>&nbsp;</o:p></span></p><p style=\"margin: 0cm 0cm 0pt; text-indent: 21pt; line-height: 150%;\" class=\"MsoNormal\"><span style=\"line-height: 150%; font-family: 宋体;\">感谢您对本次将于中国&bull;大连召开</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay User Group (LUG)</span><span style=\"line-height: 150%; font-family: 宋体;\">大会的关注，本次会议将由美国</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay</span><span style=\"line-height: 150%; font-family: 宋体;\">有限公司</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">&lt;</span><span style=\"line-height: 150%; font-family: 宋体;\">注：</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay Portal</span><span style=\"line-height: 150%; font-family: 宋体;\">由</span><span style=\"line-height: 150%; font-family: Arial;\"> <span lang=\"EN-US\">Liferay</span></span><span style=\"line-height: 150%; font-family: 宋体;\">公司研发</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">&gt;</span><span style=\"line-height: 150%; font-family: 宋体;\">的中国子公司</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay Dalian</span><span style=\"line-height: 150%; font-family: 宋体;\">主持并召开。</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\"><o:p></o:p></span></p><p style=\"margin: 0cm 0cm 0pt; text-indent: 21pt; line-height: 150%;\" class=\"MsoNormal\"><span style=\"line-height: 150%; font-family: 宋体;\">本次会议的主要目的是用来为数量不断增长的大连、中国地区以及泛亚洲地区的</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay</span><span style=\"line-height: 150%; font-family: 宋体;\">用户提供技术支持，同时展示如何应用</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay</span><span style=\"line-height: 150%; font-family: 宋体;\">来提高个人或者组织机构在</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Web 2.0</span><span style=\"line-height: 150%; font-family: 宋体;\">的世界中</span><span style=\"line-height: 150%; font-family: Arial;\"> </span><span style=\"line-height: 150%; font-family: 宋体;\">生存和发展的能力。首席执行官（</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">CEO</span><span style=\"line-height: 150%; font-family: 宋体;\">）</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Bryan Cheung</span><span style=\"line-height: 150%; font-family: 宋体;\">先生和首席架构师（</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Chief Software Architect </span><span style=\"line-height: 150%; font-family: 宋体;\">）</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Brian Chan</span><span style=\"line-height: 150%; font-family: 宋体;\">先生携同</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay Portal</span><span style=\"line-height: 150%; font-family: 宋体;\">核心开发团队的其他</span><span style=\"line-height: 150%; font-family: Arial;\"> </span><span style=\"line-height: 150%; font-family: 宋体;\">成员届时将会做关于</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay</span><span style=\"line-height: 150%; font-family: 宋体;\">相关技术的讲座，并就用户提出的问题进行相关答疑。这也将是您的一个通过网络与其他</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay</span><span style=\"line-height: 150%; font-family: 宋体;\">用户相互学习，并在真正开源的理念上，开拓创新技术和结成商业同盟的一个好机会。</span><span style=\"line-height: 150%; font-family: Arial;\"> <span lang=\"EN-US\"><o:p></o:p></span></span></p><p style=\"margin: 0cm 0cm 0pt; text-indent: 21pt; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">LUG</span><span style=\"line-height: 150%; font-family: 宋体;\">大会是免费向公众开放的，我们欢迎任何对</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay Portal</span><span style=\"line-height: 150%; font-family: 宋体;\">感兴趣的个人或者公司来参加此次会议</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">.<o:p></o:p></span></p><p style=\"margin: 0cm 0cm 0pt; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\"><o:p>&nbsp;</o:p></span></p><p style=\"margin: 0cm 0cm 0pt; line-height: 150%;\" class=\"MsoNormal\"><b style=\"\"><span style=\"line-height: 150%; font-family: 宋体;\">大连</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">Liferay User Group</span></b><b style=\"\"><span style=\"line-height: 150%; font-family: 宋体;\">（</span></b><b style=\"\"><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\">LUG</span></b><b style=\"\"><span style=\"line-height: 150%; font-family: 宋体;\">）大会：</span><span lang=\"EN-US\" style=\"line-height: 150%; font-family: Arial;\"><o:p></o:p></span></b></p><p style=\"margin: 0cm 0cm 0pt; line-height: 150%;\" class=\"MsoNormal\"><