insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4601, 4602, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3338', 1.0, 'HEADER-Liferay Meetup 2008', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"header\" type=\"text\">\n		<dynamic-content><![CDATA[Liferay Meetup 2008 - Los Angeles]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-HEADER', 'BASIC-HEADER', SPECIFIC_TIMESTAMP_20080313055600, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4603, 4604, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3340', 1.0, 'Aboutus-Events-Liferay_2008 (full page)', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"sidebar-location\" type=\"list\">\n		<dynamic-content><![CDATA[right]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"sidebar-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Date</h2> <p>July 28, 2008</p><h2>Location</h2> <!--{12054424453280}--> <p>Los Angeles, CA</p>  <h2>Additional Information</h2> <p>Agenda and list of presenters coming soon!</p> <h2>Registration: FREE!</h2> <p>PLEASE Register online by July 15th, 2008 to secure a spot at this year\'s Liferay Meetup.</p><p>Attendance will be capped at 200!</p><p>&nbsp;</p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"main-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p>&nbsp;</p><p><b>Come one come all to Liferay\'s 2008 Community Meetup in Los Angeles!</b></p><p>This is where you meet Liferay\'s core development team face-to-face.</p><p>The event is comprised of a series of technical roundtable discussions led by Liferay, our partners and current users. It\'s a great place to learn about our technology and its latest enhancements as well as to brainstorm with us about future development!</p><p>In true open source form, the Liferay Meetup is absolutely free. We\'ll even take care of lunch and refreshments served throughout the day!</p><p>&nbsp;</p><p><b>Registration: </b>We will be capping registration at 200 attendees. Please register and reserve your spot before July 15th!</p><p>The registration form will be ready soon and this page will be updated regularly with information about the location and agenda. If you would like email notification of when registration opens, please subscribe to the Meetup thread in our <a href=\"http://www.liferay.com/web/guest/community/forums/message_boards/message/615553\" target=\"_blank\">community forums</a>.&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT-SIDEBAR', 'BASIC-CONTENT-SIDEBAR', SPECIFIC_TIMESTAMP_20080313060000, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
