insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4621, 4622, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '3361', 1.0, 'Aboutus-Events-GartnerPCC (full page)', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"sidebar-location\" type=\"list\">\n		<dynamic-content><![CDATA[right]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"sidebar-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p><a target=\"_blank\" href=\"http://www.liferay.com/c/document_library/get_file?p_l_id=1980&amp;folderId=633258&amp;name=DLFE-442.pdf\"><b>PORTAL BEST PRACTICES: GETTING THE MOST OUT OF YOUR PORTAL INITIATIVE</b></a></p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"main-content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p><b><span style=\"font-size: medium;\">Thanks to all those who dropped in on the Liferay Booth in Baltimore!</span><br type=\"_moz\" /></b></p><p>It was a pleasure meeting all of you and we hope to keep in touch!</p><p>Some of you may have had the pleasure of sitting in on a session lead by Harvard Business School that discussed its current deployment of Liferay Portal. Others may have heard about how Novell Teaming + Conferencing uses Liferay Portal as its default platform.</p><p>We would be more than happy to talk with you further about the different organizations and projects using our product. Just send us an email to <a href=\"mailto:pr@liferay.com?subject=Product%20Information%20Request%20(via%20Gartner%20PCC)&amp;body=Name%3A%0ATitle%3A%0ACompany%3A%0AHow%20did%20you%20hear%20about%20Liferay%3F\">pr@liferay.com</a> if you need any additional information about our users, products and/or services.</p> <p>In the meantime, here are a few links for your convenience:</p> <ul><li><a target=\"_blank\" href=\"http://demo.liferay.net/web/guest/home/\">Online Demo</a></li><li><a href=\"mailto:sales@liferay.com?subject=Live%20Demo%20Request&amp;body=Name%3A%0ACompany%3A%0AWhere%20did%20you%20hear%20about%20Liferay%3F%3A%0AHave%20you%20been%20in%20touch%20with%20our%20company%20before%3F%3A\">Request a live demo</a></li><li><a target=\"_blank\" href=\"http://www.liferay.com/c/document_library/get_file?p_l_id=136815&amp;folderId=167862&amp;name=DLFE-314.pdf\">Full list of Liferay\'s portlets</a></li><li><a href=\"http://www.liferay.com/web/guest/community/tech_specs\" target=\"_blank\">Technical details for your IT team</a></li></ul>]]></dynamic-content>\n	</dynamic-element>\n</root>', 'general', 'BASIC-CONTENT-SIDEBAR', 'BASIC-CONTENT-SIDEBAR', SPECIFIC_TIMESTAMP_20080330064900, TRUE, 2, 'Joe Bloggs', FALSE, TRUE);
insert into JournalArticle (id_, resourcePrimKey, groupId, companyId, userId, userName, createDate, modifiedDate, articleId, version, title, description, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName, expired, indexable) values (4623, 4624, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, '2371', 1.0, 'Products - Liferay Portal - Created for the Enterprise', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n\n<root>\n	<dynamic-element name=\"company-image\" type=\"image\">\n		<dynamic-content></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"company-link\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"company-link-name\" type=\"text\">\n		<dynamic-content><![CDATA[]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"quote\" type=\"text_area\">\n		<dynamic-content><![CDATA[<p><span style=\"font-size: medium;\"> <p style=\"text-align: right;\"><i>&quot;Liferay Portal...rivals many commercial portal platforms in both functionality and technology...&quot;</i></p> </span></p> <p style=\"text-align: right;\"><span style=\"font-size: medium;\">--InfoWorld 2007 Technology of the Year Awards: </span></p> <p style=\"text-align: right;\"><span style=\"font-size: medium;\">Best Open Source Portal</span><span style=\"font-size: small;\"><i>      </i></span></p>]]></dynamic-content>\n	</dynamic-element>\n	<dynamic-element name=\"content\" type=\"text_area\">\n		<dynamic-content><![CDATA[<h2>Created for the Enterprise</h2> <p><b>Personalization and easy customization</b></p> <p>Give the right people the right access to the right applications and documents! A highly granular permissioning system allows you to customize the user experience at the organizational and personal level.</p> <p><b>Workflow adaptable</b></p> <p>Liferay&rsquo;s technology is built to quickly adapt business and organizational changes, ensuring mi