insert into JournalTemplate (id_, groupId, companyId, userId, userName, createDate, modifiedDate, templateId, structureId, name, description, xsl, langType, smallImage, smallImageId, smallImageURL) values (6688, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'CSS-TEMPLATE', '', 'CSS Template', 'A CSS file loaded on every page.', '/* ****************************\n   *                          *\n   *       GENERAL CSS        *\n   *                          *\n   **************************** */\n\n\n/* ------ GLOBAL ------ */\n#content-wrapper {a\n	font-family: Arial;\n	fosnt-size: 12px;\nfont-size: 1.2em;\n}\n\n\n.portlet-borderless-container blockquote {\n	padding: .5em 3em;\n}\n\n\n.portlet-borderless-container h1 {\n	font-size: 20px;\n	margin: .6em 0;\n}\n\n\n.portlet-borderless-container h2 {\n	font-size: 17px;\n	margin: .5em 0;\n}\n\n\n.portlet-borderless-container h4 {\n	font-size: 12px;\n	color: #1A698F;\n	margin: .5em 0;\n	font-weight: normal;\n	text-transform: uppercase;\n}\n\n\n.portlet-borderless-container ul {\n	margin: 0em 0em 0em 2em;\n	list-style-type: disc;\n}\n\n\n.portlet-borderless-container li {\n	padding-top: 5px;\n}\n\n\n.portlet-borderless-container td {\n	vertical-align: top;\n}\n\n\n.portlet-borderless-container a {\n	text-decoration: none;\n	color: #1B568C\n}\n\n\n.portlet-borderless-container p {\n	margin-bottom: 0.5em;\n}\n\n\n.portlet-borderless-container a:hover {\n	text-decoration: underline;\n}\n\n\n.columns-1-2 #column-1 .lfr-portlet-column {\n	background: none;\n	border: none;\n}\n\n\n/* ------ NAVIGATION ------ */\nhtml body .nav-menu {\n	background: #FFF;\n	margin: 12px;\n	padding: 10px 0;\n}\n\nhtml body .nav-menu li {\n	list-style-image: url(../images/navigation/bullet_selected.png);\n	margin-top: 3px;\n}\n\n/* ------ LAYOUT FIX ------ */\n.lfr-column.seventy {\n	width: 702px;\n}\n\n\n.lfr-column.thirty {\n	width: 238px;\n}\n\n\n/* ****************************\n   *                          *\n   *       TEMPLATE CSS       *\n   *                          *\n   **************************** */\n\n\n/* ------ BASIC-ARTICLE ------ */\n.journal-basic-article-container {\n	background: #FFFFFF;\n	border-bottom: 1px solid #D7D7D7;\n	margin: 0 13px;\n	padding: 20px 12px 20px 195px;\n	position: relative;\n	line-height: normal;\n	min-height: 110px;\n}\n\n\n.ie6 .journal-basic-article-container {\n	height: 110px;\n}\n\n\n.journal-basic-article-container h2 {\n	margin: 0;\n	padding: 0 0 5px;\n}\n\n\n.journal-basic-article-container h2 a {\n	color: #000;\n	text-decoration: none;\n}\n\n\n.journal-basic-article-container img {\n	position: absolute;\n	display: block;\n	left: 12px;\n	top: 20px;\n}\n.journal-basic-article-container .details {\n	color: #1B568C\n	clear: both; \n	overflow: hidden;\n	float: left;\n	width: 100%;\n	font-size: 11px;\n	margin: 0 0 .5em;\n}\n\n\n.journal-basic-article-container .date {\n	float: left; \n}  \n\n\n.journal-basic-article-container .type {\n	float: right;\n}\n\n\n.journal-basic-article-container p {\n	margin: 1em 0;\n}\n\n\n/* ------ BASIC-CONTENT ------ */\n.journal-basic-content-container {\n	background: #FFF;\n	margin: 12px;\n	padding: 12px;\n}\n\n\n/* ------ BASIC-CONTENT-SIDEBAR ------ */\n\n\n.journal-basic-content-sidebar-container {\n	background: #FFF;\n	padding: 12px;\n	margin: 12px;\n}\n\n\n.journal-basic-content-sidebar-container .sidebar {\n	background: #D5DADE;\n	margin: 10px;\n	padding: 10px;\n	width: 200px;\n}\n\n\n.journal-basic-content-sidebar-container .sidebar.right {\n	float: right;\n}\n\n\n.journal-basic-content-sidebar-container .sidebar.left {\n	float: left;\n}\n\n\n/* ------ BASIC-HEADER ------ */\n.journal-basic-header-container {\n	margin: 13px 13px 0;\n}\n\n\n/* ------ BASIC-IMAGE-TEXT ------ */\n.journal-basic-image-text-container {\n	background: #FFF;\n	margin: 12px;\n}\n\n\n.journal-basic-image-text-container td {\n	padding: 12px;\n	vertical-align: top;\n	font-size: 12px;\n}\n\n\n.journal-basic-image-text-container a {\n	text-decoration: none;\n}\n\n\n/* ------ FAST-FACTS ------ */\n.journal-fast-facts-container {\n	background: #FFF;\n	margin: 12px;\n}\n.journal-fast-facts-container h2 {\n	margin: 0;\n}\n\n\n.journal-fast-facts-container .fast-facts-header {\n	border-bottom: 1px solid #D7D7D7;\n	padding: 12px;\n}\n\n\n.journal-fast-facts-container .fast-facts-item-container {\n	padding: 12px;\n}\n\n\n.journal-fast-facts-container .fast-facts-item {\n	border-bottom: 1px solid #D7D7D7;\n}\n\n\n.journal-fast-facts-container .fast-facts-num {\n	color: #1A698F;\n	font-weight: bold;\n	text-transform: uppercase;\n}\n\n\n/* ------ IMAGE-FLOW ------ */\n.journal-image-flow-container {\n	background: #FFF;\n	margin: 12px;\n	padding: 12px;\n}\n\n\n.journal-image-flow-container img {\n	margin: 12px;\n}\n\n\n.journal-image-flow-container .default {\n	float: left;\n}\n\n\n.journal-image-flow-container .left {\n	float: left;\n}\n\n\n.journal-image-flow-container .right {\n	float: right;\n}\n	\n/* ------ PAGE-HEADER ------ */\n.journal-page-header-container {\n	padding: 10px 12px 10px;\n}\n\n\n.journal-page-header-container .page-name {\n	color: #FFF;\n	font-size: 30px;\n	line-height: 36px;\n}\n\n\n.journal-page-header-container .page-description {\n	color: #89C6E4;\n	border-left: 1px solid #4F595E;\n	margin-left: 10px;\n	padding-left: 10px;\n	font-size: 15px;\n}\n\n\n.journal-page-header-container td {\n	vertical-align: middle;\n}\n\n\n/* ------ READ-MORE ------ */\n\n\n.journal-read-more-container {\n	background: #FFF;\n	margin: 12px;\n}\n\n\n.ie .journal-read-more-container {\n	zoom: 1;\n}\n\n\n.journal-read-more-container .read-more-link {\n	float: right;\n	margin: 9px;\n}\n\n\n.journal-read-more-container .header {\n	background: url(@image_path@/image/image_gallery?img_id=1954) no-repeat 12px 13px;\n	padding: 12px 12px 12px 25px;\n	border-bottom: 1px solid #D7D7D7;\n	margin: 0;\n}\n\n\n.journal-read-more-container h2 a {\n	color: #000;\n}\n\n\n.journal-read-more-container .description {\n	padding: 1