insert into JournalTemplate (id_, groupId, companyId, userId, userName, createDate, modifiedDate, templateId, structureId, name, description, xsl, langType, smallImage, smallImageId, smallImageURL) values (6700, 14, 1, 2, 'Joe Bloggs', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'RSS-FEED-PAGINATION-JS', 'RSS-FEED-PAGINATION', 'RSS Feed Pagination JavaScript', 'Pagination of RSS Feed Reader.', '<script>\n	Liferay.Noir = Liferay.Noir || {};\n\n	Liferay.Noir.PaginateNewsArticles =  {\n\n			init: function(params) {\n				var instance = this;\n				if (!params.container) {\n					return;\n				}\n				instance._container = jQuery(\'#\' + params.container);\n				instance._limit = params.limit || 10;\n				if (params.itemsClass) {\n					params.itemsClass = \'.\' + params.itemsClass;\n				}\n				instance._rows = instance._container.find(params.itemsClass || \'> div\');\n				instance._page = 0;\n				instance._total = Math.ceil(instance._rows.length / instance._limit);\n				instance._links = params.container + \'Links\';\n\n				var span = jQuery(\'<span class=\"\' + instance._links + \'\"></span>\');\n				var a;\n				for (var i = 1; i <= instance._total; i++) {\n					a = jQuery(\'<a href=\"javascript: ;\">\' + i + \'</a>\');\n					a.click(\n						function() {\n							var number = jQuery(this).text();\n							number = parseInt(number) - 1;\n							instance.pageTo(number);\n						}\n					);\n					span.append(a);\n				}\n				instance._container.append(span);\n				span.find(\'a\').not(\':last\').after(\' | \');\n				\n				instance._linksContainer = span;\n\n				instance.pageTo(0);\n			},\n			pageTo: function(page) {\n				var instance = this;\n				if (!instance._container.length) {\n					return;\n				}\n				\n				var links = instance._linksContainer.find(\'a\');\n				\n				instance._rows.hide();\n				\n				var start = instance._limit * page;\n				var end = start + (instance._limit - 1);\n\n				instance.setPage(start, end, \'show\');\n				instance._page = page;\n				\n				links.css({\n						\'font-weight\': \'normal\',\n						\'text-decoration\': \'\'\n					});\n				links.eq(page).css(\n					{\n						\'font-weight\': \'bold\',\n						\'text-decoration\': \'none\'\n					}\n				);\n			},\n			setPage: function(start, end, display) {\n				var instance = this;\n\n				var table = instance._container;\n				var rows = instance._rows;\n				\n				end = Math.min(end, rows.length - 1);\n				rows = rows.filter(\n					function(i) {\n						return (i >= start && i <= end);	\n					}\n				);\n				\n				if (display == \'none\' || display == \'hidden\') {\n					rows.hide();\n				} else {\n					rows.show();\n				}\n			}\n	};\n	jQuery(\n		function () {\n			Liferay.Noir.PaginateNewsArticles.init({container: \'journal-rss-feed-reader-container\', itemsClass: \'journal-rss-feed-item-container\', limit: 10});\n		}\n	);\n</script>', 'vm', FALSE, 6701, '');
